[65133] = {name = "Nuage d'orage", description = "Invoque un nuage d'orage chargé d'éclairs sur un personnage-joueur allié aléatoire. Tant qu'il est entouré par ce nuage, un maximum de 6 unités alliées touchées par ce personnage sont frappées par un éclair et reçoivent la Puissance de la tempête, ce qui augmente les dégâts de leurs coups critiques de 135% pendant 30 sec.", mechanic = nil},
[65147] = {name = "Brise-bouclier", description = "Une attaque à distance qui inflige 2000 points de dégâts et enlève une couche de Défense à l'adversaire.", mechanic = nil},
[65162] = {name = "Le joueur chevauche Keristrasza", description = nil, mechanic = nil},
[65172] = {name = "Eclair de givre", description = "Inflige des dégâts de Givre à un ennemi et réduit sa vitesse de déplacement pendant 4 sec.", mechanic = nil},
[65173] = {name = "Chaînes de glace", description = "L'ennemi est entouré de chaînes de glace qui l'empêchent de bouger et brûlent 350 to 450 de ses points de mana toutes les 2 s pendant 10 sec.", mechanic = "Immobilisé"},
[65191] = {name = "Dévorée", description = "Dévorée\nQuelque chose est en train de dévorer cette créature, corps et âme.", mechanic = nil},
[65192] = {name = "Coupe-flamme", description = "Pulvérise le Coupe-flamme dans les airs, ce qui éteint toutes les flammes à moins de 10 mètres. De plus, tous les ennemis affectés voient leur vitesse d'incantation réduite de 50% pendant 8 sec.", mechanic = nil},
[65204] = {name = "La Bête Déchaînée", description = nil, mechanic = nil},
[65210] = {name = "Matrice de déstabilisation", description = "Canalise un rayon d'énergie titanesque qui déstabilise la structure moléculaire de certaines créatures à base de saronite, ce qui réduit leur vitesse d'attaque de 100% et leur vitesse d'incantation de 300%. Dure 30 sec.", mechanic = nil},
[65214] = {name = "Immolation seigneuriale", description = "Brûle tous les vortex tourbillonnants proches, leur infligeant 24 to 29 points de dégâts de Feu, puis 4 points de dégâts de Feu supplémentaires toutes les 3 s pendant 12 sec.", mechanic = nil},
[65215] = {name = "Conflagration infernale", description = "Inflige 302 to 338 points de dégâts de Feu à un vortex tourbillonnant aléatoire et ceux qui se trouvent alentour.", mechanic = nil},
[65216] = {name = "Vengeance du Vortex", description = "Enveloppe le seigneur Magmathar de la puissance du Vortex, ce qui inflige 278 to 322 points de dégâts de Nature et 225 points de dégâts de Nature supplémentaires toutes les 3 s pendant 9 sec.", mechanic = nil},
[65231] = {name = "Eclair céleste", description = "Frappe d'éclairs le seigneur Magmathar et lui inflige 24 to 29 points de dégâts de Nature.", mechanic = nil},
[65232] = {name = "Extinction des flammes", description = "Éteint l'immolation seigneuriale du seigneur Magmathar. Peut être utilisée toutes les 15 secondes.", mechanic = nil},
[65233] = {name = "Retour au Vortex", description = "Vous renvoie avec le vortex tourbillonnant au Vortex à la frontière sud du Pic-Tonnerre. Ne peut être utilisé que si vous encerclez déjà le seigneur Magmathar.", mechanic = nil},
[65248] = {name = "Souffle de givre", description = "Inflige 5363 to 5637 points de dégâts de Givre aux ennemis se trouvant dans un cône de 30 mètres devant le lanceur de sorts. De plus, la vitesse d'attaque de la cible est réduite de 100% pendant 6 sec.", mechanic = nil},
[65260] = {name = "Frappe des ailes", description = "Inflige 750 to 1250 points de dégâts à tous les ennemis se trouvant à moins de 25 mètres et les fait tomber à la renverse.", mechanic = nil},
[65297] = {name = "Explosion instable", description = "Le pauvre orc qui porte le lance-flamme subit 2625 to 3375 points de dégâts de Feu.", mechanic = nil},
[65305] = {name = "Réponse de l'artéfact", description = nil, mechanic = nil},
[65306] = {name = "Invocation d'une pile de trésors enfouis", description = nil, mechanic = nil},
[65310] = {name = "Titan's Terminal Quest Complete", description = nil, mechanic = nil},
[65351] = {name = "Lancer un zeppelin en papier", description = "Lancez le zeppelin à votre ami du Norfendre.", mechanic = nil},
[65386] = {name = "Honorer les morts", description = "Honore les disparus à l'occasion du Jour des morts.", mechanic = nil},
[65388] = {name = "Lance-flammes", description = "Brûle tous les ennemis qui se trouvent dans un cône devant le démolisseur, ce qui leur inflige des dégâts de Feu supplémentaires toutes les 1 s pendant 10 sec.", mechanic = nil},
[65402] = {name = "Balayage", description = "Frappe les ennemis proches et inflige 241 to 279 points de dégâts.", mechanic = nil},
[65406] = {name = "Griffure", description = "Lacère la cible, lui infligeant 240 to 360 points de dégâts et 360 points de dégâts supplémentaires en 9 sec.", mechanic = "Sanguinolent"},
[65407] = {name = "Traquenard", description = "Bondit sur une cible à 5 to 45 mètres au maximum, l'étourdit pendant 3 sec et lui inflige 319 to 431 points de dégâts. Portée minimum de 5 mètres.", mechanic = nil},
[65417] = {name = "Le démolisseur chanteguerre explose", description = "Le démolisseur explose en infligeant 263 to 337 points de dégâts de Feu à tous les membres de la Horde à moins de 10 mètres et les envoie à terre.", mechanic = nil},
[65481] = {name = "Retour à la base !", description = "Commande au coursier du vent de rentrer au Guet de Hurlenfer.", mechanic = nil},
[65488] = {name = "Fouet mental", description = "Inflige des dégâts d'Ombre à un ennemi et réduit sa vitesse de déplacement pendant 3 sec.", mechanic = "Piéger"},
[65489] = {name = "Lancement d'explosif", description = "Détruit les elfes et leurs biens à Astranaar.", mechanic = nil},
[65490] = {name = "Toucher vampirique", description = "Inflige 33750 to 41250 points de dégâts d'Ombre en 15 sec. à votre cible.", mechanic = nil},
[65492] = {name = "Attaque mentale", description = "Frappe la cible et lui inflige 7088 to 7912 points de dégâts d'Ombre.", mechanic = nil},
[65494] = {name = "Appel d'un kodo de bât", description = nil, mechanic = nil},
[65516] = {name = "Boule de neige", description = "Lancez-moi avant que je ne fonde !", mechanic = nil},
[65541] = {name = "Mot de l'ombre : Douleur", description = "Prononce un mot des ténèbres qui inflige des dégâts d'Ombre à un ennemi toutes les 3 s pendant 15 sec.", mechanic = nil},
[65542] = {name = "Silence", description = "Rend la cible silencieuse, l'empêchant de lancer des sorts pendant 5 sec.", mechanic = "Réduit au silence"},
[65543] = {name = "Cri psychique", description = "Pousse un cri psychique, forçant tous les ennemis proches à fuir pendant 8 sec.", mechanic = "En fuite"},
[65544] = {name = "Dispersion", description = "Votre corps devient de l'énergie d'ombre pure, ce qui réduit tous les dégâts subis de 90%. Vous ne pouvez pas attaquer ni lancer de sorts, mais vous régénérez 6% de votre mana toutes les secondes pendant 6 sec. Dispersion peut être lancé lorsque vous êtes étourdi, apeuré ou réduit au silence. Dissipe tous les effets affectant le déplacement. Vous restez insensible à ces effets tant que vous êtes de l'énergie pure.", mechanic = nil},
[65546] = {name = "Dissipation de la magie", description = "Dissipe la magie sur la cible et supprime 2 sorts néfastes affectant un ami ou 2 sorts favorables dont bénéficie un ennemi.", mechanic = nil},
[65547] = {name = "Bijou JcJ", description = "Dissipe tous les effets affectant le déplacement et tous les effets qui provoquent une perte du contrôle de votre personnage.", mechanic = nil},
[65571] = {name = "Offrande de cristaux", description = "Invoque une offrande de cristaux pour les Tout-puissants.", mechanic = nil},
[65579] = {name = "Faire atterrir Thessera", description = "Ordonne à Thessera d'atterrir.", mechanic = nil},
[65605] = {name = "Graine explosive", description = "Plante une graine volatile sur un ennemi. Au bout de 4 s, la graine explose et inflige 3200 points de dégâts de Nature à la cible et à ses alliés proches.", mechanic = nil},
[65611] = {name = "Lancer la poupée de chiffon", description = "Lance la poupée en chiffon à une brute varleu.", mechanic = nil},
[65628] = {name = "Gentil", description = "Vous êtes quelqu'un de bien.", mechanic = nil},
[65629] = {name = "Méchant", description = "Vous n'êtes pas quelqu'un de bien.", mechanic = nil},
[65667] = {name = "Chaleur", description = "Une chaleur intense s'abat sur tous les assemblages de fer proches, ce qui augmente leur vitesse de déplacement de 5 et leur hâte de 5. Cet effet est cumulable jusqu'à 10 fois. S'il est cumulé 10 fois, l'assemblage de fer affecté entre en fusion.", mechanic = nil},
[65719] = {name = "Nova de l'ombre", description = "Inflige sans discrimination des dégâts d'Ombre aux créatures se trouvant à moins de 15 mètres.", mechanic = nil},
[65735] = {name = "Récupération", description = "Soigne un allié toutes les 3 s pendant 12 sec.", mechanic = nil},
[65753] = {name = "Yeux de Tonga", description = "Tonga Totem-runique veille sur vous depuis les airs.", mechanic = nil},
[65762] = {name = "Griffes extra acérées", description = "Shadumbra en veut terriblement à l'Avant-garde chanteguerre d'avoir envahi la Retraite de Raynebois et inflige à ses membres 110 to 140 points de dégâts physiques supplémentaires.", mechanic = nil},
[65766] = {name = "Vague de Lumière", description = "Envoie une Vague de Lumière qui inflige 4500 points de dégâts toutes les 2 s aux ennemis Sombres.", mechanic = nil},
[65768] = {name = "Vague de ténèbres", description = "Envoie une Vague de ténèbres qui inflige 4500 points de dégâts toutes les 2 s aux ennemis Lumineux.", mechanic = nil},
[65775] = {name = "Mandibules trempées d'acide", description = "L'acide projeté par les mandibules du scarabée inflige 1800 points de dégâts de Nature toutes les 3 s. Dure 1 min.", mechanic = nil},
[65790] = {name = "Contresort", description = "Contre l'incantation de sort de l'ennemi et l'empêche de lancer des sorts de cette école de magie pendant 8 sec. Génère un niveau élevé de menace.", mechanic = "Interrompu"},
[65791] = {name = "Déflagration des Arcanes", description = "Frappe la cible avec de l'énergie et lui inflige 12488 to 14512 points de dégâts des Arcanes. Chaque fois que vous lancez Déflagration des Arcanes, les dégâts de tous les sorts des Arcanes sont augmentés de 10%, mais le coût en mana augmente de 150%. Effet cumulable jusqu'à 4 fois. Dure 6 sec ou jusqu'à ce qu'un sort de dégâts des Arcanes autre que Déflagration des Arcanes soit lancé.", mechanic = nil},
[65792] = {name = "Nova de givre", description = "Frappe les ennemis qui se trouvent à proximité du lanceur de sorts, leur inflige 365 to 415 points de dégâts de Givre et les gèle sur place pendant 8 sec au maximum. Si une cible subit des dégâts, l'effet peut être interrompu sur elle.", mechanic = nil},
[65793] = {name = "Transfert", description = "Téléporte le lanceur de sorts 20 mètres plus loin vers l'avant, à moins qu'il n'y ait un obstacle. Annule les effets d'étourdissement et d'immobilisation.", mechanic = nil},
[65795] = {name = "Lumière libérée", description = "La Boule de lumière libère son énergie et inflige 14625 to 15375 points de dégâts de Lumière aux ennemis se trouvant à moins de 6 mètres.", mechanic = nil},
[65799] = {name = "Barrage des Arcanes", description = "Lance plusieurs projectiles sur la cible ennemie et lui inflige 10450 to 11550 points de dégâts des Arcanes.", mechanic = nil},
[65800] = {name = "Explosion des Arcanes", description = "Crée une explosion de magie des Arcanes autour du lanceur et inflige 5280 to 5720 points de dégâts des Arcanes à toutes les cibles qui se trouvent dans un rayon de 10 mètres.", mechanic = nil},
[65801] = {name = "Métamorphose", description = "Transforme l'ennemi en mouton et le force à errer dans les environs pendant 10 sec au maximum. Le mouton ne peut ni attaquer, ni lancer de sorts. Si le mouton subit des dégâts, il retrouve sa forme normale. Vous ne pouvez métamorphoser qu'une cible à la fois. Ne fonctionne que sur les bêtes, les humanoïdes et les bestioles.", mechanic = "Métamorphosé"},
[65802] = {name = "Bloc de glace", description = "Vous enveloppe dans un bloc de glace qui vous protège des attaques physiques et des sorts pendant 5 sec. En contrepartie, vous ne pouvez pas attaquer, vous déplacer ni lancer de sorts. Cause également une Hypothermie qui vous empêche de lancer un nouveau Bloc de glace pendant 30 sec.", mechanic = "Invulnérable"},
[65804] = {name = "Planter la bannière de la Horde", description = "Pour la Horde !", mechanic = nil},
[65807] = {name = "Eclair de givre", description = "Lance un éclair de givre sur l'ennemi, inflige 11563 to 13437 points de dégâts de Givre et réduit sa vitesse de déplacement de 40% pendant 9 sec.", mechanic = nil},
[65808] = {name = "Ténèbre libérée", description = "La Ténèbre concentrée libère son énergie en infligeant 14625 to 15375 points de dégâts des Ténèbres aux ennemis se trouvant à moins de 6 mètres.", mechanic = nil},
[65809] = {name = "Peur", description = "Effraie l'ennemi et l'oblige à s'enfuir pendant 10 sec au maximum. Si la cible subit des dégâts, l'effet peut être interrompu. Vous ne pouvez effrayer qu'une seule cible à la fois.", mechanic = "En fuite"},
[65810] = {name = "Corruption", description = "Corrompt la cible et lui inflige 30000 points de dégâts d'Ombre en 18 sec.", mechanic = nil},
[65812] = {name = "Affliction instable", description = "L’énergie de l’Ombre détruit lentement la cible, infligeant 23125 to 26875 points de dégâts en 15 sec. De plus, si l'Affliction instable est dissipée, celui qui la dissipe subit 13875 to 16125 points de dégâts d'Ombre et est réduit au silence pendant 4 sec.", mechanic = nil},
[65814] = {name = "Malédiction d'agonie", description = "Afflige la cible d'une Malédiction d'agonie qui lui inflige 42000 points de dégâts d'Ombre en 24 sec. Les dégâts sont progressifs et sont plus importants avec le temps. Une seule malédiction par démoniste peut être active sur une cible.", mechanic = nil},
[65815] = {name = "Malédiction d'épuisement", description = "Réduit la vitesse de la cible de 50% pendant 12 sec. Une seule malédiction par démoniste peut être active sur une cible.", mechanic = "Piéger"},
[65816] = {name = "Flammes infernales", description = "Enflamme la zone qui entoure le lanceur de sorts. Il subit 1500 points de dégâts de Feu et inflige 9000 points de dégâts de Feu à tous les ennemis proches toutes les 1 s. Dure 15 sec.", mechanic = nil},
[65819] = {name = "Douleur brûlante", description = "Inflige une douleur brûlante à la cible ennemie qui subit 5490 to 6510 points de dégâts de Feu.", mechanic = nil},
[65821] = {name = "Trait de l'ombre", description = "Lance un éclair de l'ombre sur l'ennemi et lui inflige 11813 to 13187 points de dégâts d'Ombre.", mechanic = nil},
[65837] = {name = "Fouet de la vigne", description = "Attaque jusqu'à 5 cibles, leur infligeant 43 to 57 points de dégâts de Nature toutes les secondes.", mechanic = nil},
[65843] = {name = "Onde de choc", description = "Projette une onde de force devant l'ancien. Elle inflige 324 to 376 points de dégâts et fait tomber à la renverse toutes les cibles ennemies à moins de 30 mètres.", mechanic = nil},
[65854] = {name = "Feu stellaire", description = "Inflige 12387 to 14613 points de dégâts des Arcanes à la cible.", mechanic = nil},
[65855] = {name = "Essaim d'insectes", description = "La cible ennemie est assaillie par des insectes. Ses chances de toucher avec les attaques de mêlée et à distance sont réduites de 3% et elle subit 22200 to 25800 points de dégâts de Nature en 12 sec.", mechanic = nil},
[65856] = {name = "Eclat lunaire", description = "Brûle l'ennemi et lui inflige 4140 to 4860 points de dégâts des Arcanes, puis 7000 points de dégâts des Arcanes supplémentaires en 12 sec.", mechanic = nil},
[65857] = {name = "Sarments", description = "Immobilise la cible sur place et lui inflige 1576.7 to 1756.7 points de dégâts de Nature en 10 sec. Si la cible subit des dégâts, l'effet peut être interrompu.", mechanic = "Immobilisé"},
[65858] = {name = "Bouclier des lumières", description = "Le Bouclier des lumières protège la sœur en absorbant 1200000 points de dégâts et en empêchant l'interruption des sorts pendant 16 sec.", mechanic = nil},
[65859] = {name = "Cyclone", description = "Projette la cible ennemie dans les airs. Elle est incapable d'agir mais elle est invulnérable pendant 6 sec au maximum. Une seule cible peut être affectée par votre Cyclone à la fois.", mechanic = "Banni"},
[65860] = {name = "Ecorce", description = "La peau du druide devient aussi dure que de l'écorce. Tous les dégâts subis sont réduits de 40%. Ce sort est utilisable quand vous êtes étourdi, gelé, stupéfié, apeuré ou endormi. Utilisable avec toutes les formes. Dure 12 sec.", mechanic = nil},
[65861] = {name = "Force de la nature", description = "Invoque 3 tréants qui attaquent les cibles ennemies pendant 30 sec.", mechanic = nil},
[65862] = {name = "Colère", description = "Inflige 8460 to 9540 points de dégâts de Nature à la cible.", mechanic = nil},
[65863] = {name = "Lucioles", description = "Réduit l’armure de la cible de 15% pendant 40 sec. Tant qu'elle est affectée, la cible ne peut pas se camoufler ni devenir invisible.", mechanic = nil},
[65866] = {name = "Tir explosif", description = "Vous lancez une charge explosive sur la cible ennemie et lui infligez 4533 to 5467 points de dégâts de Feu. La charge explose ensuite sur la cible toutes les secondes pendant 2 sec supplémentaires.", mechanic = nil},
[65867] = {name = "Tir assuré", description = "Un tir assuré qui inflige les dégâts de l'arme à une cible.", mechanic = nil},
[65868] = {name = "Tir", description = "Tire sur un ennemi et lui inflige des dégâts physiques.", mechanic = nil},
[65869] = {name = "Désengagement", description = "Vous tentez de vous désengager du combat avec la cible en faisant un bond en arrière.", mechanic = nil},
[65871] = {name = "Dissuasion", description = "Lorsque cette technique est activée, elle augmente vos chances de parer de 100% et vous confère 100% de chances de dévier tous les sorts lancés par des cibles se trouvant devant vous. Dure 5 sec.", mechanic = nil},
[65874] = {name = "Bouclier des ténèbres", description = "Le Bouclier des ténèbres protège la sœur en absorbant 1200000 points de dégâts et en empêchant l'interruption des sorts pendant 16 sec.", mechanic = nil},
[65875] = {name = "Pacte de la jumelle", description = "Le Pacte de la jumelle rend 50% de leur total de points de vie.", mechanic = nil},
[65876] = {name = "Pacte de la jumelle", description = "Le Pacte de la jumelle rend 50% de leur total de points de vie.", mechanic = nil},
[65877] = {name = "Piqûre de wyverne", description = "Une piqûre qui endort la cible pendant 8 sec. Tout point de dégâts subi par la cible annule l'effet. Quand la cible se réveille, la Piqûre inflige 2.6 points de dégâts de Nature en 8 sec. Une seule technique de Morsure ou de Piqûre par chasseur peut être active sur la cible en même temps.", mechanic = "Endormi"},
[65879] = {name = "Puissance des jumelles", description = "La puissance partagée des Jumelles augmente les dégâts physiques infligés de 20% et permet à la Jumelle de tenir une arme dans chaque main pendant 15 sec.", mechanic = nil},
[65880] = {name = "Piège de givre", description = "Place un piège de givre qui crée autour de lui une coulée de givre d'une durée de 30 sec lorsque le premier ennemi s'en approche. Tous les ennemis qui se trouvent à moins de 10 mètres sont ralentis de 50% tant qu'ils sont dans sa zone d'effet. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois.", mechanic = nil},
[65883] = {name = "Visée", description = "Un tir précis qui augmente les points de dégâts infligés par votre attaque à distance de 6000 et réduit les soins prodigués à cette cible de 50%. Dure 10 sec.", mechanic = nil},
[65916] = {name = "Puissance des jumelles", description = "La puissance partagée des Jumelles augmente les dégâts physiques infligés de 20% et permet à la Jumelle de tenir une arme dans chaque main pendant 15 sec.", mechanic = nil},
[65918] = {name = "Etourdi", description = "Tombée de sa monture, la cible est étourdie pendant 10 sec.", mechanic = nil},
[65924] = {name = "Fulgurance", description = "Déclenche une attaque fulgurante, instantanée sur un ennemi et lui inflige les dégâts de l'arme plus 35. Ne peut être utilisée qu'une fois que la cible a esquivé. Fulgurance ne peut pas être bloquée, esquivée ou parée.", mechanic = nil},
[65925] = {name = "Assaut continuel", description = "Quand un personnage-joueur en cours d'incantation subit Fulgurance, ses dégâts et soins magiques sont réduits de 50% pendant 6 sec.", mechanic = nil},
[65926] = {name = "Frappe mortelle", description = "Une attaque vicieuse qui inflige les dégâts de l'arme plus 1000 et blesse la cible. L'efficacité des sorts de soins dont elle est la cible est réduite de 50% pendant 10 sec.", mechanic = nil},
[65927] = {name = "Charge", description = "Charge un ennemi et l'étourdit pendant 1.50 sec.", mechanic = nil},
[65929] = {name = "Charge étourdissante", description = "Charge un ennemi et l'étourdit pendant 1.50 sec.", mechanic = "Étourdi"},
[65930] = {name = "Cri d’intimidation", description = "Le guerrier pousse un cri qui oblige les ennemis se trouvant à moins de 8 mètres à se recroqueviller sous l'effet de la peur pendant 8 sec.", mechanic = "En fuite"},
[65932] = {name = "Représailles", description = "Contre-attaque instantanément tous les ennemis qui vous attaquent en mêlée pendant 12 sec. Les attaques de mêlée portées dans votre dos ne peuvent pas être contre-attaquées. Un maximum de 20 attaques provoquera des représailles.", mechanic = nil},
[65935] = {name = "Désarmement", description = "Retire l'arme tenue en main droite et les armes à distance à l'ennemi pendant 10 sec.", mechanic = "Désarmé"},
[65936] = {name = "Fracasser armure", description = "Fracasse l'armure de la cible et la réduit de 4% par attaque Fracasser armure portée. Génère un niveau élevé de menace. La menace est augmentée par la puissance d'attaque. Peut être utilisé jusqu'à 5 fois. Dure 20 sec.", mechanic = nil},
[65940] = {name = "Lancer fracassant", description = "Vous lancez votre arme sur l'ennemi et lui infligez les dégâts de l'arme en plus de réduire l'armure de la cible de 20% pendant 10 sec ou d'annuler les invulnérabilités.", mechanic = nil},
[65946] = {name = "Tempête de lames", description = "Dans un tourbillon d'acier, vous attaquez les ennemis se trouvant à moins de 8 mètres et infligez 50% des dégâts de l'arme.", mechanic = nil},
[65949] = {name = "Rapidité val'kyr", description = "La Puissance des jumelles augmente la vitesse d'attaque de 10% pendant 15 sec. Cumulable.", mechanic = nil},
[65950] = {name = "Toucher de lumière", description = "Vous avez été affecté par le toucher ténébreux des Val'kyrs, ce qui inflige 5850 to 6150 points de dégâts de Lumière aux joueurs sous l'effet de l'Essence sombre toutes les 2 s. Le Toucher de lumière ne fait pas de mal aux cibles qui ont les mêmes caractéristiques.", mechanic = nil},
[65954] = {name = "Hémorragie", description = "Une attaque instantanée qui inflige 110% des dégâts de l'arme à l'adversaire et provoque une hémorragie. Augmente tous les dégâts physiques infligés à la cible de 75 au maximum. Utilisable 10 fois ou pendant 15 sec. Vous gagnez 1 point de combo.", mechanic = nil},
[65955] = {name = "Eventail de couteaux", description = "Lance instantanément les deux armes sur toutes les cibles se trouvant à moins de 10 mètres, infligeant 90% des dégâts des armes.", mechanic = nil},
[65956] = {name = "Déluge de lames", description = "Augmente votre vitesse d'attaque de 20%. De plus, vos attaques frappent un adversaire proche supplémentaire. Dure 15 sec.", mechanic = nil},
[65957] = {name = "Eviscération", description = "Coup de grâce qui inflige 8750 to 11250 points de dégâts physiques.", mechanic = nil},
[65960] = {name = "Cécité", description = "Aveugle la cible et la force à errer, désorientée, pendant 1 min au maximum. Si la cible subit des dégâts, l'effet est annulé.", mechanic = "Désorienté"},
