[65973] = {name = "Horion de terre", description = "Frappe instantanément la cible avec un effet commotionnant et inflige 3213 to 3387 points de dégâts de Nature. Interrompt également les incantations de sorts et empêche l'utilisation de sorts de cette école de magie pendant 2 sec.", mechanic = nil},
