[65986] = {name = "Arme du Maelström", description = "Réduit le temps d'incantation de votre prochain sort Eclair, Chaîne d'éclairs, Vague de soins inférieurs, Vague de soins ou Salve de guérison de 20%.", mechanic = nil},
