[79892] = {name = "Bouclier d'eau", description = "Le lanceur de sorts est entouré de 3 globes d'eau. Les attaques et les sorts utilisés contre lui infligent des dégâts de Givre à l'attaquant et ralentissent ses incantations de sorts. 3 charges, dure 10 min.", mechanic = nil},
[79893] = {name = "Ver de sang", description = "Crée un ver de sang qui attaque les cibles proches. Dure 8 sec.", mechanic = nil},
[79894] = {name = "Poigne de la mort", description = "Maîtrise l'énergie impie qui entoure et lie toute matière, attirant la cible vers le chevalier de la mort et la forçant à l'attaquer pendant 3 sec.", mechanic = "Emparée"},
[79895] = {name = "Frappe de givre", description = "Frappe instantanément l'ennemi et lui inflige 110% des dégâts de l'arme sous forme de dégâts de Givre.", mechanic = nil},
