[79897] = {name = "Toucher de glace", description = "Inflige des dégâts de Givre et réduit de 15% la vitesse d'attaque en mêlée et à distance ainsi que la vitesse d'incantation de la cible pendant 8 sec.", mechanic = nil},
[79899] = {name = "Chaînes de glace", description = "Entrave la cible avec des chaînes gelées et réduit son déplacement à 5% de la normale. La cible récupère sa vitesse de déplacement à raison de 10% toutes les secondes pendant 10 sec.", mechanic = "Piéger"},
[79900] = {name = "Bouclier instable", description = "Les attaques contre le Bouclier instable provoquent un horion statique à l'emplacement d'un ennemi proche. Le horion inflige 58500 to 61500 points de dégâts de Nature aux ennemis se trouvant à moins de 6 mètres de la cible.", mechanic = nil},
