[79905] = {name = "Réanimation morbide", description = "Une goule se lève pour combattre à vos côtés. Si aucun cadavre humanoïde ayant rapporté de l'expérience ou de l'honneur n'est disponible, vous devez utiliser de la Poussière de cadavre pour compléter le sort. Vous pouvez avoir un maximum d'une goule à la fois. Dure 1 min.", mechanic = nil},
[79907] = {name = "Zone anti-magie", description = "Place une grande Zone anti-magie stationnaire qui réduit de 75% les dégâts des sorts infligés aux membres du groupe ou du raid se trouvant à l'intérieur. La Zone anti-magie dure 10 sec. ou jusqu'à ce que (10000 + 2 * Attack power) points de dégâts des sorts aient été absorbés.", mechanic = nil},
