[79913] = {name = "Chaîne d'éclairs", description = "Un éclair frappe un ennemi et rebondit sur un autre adversaire proche. Ce sort affecte jusqu'à 3 cibles et inflige des dégâts de Nature moins importants à chaque cible.", mechanic = nil},
