[79934] = {name = "Armure démoniaque", description = "Protège le lanceur de sorts, augmente son armure et augmente le montant de points de vie généré par les sorts et effets de 20%. Un seul type de sort d'armure peut être actif à la fois. Dure 30 min.", mechanic = nil},
