[79939] = {name = "Trait du chaos", description = "Lance un éclair de feu chaotique sur l'ennemi et lui inflige des dégâts de Feu. On ne peut pas résister à Trait du chaos et il traverse toutes les immunités défensives.", mechanic = nil},
[79949] = {name = "Bouclier d'eau", description = "Le lanceur est entouré de 3 globes d'eau, ce qui lui rend 10 points de mana toutes les 5 secondes. S'il est touché par une attaque en mêlée ou à distance ou par un sort, il reçoit 758 points de mana. Un globe d'eau est ainsi dépensé. Un seul globe peut s'activer toutes les quelques secondes. Dure 10 min. Un seul Bouclier élémentaire peut être actif sur le chaman à la fois.", mechanic = nil},
[79954] = {name = "Gangrarmure", description = "Entoure le lanceur d'énergie gangrenée, qui augmente la puissance des sorts de 10%. De plus, vous régénérez 2% de votre maximum de points de vie toutes les 5 s. Le démoniste ne peut bénéficier que d’un seul type de sort d’armure à la fois. Dure 30 min.", mechanic = nil},
[79955] = {name = "Pluie de feu", description = "Invoque une pluie de lave qui brûle tous les ennemis présents dans la zone sélectionnée et leur inflige des dégâts de Feu toutes les 3 s pendant 9 sec.", mechanic = nil},
[79956] = {name = "Malédiction des éléments", description = "Maudit la cible pendant 30 sec, ce qui réduit ses résistances aux Arcanes, au Feu, au Givre, à la Nature et à l'Ombre de 45 et augmente les dégâts magiques subis de 8%. Une seule malédiction par démoniste peut être active sur une cible.", mechanic = nil},
