[79967] = {name = "Bouclier sacré", description = "Augmente les chances de bloquer de 30% pendant 10 sec et inflige des dégâts du Sacré pour chaque attaque bloquée pendant qu'il est actif. Chaque blocage consomme une charge. 3 charges.", mechanic = nil},
[79968] = {name = "Bénédiction des rois", description = "Place sur une cible alliée une Bénédiction qui augmente sa Force, son Agilité, son Endurance et son Intelligence de 5% pendant 5 min.", mechanic = nil},
