[79972] = {name = "Sceau de vérité", description = "Imprègne le paladin de puissance sacrée et permet aux attaques à cible unique d'appliquer Blâme, qui inflige [(0.01 * Spell power + 0.0193 * Attack power) * 5 */ 100] points de dégâts du Sacré supplémentaires en 15 sec. Blâme peut se cumuler jusqu’à 5 fois. Lorsqu'il a été cumulé 5 fois, chacune des attaques du paladin inflige aussi un montant de dégâts du Sacré supplémentaire égal à 15% des dégâts de l'arme. Un seul Sceau peut être actif sur le paladin à la fois. Dure 5 min.\n\n Libérez l'énergie de ce Sceau pour infliger (1 + 0.223 * Spell power + 0.142 * Attack power) points de dégâts du Sacré à un ennemi, augmentés de 10% par application de Blâme sur la cible.", mechanic = nil},
[79975] = {name = "Contrôle mental de Zul'Gurub", description = "Vous permet de contrôler le jeune flagellant capturé.", mechanic = nil},
