[79977] = {name = "Bénédiction de puissance", description = "Place une Bénédiction sur une cible alliée qui augmente sa puissance d'attaque de 20% et sa puissance d'attaque à distance de 10% en plus de lui rendre 1 points de mana toutes les 5 s pendant 5 min.", mechanic = nil},
[80003] = {name = "Flèche noire", description = "Tire une Flèche noire sur la cible, ce qui augmente tous les dégâts que vous infligez à la cible de 6% et inflige des dégâts d'Ombre en 15 sec.", mechanic = nil},
