[104905] = {name = "Drain d’essence", description = "Peroth'arn révèle sa puissance véritable et draine l'essence de tous les ennemis. Pacifie, réduit au silence et étourdit la cible, en plus d’infliger des dégâts d’Ombre pendant 4 sec.", mechanic = nil},
[104936] = {name = "Embrocher", description = "Embroche la cible et l’étourdit. De plus, inflige 9000 to 11000 points de dégâts toutes les 2 secondes et augmente les dégâts subis de 150%.", mechanic = nil},
[104945] = {name = "Longue-vue", description = "Voyez des choses que personne ne peut voir !", mechanic = nil},
[104961] = {name = "Gangredrain", description = "Tue instantanément une cible affectée par la corruption démoniaque et fait retrouver au lanceur son maximum de points de vie.", mechanic = nil},
[104998] = {name = "Don de Sargeras", description = "Imprégné des énergies gangrenées du seigneur démon Sargeras. Rend 20% du maximum de points de vie toutes les 2 secondes. Inflige des dégâts de Feu aux ennemis proches.", mechanic = nil},
[105027] = {name = "Sang cobalt de Shu'ma", description = "Confère la technique Vide de mana.", mechanic = nil},
[105033] = {name = "Sang brûlant", description = "Enflamme le sang d’ennemis se trouvant à distance, choisis au hasard, et leur inflige des dégâts proportionnels à la distance qui les sépare du lanceur.", mechanic = nil},
[105041] = {name = "Larme du Néant", description = "Mannoroth ouvre un énorme portail vers le Néant distordu, permettant aux troupes démoniaques d’envahir la zone.", mechanic = nil},
[105067] = {name = "Haine vengeresse", description = "Haine vengeresse inflige 585000 to 615000 points de dégâts d’Ombre aux ennemis à moins de 6 mètres, les dégâts étant partagés entre tous ceux qui sont touchés.", mechanic = nil},
[105075] = {name = "Colère d’Élune", description = "La colère d’Élune s’abat sur les ennemis de Tyrande, leur infligeant des dégâts des Arcanes.", mechanic = nil},
[105141] = {name = "Inferno !", description = "Mannoroth invoque une horde d’infernaux !", mechanic = nil},
[105171] = {name = "Corruption profonde", description = "Les soins reçus ou les effets d’absorption déclenchent une détonation au bout de 5 cumulés.", mechanic = nil},
[105219] = {name = "Explosion", description = "Inflige 26718 to 29531 points de dégâts physiques aux ennemis proches.", mechanic = nil},
[105238] = {name = "Flamme immortelle", description = "Inflige 24375 to 25625 points de dégâts de Feu.", mechanic = nil},
[105248] = {name = "Sang absorbé", description = "Absorbe les résidus de Sang vicié proches. L'Amalgame devient plus instable avec chaque Résidu absorbé, ce qui augmente ses dégâts de 10% et sa vitesse d’attaque de 20%. Au bout de 9 résidus absorbés, il entre en surchauffe.", mechanic = nil},
[105256] = {name = "Tempête gelée", description = "Hagara est protégée par une bulle d'eau qui la rend insensible à toutes les attaques.", mechanic = "Piéger"},
[105285] = {name = "Ciblage", description = "Tente de cibler un lanceur de sorts ennemi.", mechanic = nil},
[105289] = {name = "Glace fracassée", description = "Inflige 92500 to 107500 points de dégâts de Givre à un ennemi et réduit sa vitesse de déplacement pendant 4 sec.", mechanic = nil},
[105311] = {name = "Longe cristalline", description = "Augmente la puissance d’Hagara.", mechanic = nil},
[105312] = {name = "Surcharge cristalline", description = "Augmente les dégâts subis par Hagara la Lieuse de 25%.", mechanic = nil},
[105314] = {name = "Vague de glace", description = "Inflige 190000 to 210000 points de dégâts de Givre à tous les ennemis à moins de 3 mètres et augmente les dégâts de Givre qu’ils subissent de 50% pendant 2.50 sec.", mechanic = nil},
[105316] = {name = "Javelot de glace", description = "inflige 15000 points de dégâts de Givre dans une zone de 3 mètres autour de l’ennemi le plus proche.", mechanic = nil},
[105369] = {name = "Conduit de foudre", description = "La cible la plus proche est parcourue de courant électrique.", mechanic = nil},
[105401] = {name = "Sang ardent", description = "Le Sang ardent jaillit du tentacule et inflige des dégâts de Feu toutes les 2 s. Les dégâts sont inversement proportionnels aux points de vie dont dispose le Tentacule membre.", mechanic = nil},
[105409] = {name = "Bouclier d'eau", description = "Hagara est protégée par une bulle d'eau qui la rend insensible aux attaques normales.", mechanic = nil},
[105432] = {name = "Rocher glacial", description = nil, mechanic = nil},
[105444] = {name = "Chaleur caustique", description = "Le Tentacule émet de la Chaleur caustique qui inflige 3750 points de dégâts de Feu toutes les 2 s. Chaleur caustique augmente les dégâts de 2 à chaque application. Cumulable.", mechanic = nil},
[105461] = {name = "Souffle de givre", description = "Inflige des dégâts de Givre à un ennemi et réduit sa vitesse de déplacement pendant 8 sec.", mechanic = nil},
[105465] = {name = "Tempête de foudre", description = "Inflige 17500 points de dégâts de Nature à tous les ennemis proches.", mechanic = nil},
[105479] = {name = "Plasma incendiaire", description = "Recouvre la victime du sang brûlant d’Aile de mort. Ce sang épais absorbe jusqu’à 420000 points de vie rendus à la cible et la fait tousser du sang, lui infligeant périodiquement 12000 points de dégâts physiques.", mechanic = nil},
[105482] = {name = "Longe cristalline", description = "Augmente la puissance d’Hagara.", mechanic = nil},
[105486] = {name = "Blizzard", description = "Invoque un blizzard qui stagne 10 sec et inflige 12 points de dégâts de Givre toutes les 2 s à tous les ennemis qui se trouvent dans la zone sélectionnée.", mechanic = nil},
[105490] = {name = "Poigne enflammée", description = "Saisit les victimes dans un cordon de plasma qui s'abat sur elles comme un fouet, ce qui les étourdit pendant 30 sec au maximum et leur inflige 90000 points de dégâts de Feu toutes les 3 sec. La corruption doit être canalisée pour que l'effet soit maintenu, et elle peut être interrompue si des dégâts importants sont infligés au tentacule. Cet effet empêche les personnages-joueurs d'être éjectés du dos d'Aile de mort.", mechanic = nil},
[105521] = {name = "Frénésie infinie", description = "Alors que sa mort approche, Peroth'arn est pris de frénésie, ce qui augmente ses dégâts de 25%.", mechanic = nil},
[105526] = {name = "Vivacité gangrenée", description = "Augmente la vitesse d'attaque de 100%.", mechanic = nil},
[105539] = {name = "Diffusion de mana", description = "Rend un montant de mana qui dépend du montant absorbé, divisé à parts égales à toutes les unités à moins de 30 mètres.", mechanic = nil},
[105543] = {name = "Absorption d'énergie gangrenée", description = "Illidan interrompt le Drain d’essence de Peroth'arn. Le satyre est étourdi et humilié, les héros sauvés.", mechanic = nil},
[105544] = {name = "Décomposition gangrenée", description = "Le contact pestilentiel de Peroth'arn provoque la décomposition de l'intérieur d'une cible aléatoire, qui subit instantanément 38000 to 42000 points de dégâts d'Ombre plus 8075 to 8925 points de dégâts d'Ombre toutes les secondes pendant 10 sec. Tout soin prodigué au personnage-joueur affecté inflige au soigneur un montant de dégâts d'Ombre égal à 100% des points de vie rendus.", mechanic = nil},
[105545] = {name = "Gangretrouble", description = "Etourdit la cible dont l’essence vient d’être drainée.", mechanic = nil},
[105547] = {name = "Régénération", description = "Affaibli après avoir absorbé la magie de Peroth'arn, Illidan entre dans un état méditatif pour se soigner.", mechanic = nil},
[105563] = {name = "Vrilles avides", description = "Saisit les ennemis proches, leur inflige 5250 to 6750 points de dégâts de Feu et les retient sur le dos d’Aile de mort, ce qui réduit leur vitesse de déplacement de 35%. Tant que les ennemis sont retenus par les vrilles, ils ne peuvent pas être éjectés du dos d'Aile de mort.", mechanic = nil},
[105565] = {name = "Cautérisation", description = "Alexstrasza commence à cautériser les Tentacules caustiques, infligeant des dégâts considérables en 5 sec.", mechanic = nil},
[105573] = {name = "Acide digestif", description = "Inflige 67275 to 70725 points de dégâts de Nature à la cible ennemie.", mechanic = nil},
[105610] = {name = "Flammes du Crépuscule", description = "Canalise une flamme d’ombre sur une large zone et barre l’accès à une partie de la plate-forme au sommet du temple du Repos du ver. Utilisable jusqu’à annulation ou interruption.", mechanic = nil},
[105635] = {name = "Retour dans les ombres", description = "Force tous les alliés proches porteurs d’Ecran d’ombre à se camoufler.", mechanic = nil},
[105651] = {name = "Eclair d'élémentium", description = "Aile de mort projette un Eclair d'élémentium sur la plate-forme ciblée. Si l’Eclair d'élémentium atteint sa destination, il inflige 456300 to 479700 points de dégâts de Feu à tous les ennemis plus 380250 to 399750 points de dégâts de Feu toutes les 5 s pendant 2 sec.", mechanic = nil},
[105652] = {name = "Flammes du Crépuscule", description = "Canalise une flamme d’ombre sur une large zone et barre l’accès à une partie de la plate-forme au sommet du temple du Repos du ver. Utilisable jusqu’à annulation ou interruption.", mechanic = nil},
[105700] = {name = "Flammes du Crépuscule", description = "Canalise une flamme d’ombre sur une cible ennemie et lui inflige des dégâts ainsi qu’à tous ses alliés à moins de 8 mètres. Utilisable jusqu’à ce qu’elle soit annulée ou interrompue.", mechanic = nil},
[105723] = {name = "Déflagration d'élémentium", description = "La Déflagration d'élémentium inflige 456300 to 479700 points de dégâts de Feu aux ennemis à moins de 200 mètres plus 29250 to 30750 points de dégâts de Feu jusqu’à ce que l’Eclair d’élémentium soit détruit.", mechanic = nil},
[105784] = {name = "Danse des lames", description = "Inflige 11875 to 13125 points de dégâts physiques toutes les secondes à toutes les cibles à moins de 13 mètres. De plus, toutes les attaques subies sont déviées.", mechanic = nil},
[105799] = {name = "Zone temporelle", description = "Nozdormu forme une Zone temporelle à l'emplacement ciblé, faisant se perdre dans le temps le Météore en élémentium, dont la vitesse de déplacement est considérablement réduite lorsqu'il pénètre dans cette zone. Toute créature ennemie se trouvant dans la Zone temporelle voit sa vitesse d'attaque réduite de 50%.", mechanic = nil},
[105834] = {name = "Nucléus surchauffé", description = "Le noyau de l'Amalgame atteint un niveau d’instabilité critique. Il inflige 30450 to 39550 points de dégâts de Feu à tous les ennemis toutes les 3 s. Si l'Amalgame est détruit alors qu’il est dans cet état, il déclenche une Déflagration nucléaire.", mechanic = nil},
[105841] = {name = "Morsure dégénérative", description = "Inflige 2340 to 2460 points de dégâts d'Ombre toutes les 1 s pendant 10 sec. Cumulable.", mechanic = nil},
[105845] = {name = "Déflagration nucléaire", description = "Le nucléus surchauffé de l'Amalgame déclenche une énorme explosion qui inflige 350625 to 399375 points de dégâts de Feu aux cibles à moins de 8 mètres. La puissance de la déflagration soulève une des plaques d'élémentium d'Aile de mort s’il s’en trouve une à portée.", mechanic = nil},
[105847] = {name = "Sceller la brèche d’armure", description = "Quand une plaque d’armure est soulevée, les Tendons brûlants tirent rapidement dessus pour la refermer, ce qui protège les tendons de dégâts supplémentaires et empêche la plaque de s’envoler.", mechanic = nil},
[105848] = {name = "Sceller la brèche d’armure", description = "Quand une plaque d’armure est soulevée, les Tendons brûlants tirent rapidement dessus pour la refermer, ce qui protège les tendons de dégâts supplémentaires et empêche la plaque de s’envoler.", mechanic = nil},
[105855] = {name = "Tourbillon de lames", description = nil, mechanic = nil},
[105858] = {name = "Souffle du Crépuscule", description = "inflige de 34000 à 46000 points de dégâts d’Ombre à tous les ennemis se trouvant devant le lanceur de sorts.", mechanic = nil},
[105859] = {name = "Traverser", description = "Inflige 5000 points de dégâts physiques par seconde et augmente les dégâts subis de 50% pendant 8 sec.", mechanic = nil},
[105915] = {name = "Ecran d’ombre", description = "Grâce aux pouvoirs magiques d'Illidan, il est recouvert d’ombres tremblantes qui le cachent des démons.", mechanic = nil},
[105925] = {name = "Lumière faiblissante", description = "Aspire toute lumière hors de la cible et l'attire dans le Crépuscule. À la fin de l'effet, les cibles se trouvant dans le royaume du Crépuscule sont instantanément tuées. Les cibles se trouvant dans le royaume normal sont attirées vers le royaume du Crépuscule, et toute menace contre Ultraxion est remise à zéro. De plus, aucune menace n'est générée pendant 5 sec.", mechanic = nil},
[105984] = {name = "Boucle temporelle", description = "Capture l'esprit dans une boucle temporelle finie. Toute attaque qui normalement tuerait la cible lui fait atteindre à la place 100% de son maximum de points de vie.", mechanic = nil},
[106005] = {name = "Absorption de sang", description = "Les Amalgames détruits dégradent le maximum de points de vie de toutes les cibles ennemies de 5%. Cet effet se produit quel que soit l’état du nucléus et se cumule jusqu’à 15 fois.", mechanic = nil},
[106199] = {name = "Corruption du sang : Mort", description = "La corruption d’Aile de mort parcourt vos veines. Le personnage-joueur sera infecté par le Sang d’Aile de mort si la corruption n’est pas retirée rapidement. Lorsqu’il est dissipé, cet effet passe à une nouvelle cible et peut muter en Corruption du sang : Terre.", mechanic = nil},
[106200] = {name = "Corruption du sang : Terre", description = "Une ombre de Neltharion parcourt vos veines. Vous serez infecté par le Sang de Neltharion si la corruption n’est pas retirée rapidement. Lorsqu’il est dissipé, cet effet passe à une nouvelle cible et peut muter en Corruption du sang : Mort.", mechanic = nil},
[106218] = {name = "Dernier défenseur d’Azeroth", description = "Reçoit de Thrall le Gardeterre la force inébranlable des pierres anciennes d'Azeroth. Temps de recharge des techniques défensives réduits de 50%. Durée des techniques défensives augmentée de 100%.", mechanic = nil},
[106369] = {name = "Transfert du Crépuscule", description = "Ultraxion attire ses ennemis dans le royaume du Crépuscule, où ils peuvent être affectés par ses attaques du Crépuscule.", mechanic = nil},
[106371] = {name = "Heure du Crépuscule", description = "L’Heure du Crépuscule s’abat sur les ennemis d'Ultraxion. Les ennemis pris dans le royaume du Crépusucle subissent 300000 points de dégâts d’Ombre auxquels il est impossible de résister. Les personnages-joueurs qui se trouvent dans le royaume normal sont instantanément forcés à rentrer dans le royaume du Crépuscule. Si aucun ennemi n'est touché par Heure du Crépuscule, les Aspects subissent l'attaque de plein fouet, ce qui perturbe le bouclier qui protège l'Âme des dragons, marquant la fin de toute vie sur Azeroth.", mechanic = nil},
[106372] = {name = "Monstruosité instable", description = "L’instabilité crépusculaire croissante provoque l’éruption d’arcs d’énergie du Crépuscule sur la peau d'Ultraxion toutes les 6 secondes. L'instabilité empêche Ultraxion de parer les attaques.", mechanic = nil},
[106388] = {name = "Eruption du Crépuscule", description = "L’instabilité d'Ultraxion atteint son niveau maximum, déclenchant une énorme explosion qui détruit tous les ennemis proches et Ultraxion lui-même.", mechanic = nil},
[106400] = {name = "Empaler", description = "Le Tentacule de queue se prépare à empaler sa cible, infligeant 1200000 points de dégâts physiques.", mechanic = nil},
[106415] = {name = "Jaillissement du Crépuscule", description = "Jaillissement du Crépuscule inflige 73125 to 76875 points de dégâts d’Ombre et augmente les dégâts magiques subis de 50% pendant 6 sec. Cumulable.", mechanic = nil},
[106523] = {name = "Cataclysme", description = "Aile de mort tente d’achever la tâche qu’il a commencée en produisant un second Cataclysme. Il inflige 1462500 to 1537500 points de dégâts de Feu à tous les ennemis.", mechanic = nil},
[106548] = {name = "Douleur déchirante", description = "La douleur infligée à Aile de mort par la perte d’un de ses membres interrompt sa concentration, l’étourdit et lui inflige un montant de dégâts égal à 20% de ses points de vie.", mechanic = nil},
[106588] = {name = "Perce-faille", description = "Alexstrasza a révélé la faiblesse du membre, augmentant les dégâts qu’il subit de 100%.", mechanic = nil},
[106600] = {name = "Perce-faille", description = "Nozdormu a révélé la faiblesse du membre, augmentant les dégâts qu’il subit de 100%.", mechanic = nil},
[106613] = {name = "Perce-faille", description = "Ysera a révélé la faiblesse du membre, augmentant les dégâts qu’il subit de 100%.", mechanic = nil},
