[106730] = {name = "Tétanos", description = "Inflige 35100 to 36900 points de dégâts d'Ombre toutes les 1 s pendant 6 sec. Cumulable.", mechanic = nil},
[106791] = {name = "Shrapnel", description = "Le Fragment tire un morceau de Shrapnel sur la cible, ce qui lui inflige 390000 to 410000 points de dégâts physiques et transperce son armure.", mechanic = nil},
[106834] = {name = "Sang vicié", description = "Le Sang vicié jaillit d’Aile de mort et inflige des dégâts de Feu toutes les 2 s.", mechanic = nil},
[107018] = {name = "Aspect d’assaut", description = "Aile de mort commence à chercher le prochain ennemi qu’il attaquera. Il s’en prendra à la plate-forme portant le plus grand nombre de joueurs parmi celles saisies par ses membres.", mechanic = nil},
[107029] = {name = "Empaler un Aspect", description = "Inflige à la cible un montant de dégâts physiques égal à 25% de son maximum de points de vie.", mechanic = nil},
[107104] = {name = "Cri démoralisant", description = "Réduit la puissance d'attaque en mêlée des ennemis proches pendant 20 sec.", mechanic = nil},
[107105] = {name = "Eventail de couteaux", description = "Lance instantanément des couteaux sur tous les ennemis proches, infligeant 100% des dégâts des armes.", mechanic = nil},
[107111] = {name = "Explosion des Arcanes", description = "Libère une vague explosive de magie qui inflige des dégâts des Arcanes aux ennemis qui se trouvent à proximité.", mechanic = nil},
[107112] = {name = "Nova de givre", description = "Inflige des dégâts de Givre à tous les ennemis proches et les immobilise pendant 4 sec au maximum.", mechanic = "Immobilisé"},
[107113] = {name = "Lancer de poison", description = "Lance sur un ennemi un flacon qui lui inflige des dégâts de Nature et l’empoisonne. L’ennemi subit des dégâts sur la durée.", mechanic = nil},
[107114] = {name = "Flaque de poison", description = "Inflige des dégâts de Nature à tous les ennemis se trouvant dans la flaque. Dure 6 sec.", mechanic = nil},
[107115] = {name = "Lancer de dynamite", description = "Inflige des dégâts de Feu à tous les ennemis qui se trouvent dans la zone sélectionnée.", mechanic = nil},
[107116] = {name = "Rafale hurlante", description = "Un vent glacial frappe la cible et inflige des dégâts de Givre à tous les ennemis se trouvant à moins de 10 mètres. Inflige des dégâts triples aux cibles gelées.", mechanic = nil},
[107439] = {name = "Barrage du Crépuscule", description = "Inflige 280000 points de dégâts d’Ombre aux personnages-joueurs à moins de 5 mètres du point d'impact. Si aucun personnage-joueur n'est touché, le Brûleciel subit le choc de plein fouet.", mechanic = nil},
[107518] = {name = "Détonation", description = "Fait exploser un explosif de grande taille, qui inflige 243750 to 256250 points de dégâts de Feu aux ennemis à moins de 8 mètres, endommage les machines du Brûleciel et tue le lanceur.", mechanic = nil},
[107558] = {name = "Dégénérescence", description = "Sème la ruine en infligeant 114000 to 126000 points de dégâts d’Ombre à tous les ennemis se trouvant devant le lanceur. Ces derniers sont également victimes de sombres énergies qui leur infligent 9000 points de dégâts d’Ombre toutes les 2 s pendant 1 min.", mechanic = nil},
[107567] = {name = "Frappe brutale", description = "Inflige 150% des dégâts de l’arme et fait subir à la cible 9000 points de dégâts physiques toutes les 2 s pendant 1 min.", mechanic = nil},
