[107597] = {name = "Jet de rocher", description = "Lance un rocher sur la cible, lui inflige 84825 to 95175 points de dégâts et étourdit tout le monde dans une zone de taille réduite.", mechanic = nil},
[107675] = {name = "Tempête de poussière", description = "Inflige 18850 to 21150 points de dégâts de Nature toutes les secondes à tous les ennemis proches.", mechanic = nil},
[107770] = {name = "Eau pure", description = "Augmente tous les dégâts de 50% et annule les effets de Tempête de poussière.", mechanic = nil},
