[107840] = {name = "Vol de charognards", description = "Une vague de magie chaotique asperge les ennemis devant le lanceur et leur inflige 42750 to 52250 points de dégâts d'Ombre.", mechanic = nil},
[107850] = {name = "Assaut focalisé", description = "Découpe l'ennemi avec rapidité, lui infligeant 50% des dégâts de l'arme.", mechanic = nil},
[107852] = {name = "Corruption du Crépuscule", description = "Inflige 28275 to 31725 points de dégâts d’Ombre à tous les ennemis dans une zone autour du lanceur de sorts et augmente l’énergie de 34.", mechanic = nil},
[107865] = {name = "Annihilation des Arcanes", description = "Frappe la cible avec de l'énergie et lui inflige 19000 to 21000 points de dégâts des Arcanes. Chaque fois que vous lancez Annihilation des Arcanes, les dégâts de tous les sorts des Arcanes sont augmentés de 10% et le temps d'incantation des sorts est réduit de 10%. L'effet est cumulable jusqu'à 4 fois et dure 6 sec.", mechanic = nil},
[107872] = {name = "Rage du Crépuscule", description = "Augmente tous les dégâts infligés de 25%.", mechanic = nil},
[107880] = {name = "Mana infini", description = "Régénère la totalité du mana en 5 sec.", mechanic = nil},
[107899] = {name = "Explosion", description = "Explose et inflige 27750 to 32250 points de dégâts de Feu à tous les ennemis à l’intérieur d’une zone de taille réduite.", mechanic = nil},
[107900] = {name = "Protection démoniaque", description = "Protège l'Arcaniste corrompu proche avec de l’énergie gangrenée, absorbant 50000 points de dégâts pendant 5 sec.", mechanic = nil},
[107980] = {name = "Explosion de lave", description = "Projette de la lave en fusion sur la cible et lui inflige 43750 to 56250 points de dégâts de Feu.", mechanic = nil},
[108010] = {name = "Balle lourde", description = "Tire à grande vitesse une balle lourde en métal qui inflige 17100 to 18900 points de dégâts à une cible.", mechanic = nil},
[108035] = {name = "Barricade des dragons", description = "La puissance de l’Âme des dragons forme une barrière impénétrable.", mechanic = nil},
[108038] = {name = "Harpon", description = "Harponne une cible.", mechanic = nil},
