[473687] = {name = "Aube d’argent", description = "Aube d’argent\nTant qu’un écuyer d’argent est présent dans le groupe ou raid, tuer des silithides dans la Ruche’Ashi, la Ruche’Regal et la Ruche’Zora accorde de la réputation auprès de l’Aube d’argent."},
[473701] = {name = "Provocation", description = "[q]Provoque la cible et la force à vous attaquer. Aucun effet si elle est déjà en train de vous attaquer."},
[474400] = {name = "Rendre fou", description = "[q]Rend momentanément folle une cible ennemie. Ses attaques sont accélérées de 50 % et ses déplacements de 80 %. Elle attaque ses alliés pendant 6 sec. Les cibles affectées subissent des dégâts réduits de 90 %."},
[474402] = {name = "Attaque mentale", description = "[q]Inflige des dégâts d’ombre à un ennemi, mais génère un niveau élevé de menace."},
[474444] = {name = "Grondement du tonnerre", description = "[q]Inflige les dégâts normaux plus 15 aux ennemis proches et les projette dans les airs."},
[474742] = {name = "Coup de queue", description = "[q]Inflige 600 to 1000 points de dégâts aux ennemis dans un cône derrière le lanceur et les repousse."},
[1213155] = {name = "Infection volatile", description = "[q]Infecte un personnage adverse pendant 2 min. Celui-ci inflige 875 to 1125 points de dégâts de nature toutes les 5 s à ses alliés proches."},
[1213170] = {name = "Souffle nauséabond", description = "Souffle nauséabond\nInflige 350 to 450 points de dégâts toutes les 3 s.\nTemps de recharge augmentés de 10 s."},
[1213248] = {name = "Colère", description = "[q]Lance un éclair sur un personnage adverse et lui inflige des dégâts de nature."},
[1213250] = {name = "Météores", description = "[q]Invoque une pluie de lumière lunaire qui brûle l’ensemble des adversaires dans la zone sélectionnée et leur inflige 720 to 880 points de dégâts des arcanes toutes les 2 s pendant 6 sec."},
[1213253] = {name = "Sarments", description = "[q]Enracine un personnage adverse pour l’immobiliser et lui infliger des dégâts de nature toutes les 3 s pendant 15 sec au maximum."},
[1213269] = {name = "Tir explosif", description = "[q]Inflige les points de dégâts de l’arme plus 100 points de dégâts de feu à un personnage adverse et à tous ses alliés proches."},
[1213276] = {name = "Éclair de givre", description = "[q]Inflige des dégâts de givre à un personnage adverse et réduit sa vitesse de déplacement pendant 4 sec."},
[1213279] = {name = "Blizzard", description = "[q]Invoque un blizzard qui stagne 10 sec et inflige 4 points de dégâts de givre toutes les 2 s à l’ensemble des adversaires dans la zone sélectionnée."},
[1213298] = {name = "Lumière sacrée", description = "[q]Fait appel à la magie du sacré pour soigner un personnage allié."},
[1213300] = {name = "Protection divine", description = "[q]Protège le lanceur de sorts de toutes les attaques et de tous les sorts pendant 8 sec. Mais pendant ce temps, le lanceur de sorts ne peut pas attaquer ou utiliser de sorts."},
[1213301] = {name = "Marteau de la justice", description = "[q]Étourdit un personnage adverse. Il est incapable de se déplacer et d’attaquer pendant 4 sec."},
[1213331] = {name = "Brûlure de mana", description = "[q]Frappe un personnage adverse avec un éclair d’anti-mana. Pour chaque point de mana absorbé par l’éclair, la cible subit 0.5 points de dégâts."},
[1213332] = {name = "Contrôle mental", description = "[q]Prend le contrôle d’un adversaire humanoïde de niveau 60 ou inférieur, pendant 15 sec."},
[1213334] = {name = "Forme d’ombre", description = "[q]Adopte une forme d’ombre, augmente les points de dégâts d’ombre infligés de 20 % et réduit les dégâts subis par les attaques en mêlée de 20 %. Forme d’ombre dure jusqu’à annulation."},
[1213336] = {name = "Attaque mentale", description = "[q]Inflige des dégâts d’ombre à un personnage adverse, mais génère un niveau élevé de menace."},
[1213442] = {name = "Garrot", description = "[q]Inflige 36 points de dégâts à un personnage adverse toutes les 3 s pendant 18 sec, si vous êtes en camouflage lorsque vous déclenchez l’attaque."},
[1213451] = {name = "Pluie de feu", description = "[q]Invoque une pluie de lave qui brûle l’ensemble des adversaires dans la zone sélectionnée et leur inflige 3 points de dégâts de feu toutes les 3 s pendant 9 sec."},
[1213452] = {name = "Peur", description = "[q]Effraie un personnage adverse et l’oblige à fuir pendant 4 sec. Vous ne pouvez effrayer qu’une cible à la fois."},
[1213462] = {name = "Heurt de bouclier", description = "[q]Inflige 20 points de dégâts à un personnage adverse et l’étourdit pendant 2 sec."},
[1213464] = {name = "Coup de tonnerre", description = "[q]Inflige 24 to 26 points de dégâts de nature aux adversaires proches, augmente le temps entre leurs attaques de 33 % et réduit leur vitesse de déplacement de 40 % pendant 10 sec."},
[1213466] = {name = "Loup Tranchecrin", description = "[q]Invoque 1 loup Tranchecrin pour accompagner le lanceur de sorts. Dure jusqu’au renvoi."},
[1213477] = {name = "Chaîne d’éclairs", description = "[q]Un éclair frappe un personnage adverse et rebondit sur un autre personnage adverse proche. Ce sort affecte jusqu’à 3 cibles et inflige des dégâts de nature plus importants à chaque cible."},
[1213479] = {name = "Bouclier de foudre", description = "[q]Le lanceur de sorts s’entoure de 3 boules de foudre qui ont 50 % de chances d’infliger 2 points de dégâts aux adversaires qui l’attaquent en mêlée ou à distance. Chaque adversaire touché détruit une boule. Le bouclier expire après 10 min ou lorsqu’il a frappé 3 fois."},
[1213480] = {name = "Totem de lien terrestre", description = "[q]Invoque un Totem de lien terrestre qui dure 45 sec et réduit périodiquement la vitesse de déplacement des adversaires proches à 50 % de la normale."},
[1213482] = {name = "Horion de flammes", description = "[q]Brûle instantanément un personnage adverse et lui inflige des dégâts de feu supplémentaires toutes les 3 s pendant 12 sec."},
