[105925] = {name = "사그라지는 빛", description = "대상으로부터 빛의 힘을 빨아들여, 황혼 속으로 끌어들입니다. 효과가 사라질 때, 대상이 황혼 세계에 있으면 즉사합니다. 대상이 현실 세계에 있으면 황혼 세계로 끌려들어가며, 울트락시온에 대한 위협 수준이 초기화됩니다. 추가로 5 sec 동안은 위협 수준이 생성되지 않습니다.", mechanic = nil},
