[29837] = {name = "Каменный кулак", description = "[q]Увеличивает наносимый заклинателем физический урон на 35%, но снижает скорость его передвижения на 75% и замедляет его атаки на 35%."},
[29846] = {name = "Праздник Огня - неистовство", description = nil},
