[29900] = {name = "Нестабильная магия", description = "[q]Повышение всего урона, причиненного находящимися поблизости неприятелями до 300 ед.\n Повышение всего урона от заклинаний, полученных неприятелем до 600 ед. Время действия – 2 min."},
[29901] = {name = "Кислотный клык", description = "[q]Наносит противнику обычный урон, плюс дополнительный урон, а после этого – дополнительный урон от сил природы раз в 2 сек. в течение 30 sec. Уменьшает броню. Эффект суммируется до 7 раз."},
[29903] = {name = "Пикирование", description = "[q]Шадикит набрасывается на противника и оглушает его, нанося обычный и дополнительный урон."},
[29904] = {name = "Грохот", description = "[q]Наносит находящимся рядом противникам обычный урон и еще 2188 to 2812 ед., не позволяя им применять заклинания в течение 5 sec."},
[29905] = {name = "Взмах крылом", description = "[q]Нанесение 1050 to 1350 ед. урона противникам в секторе перед заклинателем, отбрасывая их назад."},
[29906] = {name = "Накинуться", description = "[q]Наносит противнику обычный урон и еще 500 ед. и вызывает кровотечение, наносящее 2550 to 3450 ед. урона за 10 sec."},
[29908] = {name = "Астральное жало", description = "[q]Порожденные магией Медива и Круговертью Пустоты, эти твари способны вырвать из тела противника его магию, превратив ее в урон от тайной магии."},
[29911] = {name = "Искривление силы", description = "[q]Проклятие находящихся рядом противников: увеличение затрат на их заклинания и умения на 100% в течение 15 sec. Повышение затрат касается всех заклинаний и умений, вне зависимости от того, тратится ли на них мана, ярость или энергия."},
[29915] = {name = "Гниение плоти", description = "[q]Наносит противнику 1050 to 1350 ед. урона от темной магии раз в 2 сек. в течение 12 sec."},
[29919] = {name = "Искривленная брешь", description = "[q]Взрыв, наносящий массированный урон от тайной магии находящимся рядом противникам."},
[29920] = {name = "Изменчивая невидимость", description = "[q]Заклинатель то появляется, то исчезает, являя собой связующее звено между этой и следующей жизнью."},
[29922] = {name = "Град огненных шаров", description = "[q]Наносит 1530 to 2070 ед. урона от огня находящимся рядом противникам и еще 200 ед. урона раз в 1 сек. в течение 3 sec."},
[29923] = {name = "Залп ледяных стрел", description = "[q]Нанесение ближайшим противникам урона от магии льда и снижение скорости их передвижения на 4 sec."},
[29924] = {name = "Залп стрел Тьмы", description = "[q]Наносит урон от темной магии находящимся поблизости противникам."},
[29925] = {name = "Огненный шар", description = "[q]Наносит противнику 1530 to 2070 ед. урона от огня и еще 200 ед. урона раз в 1 сек. в течение 3 sec."},
[29926] = {name = "Ледяная стрела", description = "[q]Наносит противнику урон от магии льда и снижает его скорость передвижения на 4 sec."},
[29927] = {name = "Стрела Тьмы", description = "[q]Наносит урон от темной магии находящимся поблизости противникам."},
[29928] = {name = "Жертвенный огонь", description = "[q]Заклинание обжигает противника и наносит дополнительный урон от огня раз в 3 сек. в течение 21 sec."},
[29930] = {name = "Проклятие агонии", description = "[q]Наносит урон от темной магии раз в 3 сек. в течение 30 sec. Одновременно на цель может действовать только одно проклятие каждого чернокнижника."},
[29935] = {name = "Зияющая пасть", description = "[q]Наносит находящимся рядом противникам физический урон раз в 3 сек. в течение 15 sec."},
