[29939] = {name = "Заразный яд", description = "[q]Наносит 850 to 1150 ед. урона от сил природы находящимся рядом противникам."},
[29946] = {name = "Венец пламени", description = "[q]Наложение ауры, которая взрывается, если не будет наложена заново или снята в течение 20 sec. Рассеивается через 20 sec."},
