[29969] = {name = "Призыв снежной бури", description = "[q]Activates the Blizzard NPC."},
[29973] = {name = "Чародейский взрыв", description = "[q]Взрыв чародейской силы, наносящий 9000 to 11000 ед. урона от тайной магии."},
[29982] = {name = "Искривление пространства", description = "[q]Аура, снижающая характеристики находящегося поблизости противника на 3% раз в 3 сек. Аура держится до отмены."},
[29990] = {name = "Замедление", description = "[q]Снижает скорость атаки противника на 50% и скорость передвижения на 80% на 10 sec."},
[29991] = {name = "Ледяные оковы", description = "[q]Сковывание противника ледяными цепями, не позволяющими ему двигаться с места в течение 10 sec."},
[29998] = {name = "Разлагающая лихорадка", description = "[q]Наносит 3000 ед. урона от сил природы раз в 3 сек. и снижает максимальный запас здоровья ближайших целей на 50% на 21 sec."},
[30007] = {name = "Искривление пространства", description = "[q]Аура, снижающая характеристики находящегося поблизости противника на 3% раз в 3 сек. Аура держится до отмены."},
