[30035] = {name = "Массовое замедление", description = "[q]Снижение скорости атаки всех противников на расстоянии 13 м на 35% и замедление их передвижения на 60% в течение 10 sec."},
[30036] = {name = "Похищение магии", description = "[q]Рассеивание магических эффектов с цели, снимающее 1 вредоносный эффект с союзника или 1 благотворный эффект с противника."},
[30039] = {name = "Перенаправление", description = "[q]Применяемые к вам исцеляющие эффекты перенаправляются."},
