[30113] = {name = "Гнилостный укус", description = "[q]Повышение получаемого противником урона на 10% на 30 sec."},
[30115] = {name = "Жертвоприношение", description = "[q]Оглушение цели и высасывание у нее 1500 ед. здоровья раз в 1 с в течение 30 sec."},
[30127] = {name = "Жгучая зола", description = "[q]3000 ед. урона от огня в течение 18 sec."},
[30128] = {name = "Дымовой заряд", description = "[q]Нанесение 1850 to 2150 ед. физического вреда цели и дополнительного урона от огня в течение определенного времени."},
[30129] = {name = "Опаленная земля", description = "[q]Поджигание земли с нанесением 21880 to 28120 ед. урона всему находящемуся на опаленной территории в течение 30 sec."},
[30130] = {name = "Отвлекающие угли", description = "[q]Снижает вероятность попадания заклинаниями и способностями на 30%."},
