[30221] = {name = "Выстрел", description = "[q]Выстрел в цель из оружия дальнего боя."},
[30225] = {name = "Безмолвие", description = "[q]Вызывает немоту ближайших противников, предотвращая сотворение ими заклинаний в течение 4 sec."},
