[30460] = {name = "Пинок", description = nil},
[30466] = {name = "Врата Пустоты - Непоколебимость", description = "[q]Усиление панциря Гнева Пустоты, снижение получаемого им урона на 1%."},
[30467] = {name = "Врата Пустоты - Безмятежность", description = "[q]Восстанавливает жизненные силы Гнева Пустоты, исцеляя его на 4000 ед. раз в 1 сек."},
[30468] = {name = "Врата Пустоты - Господство", description = "[q]Наполнение Гнева Пустоты энергией, увеличивающей наносимый им урон на 1%."},
