[30472] = {name = "Аура тренировки", description = "[q]Повышает вероятность парирования и блокирования ударов находящихся рядом союзников на 4 min."},
[30474] = {name = "Кровожадность", description = "[q]Мгновенная атака, наносящая противнику 120% обычного урона от оружия. Кроме того, последующие успешные атаки ближнего боя (но не более 5) восстановят здоровье на 238 to 262. Время действия – 10 sec."},
[30478] = {name = "Кровоизлияние", description = "[q]Нанесение урона от оружия плюс 110% от обычного и увеличение физического урона, полученного противником, на 10% в течение 10 sec."},
