[30482] = {name = "Раскаленный доспех", description = "[q]Наносит атакующим вас по 195 ед. урона от огня за каждый удар. Повышает вероятность критического эффекта заклинаний на 3% и снижает вероятность получить критический урон на 5%. Маг может использовать единовременно только одно заклинание типа \"Доспех\". Время действия – 30 min."},
[30485] = {name = "Исступление", description = "[q]Увеличивает физический урон и скорость атаки на 1 min."},
[30487] = {name = "Врата Пустоты - Непоколебимость", description = "[q]Призывает врата Пустоты – Непоколебимость"},
