[30491] = {name = "Врата Пустоты - Господство", description = "[q]Призывает врата Пустоты – Господство"},
[30493] = {name = "Выстрел из лука", description = nil},
[30494] = {name = "Липкая жижа", description = "[q]Густая слизь, уменьшающая передвижение и скорость атаки всех находящихся поблизости противников в течение 20 sec."},
[30495] = {name = "Теневой рассекающий удар", description = "[q]Наносит противнику и его ближайшим союзникам урон от оружия, увеличенный на 275 ед. Захватывает до 3 целей."},
[30496] = {name = "Слабая расщелина тьмы", description = "[q]Создает портал Бездны, который наносит урон от темной магии находящимся в нем противникам."},
[30499] = {name = "Откровения", description = nil},
[30500] = {name = "Лик смерти", description = "[q]Принуждение противника к паническому бегству в течение 4 sec и нанесение ему 1260 ед. урона от темной магии. Заклинатель получает 100% урона."},
[30502] = {name = "Темное вращение", description = "[q]Обрушивает на находящихся рядом противников шквал стали и теней в течение until cancelled."},
[30505] = {name = "Стрела Тьмы", description = "[q]Заклинатель начинает вращаться, выпуская по всем окружающим противникам стрелы Тьмы, наносящие 1063 to 1437 ед. урона от темной магии."},
