[33127] = {name = "Печать повиновения", description = "[q]Наделяет атаки паладина возможностью нанести дополнительный урон от светлой магии, равный 7% от базового урона оружия. Единовременно паладин может привести в действие только одно заклинание категории \"Печать\". Длительность – 30 sec.\n\n Высвобождение энергии этой печати обрушивается на противника уроном от светлой магии в 0 ед.; 0 ед., если цель оглушена или обездвижена."},
[33129] = {name = "Темное гниение", description = "[q]Наносит 500 ед. урона от темной магии раз в 2 сек."},
[33130] = {name = "Лик смерти", description = "[q]Принуждение противника к паническому бегству в течение 3 sec и нанесение ему 1750 to 2250 ед. урона от темной магии. Заклинатель получает 100% урона."},
[33131] = {name = "Вызов дикого Охотника Скверны", description = "[q]Призыв на помощь игроку дикого охотника Скверны."},
[33132] = {name = "Кольцо огня", description = "[q]Нанесение урона от огня ближайшим противникам."},
[33144] = {name = "Исцеление", description = "[q]Призывает силы света для исцеления союзника."},
[33147] = {name = "Большее слово силы: щит", description = "[q]Защищен от физического и магического урона.\n Невосприимчив к прерыванию произнесения заклинаний.\n Невосприимчив к эффектам оглушения."},
[33152] = {name = "Молитва исцеления", description = "[q]Обращение к светлой магии для исцеления ближайших участников группы."},
[33173] = {name = "Могущественное превращение", description = "[q]Превращает противника в овцу. Он вынужден бродить вокруг в течение 10 sec. Овца не может атаковать или произносить заклинания, но быстро восстанавливает здоровье и ману. Любой нанесенный урон прекратит действие заклинания. Одновременно заклинание превращения можно наложить только на одну цель. Действует только на животных, драконов, великанов, гуманоидов и фауну."},
[33175] = {name = "Чародейский разряд", description = "[q]Поражает противника ударной волной, нанося урон от сил природы и прерывая применение заклинаний на 5 sec."},
[33230] = {name = "Могучий удар", description = "[q]Наносит противнику обычный урон и еще 139 to 161 ед. и отбрасывает его."},
[33232] = {name = "Шквал", description = "[q]Увеличивает физический урон, наносимый заклинателем, а также повышает скорость атаки в ближнем бою."},
[33237] = {name = "Чародейский взрыв", description = "[q]Заклинатель пускает магическую взрывную волну, наносящую 1750 to 2250 ед. урона от тайной магии ближайшим противникам."},
[33238] = {name = "Вихрь", description = "[q]Кружась в вихре, атакует находящихся рядом противников, нанося обычный урон и еще 110 ед. урона за 15 sec."},
[33239] = {name = "Вихрь", description = "[q]Кружась в вихре стали, заклинатель наносит окружающим противникам обычный урон."},
[33245] = {name = "Ледяная преграда", description = "[q]Заклинателя мгновенно окружает защитный барьер, поглощающий 650 ед. урона. Действует 30 sec. Пока щит держится, заклинания невозможно прервать."},
[33324] = {name = "Старшее исцеление", description = "[q]Призывает силы света для исцеления союзника."},
[33325] = {name = "Темное восстановление", description = "[q]Восполняет союзникам в радиусе 15 м 82 ед. здоровья."},
[33335] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[33339] = {name = "Состояние зеленого портала", description = nil},
[33382] = {name = "Прыготрон", description = nil},
[33384] = {name = "Массовое подчинение", description = nil},
[33385] = {name = "Сожжение маны", description = "[q]Молния, сжигающая ману. На каждую единицу маны, сожженную молнией, жертва получает 2 ед. урона."},
[33387] = {name = "Великое исцеление", description = "[q]Призывает силы света для исцеления союзника."},
[33390] = {name = "Волшебный поток", description = "[q]Вызывает немоту всех противников в радиусе 8 м на 2 sec."},
