[33395] = {name = "Холод", description = "[q]Поражает выбранную область, нанося противникам в радиусе 8 м [( 47 + 54 ) / 2] ед. урона от магии льда и примораживая их к месту максимум на 8 sec. Нанесенный урон может прервать действие эффекта."},
[33417] = {name = "Огненный шар", description = "[q]Наносит противнику урон от огня."},
[33418] = {name = "Снежная буря", description = "[q]Призывает на 10 sec. снежную бурю, наносящую 12 ед. урона от магии льда раз в 2 сек. противникам в области поражения."},
