[33525] = {name = "Удар по земле", description = "[q]Сковывает движения всех находящихся поблизости противников, превращая их в камень."},
[33526] = {name = "Удар пламени", description = "[q]Наносит противнику 18 to 21 ед. урона от огня и увеличивает получаемый урон от огня на 20 на 20 sec."},
[33527] = {name = "Поражение чарами", description = "[q]Нанесение противнику 18 to 21 ед. урона от тайной магии и увеличение получаемого им урона от тайной магии на 20 ед. на 20 sec."},
[33528] = {name = "Ледяная пощечина", description = "[q]Нанесение противнику урона от магии льда. Получаемый противником урон от магии льда повышается на 20 sec."},
[33529] = {name = "Теневое столкновение", description = "[q]Нанесение противнику 18 to 21 ед. урона от темной магии и увеличение получаемого или урона от темной магии на 20 в течение 20 sec."},
[33534] = {name = "Чародейский разряд", description = "[q]Поражает противника ударной волной. Затем наносит дополнительный урон от тайной магии раз в 3 сек. в течение 12 sec."},
