[33547] = {name = "Страх", description = "[q]Вселяет в сердца противников в радиусе 45 м страх, заставляя их в панике убегать. Время действия – 6 sec."},
[33551] = {name = "Разъедающая кислота", description = "[q]Струя разъедающей кислоты наносит удар по всем противникам, находящимся напротив заклинателя, нанося 1094 to 1406 ед. урона от сил природы раз в 3 сек. и ослабляя их броню на 3939 ед. в течение 15 sec."},
[33554] = {name = "Правосудие повиновения", description = "[q]Обрушивает на противника правосудие, нанося 836 to 924 ед. урона от светлой магии."},
[33559] = {name = "Освящение", description = "[q]Освящение земли, на которой стоит паладин, с нанесением 100 ед. урона от светлой магии в течение 20 sec. противникам, которые входят на этот участок."},
[33560] = {name = "Тотем потока лавы", description = "[q]Создает рядом с собой тотем потока магмы, который наносит 65 ед. урона от огня находящимся рядом противникам раз в 3 сек. Время действия – 20 sec."},
[33563] = {name = "Приманивание теней", description = "[q]Приманивает противников к заклинателю."},
[33564] = {name = "Великое благословение могущества", description = "[q]Благословляет находящихся рядом союзников, повышая силу их атаки на 550 на 30 sec."},
[33570] = {name = "Тотем силы бури", description = "[q]Создает рядом с собой тотем с 5 ед. прочности, который повышает силу участников группы в радиусе 40 м на 549 . Время действия – 2 min."},
[33581] = {name = "Божественный щит", description = "[q]Защищает от атак и заклинаний на 10 sec., но при этом увеличивает интервал между атаками на 100%."},
[33617] = {name = "Огненный ливень", description = "[q]Вызов лавового дождя, наносящего всем противникам на выбранном участке 903 to 997 ед. урона от огня раз в 1 сек. в течение 6 sec."},
[33620] = {name = "Удар теней", description = "[q]Поражает противника ударной волной. Затем наносит дополнительный урон от темной магии раз в 3 сек. в течение 12 sec."},
[33623] = {name = "Чародейский взрыв", description = "[q]Создание магической взрывной волны, наносящей противникам урон от тайной магии."},
[33624] = {name = "Снежная буря", description = "[q]Призывает на 10 sec. снежную бурю, наносящую 24 ед. урона от магии льда раз в 2 сек. противникам в области поражения."},
[33625] = {name = "Развеивание магии", description = "[q]Снимает с противника 1 положительный магический эффект и снижает его скорость передвижения на 20%."},
[33626] = {name = "Разящий удар", description = "[q]Удар по противнику с нанесением увеличенного урона в ближнем бою."},
[33627] = {name = "Огненный ливень", description = "[q]Призывает раскаленный дождь пролиться, поджигая всех противников на выбранной территории и нанося им урон от огня раз в 3 сек. в течение 9 sec."},
[33628] = {name = "Привязь молнии", description = "[q]Урон противнику от сил природы в течение 5 sec."},
[33632] = {name = "Экзорцизм", description = "[q]Нанесение демонам или нежити урона от светлой магии."},
[33634] = {name = "Снежная буря", description = "[q]Призывает на 10 sec. снежную бурю, наносящую 380 to 420 ед. урона от магии льда раз в 2 сек. противникам в области поражения."},
[33641] = {name = "Вспышка Света", description = "[q]Призывает силы Света для исцеления союзника."},
[33642] = {name = "Цепное исцеление", description = "[q]Преисполняет раненого союзника целительной энергией, которая распространяется на другого находящегося поблизости союзника. Максимальное количество целей – 3."},
[33643] = {name = "Цепная молния", description = "[q]Поражает противника молнией, которая переходит на другого находящегося рядом противника. Поражает до 5 целей, нанося каждой из них возрастающий урон от сил природы."},
[33653] = {name = "Бешенство", description = "[q]Увеличение физического урона заклинателя на 100% на 10 min."},
[33654] = {name = "Дробление", description = "[q]Разбивает на куски всех противников, обращенных в камень, нанося урон их союзникам. Урон зависит от того, как близко они находятся к раскалываемым целям."},
[33657] = {name = "Резонанс", description = "[q]Импульс направленной звуковой энергии, наносящий 2000 ед. урона от сил природы и повышающий получаемый урон от сил природы на 2000 в течение 45 sec."},
[33661] = {name = "Сокрушение доспеха", description = "[q]Ослабление брони противника на 10% на 20 sec. Эффект суммируется до 5 раз."},
[33663] = {name = "Тотем элементаля земли", description = "[q]Создает рядом с заклинателем тотем стихии. Тотем призывает большого элементаля земли, который защищает заклинателя и союзников. Время действия – 3 min."},
[33665] = {name = "Грозовой гнев", description = "[q]Поражает противника молнией, которая переходит на другого находящегося рядом противника. Поражает до 10 целей, нанося каждой из них возрастающий урон от сил природы."},
[33666] = {name = "Звуковая волна", description = "[q]Мощный выброс звуковой энергии, наносящий находящимся поблизости противникам изначальный и дополнительный урон от сил природы раз в 3 сек. в течение 9 sec. Пока противники находятся под действием звуковой волны, их скорость передвижения снижается на 90%."},
[33675] = {name = "Призыв инферналов", description = nil},
[33676] = {name = "Провокация Хаоса", description = "[q]Вносит смятение в разум противников, заставляя их беспорядочно атаковать друг друга."},
[33678] = {name = "Темная стая", description = "[q]Обрушивает волну хаотической магии на противников перед собой, нанося 660 to 990 ед. урона от темной магии."},
[33679] = {name = "Преисподняя", description = "[q]Призывает метеор из Круговерти Пустоты, который наносит 34 ед. урона от огня и оглушает противников в области действия на 2 sec. Из кратера появляется инфернал, который в течение 1 min. подчиняется призывателю. Для сохранения контроля после истечения времени инфернала нужно поработить. Действует только вне помещений."},
[33684] = {name = "Провокация Хаоса", description = "[q]Насылает на противника временное помешательство, заставляющее его атаковать все ближайшие цели."},
[33688] = {name = "Кристальная атака", description = "[q]Атака противника с нанесением ему урона от тайной магии, равного 120% от урона от оружия."},
[33689] = {name = "Примагничивание", description = "[q]Перемещает противника к вам."},
[33707] = {name = "Громовая поступь", description = "[q]Отбрасывает находящихся рядом противников."},
[33709] = {name = "Рывок", description = "[q]Черносерд совершает рывок к противнику, нанося дополнительный урон и оглушая на 2 sec."},
[33711] = {name = "Прикосновение Бормотуна", description = "[q]Воздух вокруг вас пульсирует от звуковой энергии."},
[33731] = {name = "Правосудие пламени", description = "[q]При получении урона от огня цель оглушается. Число зарядов: 1. Время действия – 8 sec."},
[33735] = {name = "Шквал клинков", description = "[q]Повышает скорость атаки на 20%, при этом также наносится удар ближайшему противнику. Время действия – 15 sec."},
[33768] = {name = "Проходы Муркет и Шаадраз", description = nil},
[33781] = {name = "Накинуться", description = "[q]Наносит противнику обычный урон и еще 3 ед. и оглушает его на 2 sec."},
