[38959] = {name = "Казнь", description = "[q]Попытаться прикончить раненого противника, нанеся ему обычный урон, усиленный на 200 ед. Этот навык можно применять только против врагов, у которых осталось 20% здоровья и меньше."},
[38971] = {name = "Кислотный гейзер", description = "[q]Наносит 1425 to 1575 ед. урона от сил природы цели и всем находящимся поблизости противникам раз в 1 сек. в течение 8 sec."},
[38976] = {name = "Споровое землетрясение", description = "[q]Производит землетрясение, которое наносит урон от сил природы находящимся рядом противникам."},
