[39429] = {name = "Огненный столб Скверны", description = "[q]Призывает столб пламени, обжигающий всех противников на выбранном участке и наносящий дополнительный урон от огня каждые 2 сек. в течение 8 sec."},
[39449] = {name = "Наскок", description = "[q]Набрасывается на цель, причиняя урон."},
[39456] = {name = "Репутация: +500 (Нижний город)", description = nil},
[39457] = {name = "Репутация: +500 (Ша'тар)", description = nil},
[39460] = {name = "Репутация: +500 (Кенарийская экспедиция)", description = nil},
[39474] = {name = "Репутация: +500 (Консорциум)", description = nil},
[39475] = {name = "Репутация: +500 (Хранители времени)", description = nil},
[39476] = {name = "Репутация: +500 (Спореггар)", description = nil},
[39477] = {name = "На неудачу", description = "[q]Накладывает тяжкое проклятие на агрессора."},
[39489] = {name = "Зачарованное оружие", description = "[q]Обезоруживание противника, при котором он не может атаковать оружием в течение 30 sec."},
[39529] = {name = "Огненный шок", description = "[q]Поджигает противника, а затем наносит дополнительный урон от огня раз в 3 сек. в течение 12 sec."},
[39544] = {name = "Игнорируется", description = "[q]Игнорирование противника, заставляющее заклинателя не атаковать его, за исключением тех случаев, когда поблизости нет другой цели. После того как эффект исчезает, существо будет атаковать цель, представляющую наибольшую угрозу."},
[39574] = {name = "Рывок", description = "[q]Рывок к противнику, наносящий обычный урон, увеличенный на 400 to 600 ед., и оглушающий цель на 2 sec."},
[39575] = {name = "Яростный бросок", description = "[q]Заклинатель впадает в исступление и начинает случайным образом менять цели."},
[39578] = {name = "Узы духа", description = "[q]Создание связи между хозяином и питомцем, которая позволяет восполнять им обоим 5% общего запаса здоровья в секунду в течение 20 сек."},
[39580] = {name = "Грозовая туча", description = "[q]Грозовое облако, которое держится 15 sec., поражая всех противников в избранной игровой зоне с нанесением 1600 to 2400 ед. урона от сил природы и нанося 1760 to 2640 ед. дополнительного урона раз в 3 сек."},
[39581] = {name = "Скачок бури", description = "[q]Заклинатель превращается в бушующий вихрь, который наносит урон ближайшим противникам. Длительность – 10 sec."},
[39584] = {name = "Зажим сметающего крыла", description = "[q]Обездвиживает всех противников в радиусе 5 м."},
[39586] = {name = "Призыв тотема неистовства ветра", description = "[q]Заклинатель создает рядом с собой тотем неистовства ветра прочностью 1 ед., который зачаровывает ветром оружие, находящееся в правой руке у всех участников группы в радиусе 30 м. Каждый удар имеет 15% вероятности дать атакующему дополнительно 2 атаки:атак с силой атаки 366 ед. Время действия – 1.50 min."},
[39588] = {name = "Опаляющий тотем", description = "[q]Создает рядом с собой тотем с 1 ед. прочности и временем существования 30 sec., который периодически атакует 3 противников в радиусе 40 м, нанося 1980 to 2420 ед. урона от огня."},
[39589] = {name = "Тотем смерча", description = "[q]Создает тотем смерча."},
[39591] = {name = "Опаляющий тотем", description = "[q]Создает рядом с заклинателем опаляющий тотем прочностью, который регулярно атакует противника в радиусе 20 м, нанося урон от огня. Время существования тотема – 1 min."},
[39592] = {name = "Иссушение", description = "[q]Объект получает урон от огня."},
[39593] = {name = "Иссушение", description = "[q]Объект получает урон от огня в размере 1980 to 2420 ед."},
[39621] = {name = "Порча", description = "[q]Нанесение находящимся рядом противникам урона от темной магии раз в 3 сек. в течение 15 sec."},
[39635] = {name = "Бросок боевого клинка", description = "[q]Бросает клинки Аззинота."},
[39645] = {name = "Инфернал Тьмы", description = "[q]Окружает себя аурой, которая периодически наносит урон от огня находящимся рядом противникам в течение 8 sec. Не позволяет двигаться и применять способности."},
[39647] = {name = "Проклятие восстановления", description = "[q]Проклинает противника на 1.50 min., оставляя ему 20% шанс того, что он вылечит свою цель на 5% при атаке заклинанием или оружием ближнего боя."},
[39649] = {name = "Вызов исчадия Тьмы", description = "[q]Призывает исчадие Тьмы, атакующее цель. Когда исчадие наносит урон, призыватель восполняет ману. Время существования – until cancelled."},
[39665] = {name = "Нейтрализующий яд", description = "[q]Отравление противника. Все эффекты лечения, применяемые к нему, становятся на 20% менее эффективными в течение 15 sec."},
[39666] = {name = "Плащ теней", description = "[q]Снимает эффекты вредоносных заклинаний и повышает вероятность сопротивления заклинаниям на 90% на 5 sec. Не снимает эффекты, не позволяющие использовать \"Плащ теней\"."},
[39667] = {name = "Исчезновение", description = "[q]Исчезновение из виду на 3 sec."},
[39670] = {name = "Жертвенный огонь Скверны", description = "[q]Заклинание обжигает противника и наносит дополнительный урон от огня раз в 3 сек. в течение 15 sec."},
[39671] = {name = "Ливень Хаоса", description = "[q]Вызов лавового дождя, наносящего всем противникам на выбранном участке 138 to 161 ед. урона от огня раз в 3 сек. в течение 6 sec."},
[39672] = {name = "Проклятие агонии", description = "[q]Наносит урон от темной магии раз в 3 сек. в течение 30 sec. Одновременно на цель может действовать только одно проклятие каждого чернокнижника."},
[39674] = {name = "Изгнание", description = "[q]Изгнание противника, запрещающее ему совершать какие-либо действия, но делающее неуязвимым в течение 15 sec. Изгнать можно только одну цель за раз."},
[39699] = {name = "Регулятор хода битвы", description = nil},
[39700] = {name = "Телепорт", description = "[q]Телепортирует заклинателя к местонахождению цели."},
[39782] = {name = "Вызов светоносного элекка", description = "[q]Вызов 0 элекков для сопровождения заклинателя, пока он не разрешит удалиться."},
[39783] = {name = "Призыв крылобега Провидцев", description = "[q]Вызывает 0 крылобегов, помогающих заклинателю до тех пор, пока он не откажется от сопровождения."},
[39794] = {name = "Искупление", description = "[q]Возвращает погибшего игрока к жизни с 1 ед. здоровья и 0 ед. маны. Не действует в бою."},
[39796] = {name = "Оглушение каменного когтя", description = "[q]Оглушает нападающего на 3 sec."},
[39828] = {name = "Свет наару", description = nil},
[39837] = {name = "Пронзающий шип", description = "[q]Огромный шип, пронзающий цель."},
[39843] = {name = "Вызов яйца гигантской калири", description = nil},
[39855] = {name = "Вызов Слезы Аззинота", description = "[q]Призывает слезу Аззинота."},
[39869] = {name = "Необузданный гнев", description = "[q]Отрезанные от клинка духи Аззинота приходят в ярость, нанося 500% дополнительного урона и повышая скорость передвижения на 200%."},
[39872] = {name = "Щит Прилива", description = "[q]Защищает заклинателя от большинства атак и заклинаний, а также восстанавливает здоровья заклинателя."},
[39878] = {name = "Грохот прилива", description = "[q]Нанесение противникам значительного урона."},
[39906] = {name = "Вызов ездового бойцового пса", description = "[q]Вызов 0 волков:волков для сопровождения заклинателя, пока он не разрешит удалиться."},
[39941] = {name = "Преисподняя", description = "[q]Призывает метеор из Круговерти Пустоты, который наносит 34 ед. урона от огня и оглушает противников в области действия на 2 sec. Из кратера появляется инфернал, который в течение 1 min. подчиняется призывателю. Для сохранения контроля после истечения времени инфернала нужно поработить. Действует только вне помещений."},
[39942] = {name = "Темная стая", description = "[q]Обрушивает волну хаотической магии на противников перед собой, нанося 800 to 1200 ед. урона от темной магии."},
[39945] = {name = "Цепная молния", description = "[q]Поражает противника молнией, которая переходит на другого находящегося рядом противника. Поражает до 5 целей, нанося каждой из них возрастающий урон от сил природы."},
[39954] = {name = "Веерный бросок", description = "[q]Создает вокруг заклинателя щит из лезвий, наносящий урон противникам."},
[40032] = {name = "Огненное дыхание", description = nil},
[40062] = {name = "Бросок динамита", description = "[q]Наносит 510 to 690 ед. урона от огня всем противникам в пределах указанной области."},
[40066] = {name = "Стрекало молнии", description = "[q]Поражает противника молнией, которая переходит на других находящихся рядом противников. Наносит 2775 to 3225 ед. урона от сил природы каждой цели."},
[40075] = {name = "Разрывной огонь Скверны", description = "[q]Цели наносится 5% урона раз в 3 сек."},
[40076] = {name = "Электрошпора", description = "[q]Приводит в бешенство находящегося рядом левиафана, повышая его скорость атаки."},
[40077] = {name = "Взмах хвостом", description = "[q]Наносит 900 to 1500 ед. урона противникам в секторе за спиной заклинателя и отбрасывает их назад."},
[40078] = {name = "Ядовитый плевок", description = "[q]Наносит урон от сил природы, а затем дополнительный урон раз в 5 сек. в течение 10 sec."},
[40079] = {name = "Изнуряющий брызги", description = "[q]Разбрызгивание в противника яда, снижающего наносимый им урон и эффект исцеляющих заклинаний на 50% на 10 sec."},
[40080] = {name = "Луженая глотка", description = "[q]Повышает скорость атаки в ближнем бою и уменьшает время произнесения заклинаний ближайшими союзниками. Время действия – 30 sec."},
[40081] = {name = "Освободить друга", description = "[q]Освобождение находящегося поблизости союзника от парализующих эффектов воздействия противника."},
[40082] = {name = "Сеть с крючьями", description = "[q]Обездвиживает находящихся рядом противников на 6 sec. и наносит им физический урон."},
[40083] = {name = "Бросок копья", description = "[q]Бросает оружие в противника, нанося физический урон."},
[40084] = {name = "Знак гарпунера", description = "[q]Цель, помеченная знаком гарпунера, провоцирует атаку находящихся рядом драконьих черепах."},
[40086] = {name = "Водяной плевок", description = "[q]Выпускает в противника водяную стрелу, нанося 1530 to 1870 ед. урона от магии льда."},
[40087] = {name = "Панцирный щит", description = "[q]Отражает 50% полученного урона от магии. Скорость атаки в ближнем бою снижается на 25%, время произнесения заклинаний увеличивается на 25%. Время действия – 12 sec."},
[40088] = {name = "Раздвоенная молния", description = "[q]Нанесение урона от сил природы противникам, находящимся в конусе перед заклинателем."},
[40089] = {name = "Гейзер", description = "[q]Поражает группу противников, нанося 1850 to 2150 ед. урона от магии льда и отбрасывая их назад."},
[40090] = {name = "Ураган", description = "[q]Создание в указанной области неистового шторма, в течение 20 sec. наносящего противникам 1665 to 1935 ед. урона от сил природы раз в 3 сек. и увеличивающего время между их атаками на 67%. Эффект заклинания необходимо поддерживать."},
[40091] = {name = "Создать гейзер", description = "[q]Призывает гейзер, отбрасывающий находящихся поблизости противников."},
[40095] = {name = "Ядовитый град", description = "[q]Наносит урон от сил природы, а затем дополнительный урон раз в 2 сек. в течение 6 sec."},
[40096] = {name = "Кольцо Света", description = "[q]Взрыв божественного света, наносящего 1750 to 2250 ед. урона от светлой магии ближайшим противникам и снижающим уровень создаваемой заклинателем угрозы на until cancelled."},
[40097] = {name = "Восстановление", description = "[q]Исцеление союзника раз в 3 сек. в течение 15 sec."},
[40099] = {name = "Злобная слизь", description = "[q]Снижает эффективность лечения и наносимый противником урон. Наносит урон от сил природы в течение 24 сек."},
[40100] = {name = "Сокрушительная волна", description = "[q]Обрушивает поток отравленной воды на противников в 65-метровом секторе перед собой, нанося 2313 to 2687 ед. урона от сил природы."},
[40102] = {name = "Выброс ила", description = "[q]Яд, который поражает группу противников, нанося урон от сил природы. Затем наносится дополнительный урон раз в 3 с в течение 10 sec."},
[40105] = {name = "Насыщение", description = "[q]Восстанавливает 20% от максимального запаса здоровья и маны в течение 10 sec."},
[40106] = {name = "Размытие", description = "[q]Заставляет заклинателя слиться с создателем, заряжая его энергией."},
[40124] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[40134] = {name = "Призыв волшебного элементаля", description = nil},
[40157] = {name = "Духовное копье", description = "[q]В одиночную цель летит сгусток духовной энергии, нанося 6175 to 6825 ед. урона и задерживая ее. Цели, пораженные заклинанием \"Духовное копье\", постепенно восстанавливают подвижность по мере ослабления эффекта. Может использоваться только против порождений Тьмы."},
[40175] = {name = "Призрачные оковы", description = "[q]Опутывает находящихся рядом противников цепями духовной энергии, не позволяя им действовать в течение 5 sec. Урон снимает эффект. Действует только на порождений Тьмы."},
[40184] = {name = "Парализующий визг", description = "[q]Оглушение всех находящихся рядом противников в течение 6 sec."},
[40185] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[40199] = {name = "Раздирание плоти", description = "[q]Нанесение противнику физического урона раз в 3 сек. в течение 15 sec."},
[40237] = {name = "Защита ястреба", description = nil},
[40239] = {name = "Испепеление", description = "[q]Нанесение 2775 to 3225 ед. прямого урона от огня и еще 8325 to 9675 ед. урона в течение 3 sec."},
[40240] = {name = "Злоба Орла", description = nil},
[40241] = {name = "Скорость сокола", description = nil},
[40243] = {name = "Сокрушающие тени", description = "[q]Увеличивает урон от темной магии, получаемый противниками, на 60% на 15 sec."},
[40251] = {name = "Тень смерти", description = "[q]Вызов тени смерти, убивающей цель через 55 sec."},
[40276] = {name = "Извержение вулкана", description = "[q]Вызов извержения вулкана для нанесения урона находящимся рядом противникам."},
[40291] = {name = "Вытягивание сущности", description = "[q]Наполняет вас энергией Скверны, позволяя похищать здоровье у противников при каждой атаке в ближнем бою."},
[40303] = {name = "Колдовская бомба", description = "[q]Наложение на цель проклятья, приводящее к потере маны и получению целью урона каждый раз, когда она применяет заклинания."},
[40307] = {name = "Стазисное поле", description = "[q]Помещение цели в Поле стазиса, в результате чего цель получает оглушение."},
[40314] = {name = "Призрачный залп", description = "[q]Заклинание выпускает залп концентрированной духовной энергии, нанося всем целям в радиусе 12 м 9900 to 12100 ед. урона. Может использоваться только против порождений Тьмы."},
[40318] = {name = "Рост", description = "[q]Увеличение урона на 15%."},
[40321] = {name = "Вихрь перьев", description = nil},
[40322] = {name = "Духовный щит", description = "[q]Дает союзнику духовный барьер, поглощающий 11400 to 12600 ед. урона от темной магии. Время действия – 30 sec."},
[40325] = {name = "Призрачный удар", description = "[q]Могучий удар, ослабляющий цель и снижающий наносимый ей урон на 10% на 5 sec."},
[40327] = {name = "Атрофия", description = "[q]Ослабляет цель и наносит ей урон, снижает скорость атаки в ближнем, дальнем бою и скорость применения заклинаний на 5%."},
[40331] = {name = "Бомбардировка", description = "[q]Бомбардировка всех противников на выбранном участке, наносящая урон от огня."},
[40340] = {name = "Тяжелый шаг", description = "[q]Нанесение физического урона ближайшим противникам."},
[40350] = {name = "Скверная чума", description = "[q]Вероятность заразить атакующего при нанесении удара."},
[40392] = {name = "Реванш", description = "[q]Мгновенно контратакует, нанося противнику 638 to 862 ед. урона. \"Реванш\" должен следовать за блоком, уклонением или парированием."},
[40414] = {name = "Сосредоточение внимания", description = "[q]Заклинание заставляет противника сосредоточить внимание на заклинателе на 15 sec. Пока цель сосредоточена на заклинателе, она не будет атаковать кого-либо еще."},
[40416] = {name = "Одержимая ярость", description = "[q]Заклинание заставляет противника сосредоточить внимание на заклинателе на 15 sec. Пока цель сосредоточена на заклинателе, она не будет атаковать кого-либо еще."},
[40417] = {name = "Гнев", description = "[q]Увеличение наносимого заклинателем физического урона на 150%, но снижение скорости его передвижения на 75%. Время действия – 10 sec."},
