[48872] = {name = "Залп", description = "[q]Оружейный залп, наносящий 3770 to 4230 ед. урона 3 целям:целям."},
[48873] = {name = "Калечащая рана", description = "[q]Ранит противника, нанося 125% урона от оружия и увеличивая урон от эффектов кровотечения на 75%."},
[48876] = {name = "Метка животного", description = "[q]Наложение метки животного на цель. Цель получает 1885 to 2115 ед. урона при нападении животного."},
[48878] = {name = "Проникающая рана", description = "[q]Ранит противника, нанося 125% урона от оружия и уменьшая его броню на 75%."},
[48880] = {name = "Кровопускание", description = "[q]Нанесение противнику физического урона раз в 3 сек. в течение 10 sec."},
[48894] = {name = "Цепное исцеление", description = "[q]Наполняет раненого союзника целительной энергией, которая затем переходит на другого союзника, находящегося поблизости. Заклинание может исцелить до 3 целей, но с каждой новой целью энергия слабеет на 50%."},
[48920] = {name = "Болезненный укус", description = "[q]Жестокий удар, вызывающий у цели обширное кровотечение вплоть до полного исцеления."},
[48972] = {name = "Пепельная стрела", description = "[q]1200 to 2800 ед. урона от огня, наносится раз в 5 сек."},
[48984] = {name = "Бросок лезвия пилы", description = "[q]Бросает в противника лезвие пилы, которое также поражает двух других ближайших к нему противников."},
[49025] = {name = "Самоуничтожение", description = "[q]Уничтожает крошшера, нанося урон ближайшим противникам."},
[49026] = {name = "Сосредоточение внимания", description = "[q]Заклинание заставляет противника сосредоточить внимание на заклинателе на 10 sec. Пока цель сосредоточена на заклинателе, она не будет атаковать кого-либо еще."},
[49034] = {name = "Снежная буря", description = "[q]Начинается снежная буря, длящаяся 6 sec. и наносящая 1665 to 1935 ед. урона от магии льда раз в 2 сек. всем противникам в зоне поражения."},
[49037] = {name = "Ледяная стрела", description = "[q]Заклинание наносит противнику урон от магии льда и замедляет скорость его передвижения на 4 sec."},
[49063] = {name = "Воспламенение", description = "[q]Обжигает находящихся поблизости противников и наносит дополнительный урон от огня раз в 1 сек. в течение 21 sec."},
[49081] = {name = "Высадка солдата", description = "[q]Высадка гвардейца 7-го легиона на усыпальницу Стражей Зимы."},
[49084] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
