[49170] = {name = "Ликантропия", description = "[q]Заражает заклинателя ликантропией, заставляя его превратиться в дух волка через 5 sec. В облике духа волка физический урон увеличивается на 2000, скорость атаки на 100%, а заклинатель не может быть замедлен или остановлен. В этом облике невозможно произносить заклинания. Время действия – 15 sec."},
[49172] = {name = "Дух волка", description = "[q]Заражает заклинателя ликантропией, заставляя его превратиться в дух волка через 5 sec. В облике духа волка физический урон увеличивается на 2000, скорость атаки на 100%, а заклинатель не может быть замедлен или остановлен. В этом облике невозможно произносить заклинания. Время действия – 15 sec."},
[49190] = {name = "Скорострельная пушка 7-го легиона", description = "[q]Благодаря малым затратам горючего это орудие дальнего и среднего боя приобрело стало абсолютно незаменимым в бою. Цельтесь точнее, все остальное эта пушка сделает сама!"},
[49198] = {name = "Чародейская вспышка", description = "[q]Поражение противника чародейской вспышкой, наносящей увеличенный на 1749 to 1951 ед. обычный урон и отбрасывающей его назад."},
