[49560] = {name = "Хватка смерти", description = "[q]Используя нечестивую энергию, пронизывающую и объединяющую все сущее, притягивает противника к рыцарю смерти, заставляя его атаковать рыцаря в течение 3 sec."},
[49576] = {name = "Хватка смерти", description = "[q]Используя нечестивую энергию, пронизывающую и объединяющую все сущее, притягивает противника к рыцарю смерти, заставляя его атаковать рыцаря в течение 3 sec."},
[49592] = {name = "Временная аномалия", description = "[q]Насылает на вражеского дракона временную аномалию, которая длится 10 sec. Оказавшись в пределах действия аномалии, цель получает на 100% больше повреждений. К тому же, при нанесении попавшему в аномалию противнику 15000 ед. урона генерируется 1 шоковый разряд."},
[49616] = {name = "Удар по почкам", description = "[q]Оглушает противника на 4 sec."},
[49637] = {name = "Зараженная рана", description = "[q]Повышение физического урона, получаемого противником, на 15%. Время действия – 10 sec."},
