[49717] = {name = "Рунический кулак", description = "[q]Кулаки наливаются силой темной руны."},
[49718] = {name = "Темная руна", description = "[q]Наносит противнику 300 ед. урона от темной магии раз в 2 сек. в течение 8 sec."},
