[49841] = {name = "Тревожный удар", description = "[q]Поражает разум, нанося 315 to 385 ед. урона от темной магии. Удар также отупляет нервную систему, снижая полученный урон на 5% в течение 30 sec."},
[49843] = {name = "Пробуждение крови предков", description = "[q]Похищение 225 to 275 ед. здоровья у противника и передача его заклинателю."},
[49861] = {name = "Заразный укус", description = "[q]Наносит противнику урон от сил природы раз в 2 сек. и увеличивает получаемый им физический урон на 10% на 8 sec."},
[49863] = {name = "Мощный удар щитом", description = "[q]Нанесение противнику физического урона."},
[49865] = {name = "Выклевать глаз", description = "[q]Снижает вероятность попадания на 20% на 5 sec."},
[49871] = {name = "Руна возмездия", description = "[q]Увеличение наносимого урона на 5% при получении урона. Этот эффект может срабатывать не чаще чем раз в 2 сек. и суммироваться до 5 раз."},
[49872] = {name = "Выстрел ракетой", description = "[q]Выстрел сверхмощной ракетой на большое расстояние."},
[49898] = {name = "Воздаяние", description = nil},
[49906] = {name = "Ледяное копье", description = "[q]Наносит урон от магии льда. Замороженным целям наносится тройной урон."},
[49922] = {name = "Девятый вал", description = "[q]Наносит 875 to 1125 ед. урона от магии льда и отбрасывает цель назад."},
[49935] = {name = "Сглаз мурлока", description = "[q]Превращение противника в мурлока, препятствующее его атакам или применению заклинаний на 3 sec."},
[49945] = {name = "Лазерный резак", description = "[q]Наносит 300 ед. урона от огня противнику каждую секунду в течение 3 sec."},
[49948] = {name = "Ненадежный взрыв", description = nil},
[49956] = {name = "Жгучая рана", description = "[q]Атака трезубца наносит 618 to 682 ед. урона от огня и увеличивает получаемый урон от огня на 10% на 8 sec. Суммируется 5 раз."},
[49961] = {name = "Мгновенный удар", description = "[q]Мгновенный скачок к противнику с нанесением 315 to 385 ед. урона от темной магии."},
[49966] = {name = "Хлопок", description = "[q]Наносит противнику [ 187 + ((Ranged attack power * 0.40) * 0.20)] ед. урона."},
[49978] = {name = "Хваткие когти", description = "[q]Обездвиживание противника и нанесение ему физического урона раз в 1 сек. в течение 4 sec."},
[49980] = {name = "Прожорливый микроорганизм", description = "[q]Заражение прожорливыми микроорганизмами, увеличивающими получаемый урон на 5% на 30 sec.\n Нанесение 540 to 660 ед. урона от сил природы."},
[49981] = {name = "Пулемет", description = "[q]Стреляет из пулемета, нанося 100 ед. урона раз в 0.3 сек. в течение 5 sec.\n Снижает вероятность попадания во время стрельбы на 50%."},
[50024] = {name = "Парение", description = "[q]Крылья вашего дракона расправляются, увеличивая скорость передвижения во время полета на 12000% и увеличивая восполнение здоровья. Эта способность действует только вне боя."},
[50025] = {name = "Зачумленный бочонок", description = "[q]Выстрел зачумленным бочонком на большое расстояние."},
[50027] = {name = "Рывок души", description = "[q]Передача 2 ед. здоровья от цели к заклинателю раз в 3 сек. Время действия – 12 sec."},
[50028] = {name = "Полет на грифоне до Крепости Западной Стражи", description = nil},
[50045] = {name = "Могучий размах", description = "[q]Каждая удачная атака увеличивает наносимый физический урон на 400. Суммируется до 25 раз."},
[50046] = {name = "Обгладывание костей", description = "[q]Наносит 765 to 1035 ед. физического урона и снижает вероятность попадания на 10% на 8 sec."},
[50047] = {name = "Ломкие кости", description = "[q]Снижает вероятность попадания на 10% на 8 sec."},
[50053] = {name = "Центрифужный щит", description = "[q]Защита заклинателя от урона. Этот щит питают Центрифужные создания через Окулус."},
[50054] = {name = "Быстрые ноги", description = "[q]Увеличение вероятности уклонения и парирования на 10% на 5 sec."},
[50066] = {name = "Тыквенный выстрел", description = nil},
[50084] = {name = "Могучий апперкот", description = "[q]Массивный апперкот, наносящий 660 to 940 ед. физического урона.\n Лишение противника сил."},
[50086] = {name = "Валун", description = "[q]Валун, наносящий физический урон противнику."},
[50089] = {name = "Гнев страдания", description = "[q]Наносит урон от темной магии раз в 2 сек. на протяжении 8 sec."},
[50092] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[50094] = {name = "Ледяное острие", description = "[q]Ледяное копье, наносящее 417 to 483 ед. урона от магии льда и подбрасывающее противника в воздух."},
[50100] = {name = "Грозовой удар", description = "[q]Нанесение 200 ед. урона противнику, оглушение на 3 sec."},
[50105] = {name = "Неистовый ураган", description = "[q]Создание в указанной области неистового шторма, в течение 20 sec. наносящего противникам 1665 to 1935 ед. урона от сил природы раз в 3 сек. и увеличивающего время между их атаками на 67%. Эффект заклинания необходимо поддерживать."},
[50106] = {name = "Болезнетворное облако", description = "[q]Гнилостное облако заразы окружает заклинателя, нанося урон от сил природы находящимся поблизости противникам."},
[50131] = {name = "Похищение магии", description = "[q]Забирает магию у находящихся поблизости противников, увеличивая наносимый заклинателем урон на 50% и в то же время уменьшая наносимый противниками урон и снижая эффективность применяемого ими лечения на 225."},
[50155] = {name = "Взмах хвостом", description = "[q]Наносит 600 to 1000 ед. урона противникам в секторе за спиной заклинателя и отбрасывает их назад."},
[50161] = {name = "Сфера защиты", description = "[q]Заклинателя окружает защитная сфера, укрывающая его от всех видов урона все время своего существования."},
[50163] = {name = "Мясник", description = "[q]Каждый раз, когда вы убиваете цель, приносящую опыт или честь, вы накапливаете силу рун. Кроме того, каждую секунду накапливается дополнительная сила рун."},
[50169] = {name = "Удар наотмашь", description = "[q]Сильная атака ближнего боя, наносящая дополнительно 120 ед. урона."},
[50182] = {name = "Аура чародейского ускорения", description = "[q]Повышает вашу скорость произнесения заклинаний на 15%. На 1 min. увеличивает урон от заклинаний на 150 ед."},
[50188] = {name = "Бешеный цеп", description = "[q]Нанесение ударов во все стороны. Вероятность попадания снижена на 50%."},
[50195] = {name = "Левитация", description = "[q]Заклинатель приобретает способность парить в нескольких метрах от земли. При левитации замедляется падение и приобретается возможность путешествовать над водообразными поверхностями."},
[50196] = {name = "Прикосновение гнили", description = "[q]Заражает противника гнилью при прикосновении, нанося 88 to 112 ед. урона раз в 1 сек. в течение 10 sec."},
[50198] = {name = "Магический ожог", description = "[q]Обжигает противника, нанося 950 to 1050 ед. урона от тайной магии мгновенно и 285 to 315 ед. урона от тайной магии раз в 3 сек. в течение 21 sec."},
[50204] = {name = "Стальная челюсть", description = "[q]Усиление брони на 1500% на 10 sec."},
[50206] = {name = "Обжигающий пар", description = "[q]Наносит 210 to 270 ед. урона от огня и заставляет плоть вашего противника гореть, что наносит ему 120 ед. урона от огня раз в 3 секунды в течение 12 sec."},
[50207] = {name = "Кипящая кровь", description = "[q]Наносит 219 to 281 ед. урона от огня.\n Увеличивает получаемый целью урон на 20% и наносимый урон на 10%."},
[50215] = {name = "Зефир", description = "[q]Вас овевает зефир, струящийся из Крепости Бурь.\n Скорость передвижения повышена на 50%."},
[50228] = {name = "Вихрь", description = "[q]Наносит 4713 to 5287 ед. урона в сек. находящимся рядом противникам, но уменьшает скорость передвижения на 50%."},
[50232] = {name = "Жгучий гнев", description = "[q]Выдыхание во вражеского дракона струи огня с нанесением 6800 to 9200 ед. урона от огня, затем огонь перескакивает на других драконов в радиусе 30 м. Каждый прыжок увеличивает урон на 50%. Поражает всего до 5 целей."},
[50234] = {name = "Сокрушение", description = "[q]Наносит цели 150% урона в ближнем бою, сбивая ее с ног на 2 sec."},
[50240] = {name = "Маневр уклонения", description = "[q]Позволяет вашему дракону уклоняться от всех нацеленных на него атак и заклинаний. Требуются обходные маневры. Каждое уклонение сжигает один заряд обходных маневров. Время действия – 30 sec. или пока не кончатся заряды."},
[50241] = {name = "Обходной маневр", description = "[q]Обходной маневр появляется, когда рубиновый дракон подвергается атаке или воздействию вражеского заклинания."},
[50245] = {name = "Шип", description = "[q]Приковывает цель к месту на 4 sec."},
[50253] = {name = "Мученичество", description = "[q]Перенаправление вредоносных заклинаний, направленных на дружественных драконов, на себя в течение 10 sec."},
[50255] = {name = "Отравленный дротик", description = "[q]Нанесение 4713 to 5287 физического урона дополнительно 1885 to 2115 ед. урона от сил природы раз в 3 сек. в течение 12 sec."},
[50256] = {name = "Устрашающий рев", description = "[q]Медведь рычит, уменьшая физический урон, наносимый всеми противниками в радиусе 8 м на 10 % на 15 sec."},
[50257] = {name = "Жгучий удар", description = "[q]Ошпаривание цели кипящей водой с нанесением 744 to 956 ед. урона от огня."},
[50262] = {name = "На штурм!", description = "[q]Повышает скорость передвижения на 60% в течение 10 sec. и наносит дополнительно 400 ед. урона при первых трех атаках."},
[50267] = {name = "Деморализующий мммммррргллл!", description = "[q]Снижает силу атаки ближнего боя окружающих противников на 250 ед. на 10 sec.\n Снижает наносимый урона от магии на 146 ед. на 10 sec."},
[50269] = {name = "Сброс масла", description = "[q]Увеличение вероятности промаха цели на 10% и замедление скорости ее передвижения на 10%."},
[50271] = {name = "Повреждение сухожилий", description = "[q]Удар по ногам, увеличивающий урон, получаемый целью от эффектов кровотечения, на 30 % на 15 sec."},
[50272] = {name = "Нестабильная магия", description = "[q]Повышение всего урона, причиненного находящимися поблизости неприятелями до 150 ед.\n Повышение всего урона от заклинаний, полученных неприятелем до 300 ед. Время действия – 2 min."},
[50273] = {name = "Чародейский обстрел", description = "[q]Запускает в противника несколько волшебных стрел, наносящих урон от тайной магии."},
[50274] = {name = "Споровое облако", description = "[q]Поражает противников в радиусе 6 м спорами, снижая их скорость произнесения заклинаний на 30 % на 10 sec."},
[50275] = {name = "Устойчивая магия", description = "[q]Увеличение урона от всех заклинаний находящихся поблизости противников максимум на 150 ед."},
[50276] = {name = "Бросок кружки", description = "[q]Нанесение противнику физического урона."},
[50280] = {name = "Масляное покрытие", description = "[q]Существо покрывается слоем масла. Вероятность того, что атаки и заклинания промажут по нему, повышается на 20%."},
[50283] = {name = "Слепящий рой", description = "[q]Рой насекомых, наносящий цели 263 to 337 ед. урона от сил природы.\n Вероятность того, что противник, окруженный роем, попадет по цели, снижается на 75%."},
[50285] = {name = "Облако пыли", description = "[q]Долгоног поднимает вокруг себя облако пыли. Скорость атаки в ближнем и дальнем бою всех противников в радиусе 10 м понижается на 20% на 30 sec."},
[50293] = {name = "Разъедающий яд", description = "[q]Ослабление брони противника на 900 ед. и нанесение ему 100 ед. урона от сил природы раз в 3 сек. в течение 12 sec."},
[50302] = {name = "Вихрь кристаллов", description = "[q]Заклинатель швыряет осколки кристаллов в противников в конусе перед собой, нанося урон от тайной магии."},
[50303] = {name = "Свиное бешенство", description = "[q]Заражение свиным бешенством. Вероятность при атаке спровоцировать заболевание."},
[50306] = {name = "Жесткий пинок", description = "[q]Шквал пинков, наносящий физический урон с каждым пинком."},
[50312] = {name = "Нечестивое бешенство", description = "[q]Вводит прислужника в нечестивую ярость."},
[50313] = {name = "Появление буровой установки", description = nil},
[50318] = {name = "Облачко безмятежности", description = "[q]Мотылек создает облако пыльцы, прерывая произнесение вражеских заклинаний и предотвращая произнесение заклинаний той же школы в течение 4 sec."},
[50319] = {name = "Свежевание души", description = nil},
[50323] = {name = "Заостренная кость", description = "[q]Заостренная кость, наносящая 438 to 562 ед. физического урона."},
[50324] = {name = "Костяной доспех", description = "[q]Заключение заклинателя в костяную броню, поглощающую 1203 to 1397 ед. урона в течение 1 min."},
[50328] = {name = "Отбирающий яд", description = "[q]Отравление вражеского дракона, похищение у него 1300 ед. здоровья в пользу заклинателя раз в 2 сек. в течение 12 sec. Эффект суммируется до 3 раз."},
[50332] = {name = "Погибель дураков", description = "[q]Погибель злится. Наносимый им физический урон повышается на 350, а получаемый урон снижается на 25% на 10 sec."},
[50335] = {name = "Крюк Плети", description = "[q]Наносит физический урон и притягивает цель к заклинателю."},
[50341] = {name = "Прикосновение кошмара", description = "[q]Поглощает 20000 здоровья изумрудного дракона, причиняя 25000 ед. урона от сил природы враждебному дракону и снижая наносимый им урон на 25% в течение 30 sec."},
[50344] = {name = "Канал сна", description = "[q]Передает 5% максимальной величины здоровья заклинателя дружественному дракону каждую секунду на 10 sec., пока заклинатель поддерживает канал."},
[50347] = {name = "Суета", description = "[q]Увеличивает урон от следующей атаки заклинателя в ближнем бою на 200%."},
[50348] = {name = "Ярость пламени", description = "[q]Наносит 4375 to 5625 ед. урона от огня противникам в конусе поражения перед заклинателем раз в секунду."},
[50349] = {name = "Ледяное прикосновение", description = "[q]Наносит 662 to 738 ед. урона от магии льда и уменьшает скорость атаки ближнего боя, дальнего боя и произнесения заклинаний на 15% на 8 sec."},
[50356] = {name = "Прививка чумы", description = "[q]Нанесение противнику 324 to 376 ед. урона от темной магии раз в 2 сек. в течение 8 sec."},
[50361] = {name = "Гниение брони", description = "[q]Ослабление брони противника на 25% на 10 sec."},
[50363] = {name = "Каменный щит", description = nil},
[50364] = {name = "Каменный щит", description = nil},
[50366] = {name = "Чумное облако", description = "[q]Гнилостное облако чумы окружает заклинателя, нанося урон от темной магии ближайшим противникам."},
[50370] = {name = "Раскол брони", description = "[q]Ослабляет вражескую броню при каждом расколе. Суммируется до 5 раз. Время действия – 15 sec."},
[50375] = {name = "Струя пара", description = "[q]Наносит 875 to 1125 ед. урона от огня и отбрасывает противника назад."},
[50378] = {name = "Ледяная стрела", description = "[q]Заклинание наносит противнику урон от магии льда и замедляет скорость его передвижения на 4 sec."},
[50379] = {name = "Увечье", description = "[q]Увеличение интервала между вражескими атаками на 100%, снижение скорости на 50% и силы на 50%. Время действия – 10 sec."},
[50380] = {name = "Дымка кровавых спор", description = "[q]Распыление кровавых спор вызывает состояние эйфории, которое повышает скорость на 5% на 1 min.\n Наносит 324 to 376 ед. урона от сил природы.\n Суммируется до 5 раз."},
[50402] = {name = "Поджаривание", description = "[q]Заклинание обжигает противника и наносит дополнительный урон от огня раз в 3 сек. в течение 12 sec."},
[50403] = {name = "Бросок кости", description = "[q]Бросает в противника кость, нанося 525 to 675 ед. урона."},
[50405] = {name = "Кованый гром", description = "[q]Наносит противнику 2625 to 3375 ед. урона от сил природы."},
[50410] = {name = "Удар клыками", description = "[q]Наносит противнику обычный урон, усиленный на 150 ед., и отбрасывает его назад."},
[50411] = {name = "Головокружение", description = nil},
[50413] = {name = "Рывок магнатавра", description = "[q]Повышает скорость передвижения на 60% на 10 sec. и наносит дополнительно 350 ед. урона при первых трех атаках."},
[50414] = {name = "Проклятие крови", description = "[q]Увеличение физического урона, получаемого находящимися поблизости противниками, на 500 ед. на 8 sec."},
[50416] = {name = "Обмораживающее оружие", description = "[q]Накладывает на ваше оружие обмораживающие чары. Теперь ваше оружие будет наносить 95 to 105 ед. урона от магии льда и снижать скорость передвижения цели на 30% в течение 3 sec. каждый раз, когда вы нанесете урон в ближнем бою. Время действия – 8 sec."},
[50420] = {name = "Исступление", description = "[q]Увеличение скорости атаки заклинателя на 30% и наносимого им физического урона на 10% в течение 5 min."},
[50430] = {name = "Пожирание вурдалака", description = "[q]Пожирание мусорщика пустошей. Чтобы восстанавливать здоровье и потраченную ману, вашему дракону необходимо питаться."},
[50433] = {name = "Сломать лодыжку", description = "[q]Сломать лодыжку противника, в результате чего скорость его передвижения снижается на 50 % на 6 sec."},
[50456] = {name = "Грозовой удар Тиасси", description = "[q]Наносит 1050 to 1350 ед. урона от сил природы."},
[50461] = {name = "Зона антимагии", description = "[q]Ограждает от магии большую зону, в которой урон от заклинаний, наносимый участникам группы или рейда, снижается на 75%. Зона антимагии действует в течение 10 sec. или до тех пор, пока не поглотит (10000 + 2 * Attack power) ед. урона от заклинаний."},
[50476] = {name = "Призыв зверинца", description = "[q]Призывает разных существ и переносит заклинателя в безопасное место."},
[50479] = {name = "Шок Пустоты", description = "[q]Скат Пустоты молниеносно бросается к противнику, прерывая произносимое им заклинание. Противник не сможет произносить заклинаний той же школы в течение 2 sec."},
[50494] = {name = "Покров молний", description = "[q]Заклинатель полностью неуязвим для любого урона."},
[50497] = {name = "Крик Хаоса", description = "[q]Повергает ближайших противников в страх на 10 sec."},
[50498] = {name = "Разрыв брони", description = "[q]Ящер раздирает вражескую броню, ослабляя ее на 4 %. Суммируется до 3 раз. Время действия – 30 sec."},
[50501] = {name = "Урожай плоти", description = "[q]Сдирание плоти с костей."},
[50502] = {name = "Плотная шкура", description = "[q]Усиление брони на 1000 ед. на 2 min."},
[50504] = {name = "Удар Тайной магии", description = "[q]Нанесение 350 ед. урона от тайной магии и прерывание произнесения заклинаний в течение 2 sec."},
[50505] = {name = "Ледяное дыхание", description = "[q]Наносит 438 to 562 ед. урона от магии льда противникам, находящимся в 20-м секторе перед заклинателем. Кроме того, скорость атаки цели снижается на 100% на 6 sec."},
[50506] = {name = "Знак Взрыва", description = "[q]Окружает заклинателя коконом тайной энергии. Любой урон, направленный на заклинателя, взрывает кокон, нанося 943 to 1057 ед. урона от тайной магии всем противникам в радиусе 10 м. Длительность – 2 sec."},
[50512] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[50517] = {name = "Жуткий корсар", description = "[q]Выбранная цель становится почетным членом команды Жуткого Капитана де Мезы."},
[50518] = {name = "Накинуться", description = "[q]Яростная атака, после которой цель в течение 25 sec. получает на 4 % больше физического урона."},
[50519] = {name = "Ультразвук", description = "[q]Пронзительный визг, оглушающий цель на 2 sec."},
[50520] = {name = "Глубокая копка", description = "[q]Вихревая атака находящихся поблизости противников, время действия – 2 sec., нанесение обычного урона и дополнительно 110 ед."},
[50522] = {name = "Поступь Горлока", description = "[q]Нанесение 71 to 79 ед. урона от сил природы находящимся поблизости противникам, причем время между их атаками увеличивается на 33%, а скорость передвижения снижается на 40% в течение 10 sec."},
[50523] = {name = "Парализующая слизь", description = "[q]Наносит 111 to 129 ед. урона от сил природы в течение 1 с и уменьшает скорость атаки и произнесения заклинаний на 25%."},
[50533] = {name = "Хлесткая атака", description = "[q]Удар хвостом, наносящий урон от оружия с дополнительным уроном в 150 ед. противникам."},
[50535] = {name = "Вытягивание силы", description = "[q]Отнимает силы у цели каждую секунду, уменьшая наносимый целью урон на 4% и увеличивая наносимый вами урон на 4%. Этот эффект суммируется до 20 раз. Время действия – 20 sec."},
[50538] = {name = "Сгусток рвоты", description = "[q]Рвота наносит 417 to 483 ед. урона от сил природы."},
[50541] = {name = "Захват клешнями", description = "[q]Скорпид набрасывается на противника, лишая его на 10 sec. возможности использовать оружие в правой руке и оружие дальнего боя."},
[50542] = {name = "Ядовитое облако", description = "[q]140 ед. урона от сил природы находящимся поблизости противникам раз в 2 сек. в течение 10 sec."},
[50550] = {name = "Парашют", description = "[q]Переносной парашют, автоматически открывающийся при падении персонажа."},
[50573] = {name = "Удар тайной магии", description = "[q]Нанесение противнику и его ближайшим союзникам урона от тайной магии, равного 75% от обычного урона в ближнем бою и поражающим до 3 целей."},
[50578] = {name = "Осколок льда", description = "[q]Нанесение 48 to 52 ед. урона от магии льда противнику."},
[50588] = {name = "Ледяной валун", description = "[q]Валун, наносящий физический урон противнику."},
[50597] = {name = "Ледяной сталагмит", description = "[q]Нанесение 463 to 537 ед. урона от магии льда противнику и оглушение противника, в результате чего он обездвиживается и делается неспособен двигаться или атаковать в течение 3 sec."},
[50636] = {name = "Раздраженный рев", description = "[q]Безумный рев увеличивает урон, наносимый заклинателем, на 25% на 8 sec."},
[50642] = {name = "Каннибализм", description = "[q]Восполнение здоровья в течение 4 sec."},
[50648] = {name = "Скачок", description = "[q]Телепортация заклинателя на 15 метров вперед, если ничто не перекрывает путь. Освобождение заклинателя от сковывающих эффектов."},
[50652] = {name = "Таран", description = "[q]Мощная атака всех противников перед разрушителем, наносящая им 3000 ед. урона и отбрасывающая их назад. Также наносит 1500 ед. осадного урона находящимся поблизости строениям."},
[50653] = {name = "Огненное дыхание", description = "[q]Наносит 1700 to 2300 ед. урона от огня противникам, находящимся в конусе поражения перед заклинателем."},
[50655] = {name = "Объятия льда", description = "[q]Атаки накладывают эффект \"Объятия льда\", который уменьшает скорость атаки и произнесения заклинаний на 5% и снижает скорость передвижения на 5% в течение 2 sec.\n Суммируется до 50 раз."},
[50658] = {name = "Луч средоточия", description = "[q]Наносит 100 ед. урона от тайной магии в секунду.\n Каждую секунду накладывает эффект \"Сосредоточение\"."},
[50659] = {name = "Луч превращения", description = "[q]Наносит противнику 185 to 215 ед. урона от темной магии раз в 2 сек. и накладывает на него эффект \"Слабоволие\"."},
[50661] = {name = "Слабоволие", description = "[q]Увеличение урона от темной магии, получаемого заклинателем на 10% на 8 sec.\n Эффект суммируется до 10 раз."},
[50665] = {name = "Обильное кровотечение", description = "[q]Потеряв всю кровь, пациент умрет."},
[50666] = {name = "Смерть Джонатана", description = "[q]Рыцарь Джонатан погибает от потери крови."},
[50668] = {name = "Лик смерти", description = "[q]Наносит 12 to 14 ед. урона от темной магии. Заклинатель получает обратно 100% причиненного урона в виде здоровья."},
[50672] = {name = "Деморализация", description = "[q]Кто-нибудь заказывал доставку? Силам Плети на равнинах Назама только таких снарядов и не хватает. 8640 to 10560 ед. урона всем воинам Плети в радиусе взрыва снаряда."},
[50677] = {name = "Фаршеватель", description = "[q]Задействует Фаршеватель. Маленькое расстояние – большая резня!"},
[50679] = {name = "Смерть Ламуфа", description = "[q]Рыцарь Ламуф погибает от потери крови."},
[50681] = {name = "Обильное кровотечение", description = "[q]Потеряв всю кровь, пациент умрет."},
[50688] = {name = "Удар чумы", description = "[q]Жестокий удар, наносящий цели урон от оружия и поражающий ее чумой с 568 ед. урона от темной магии в течение 12 sec."},
[50689] = {name = "Власть крови", description = "[q]Рыцарь смерти находится под властью крови, повышающей наносимый им урон на 15% и исцеляющей его на 4% от величины нанесенного урона. Рыцарь может находиться под властью только одной рунической силы единовременно."},
[50690] = {name = "Обездвиживающее поле", description = "[q]Обездвиживание ближайших противников на 4 sec."},
[50693] = {name = "Смерть Жозефины", description = "[q]Рыцарь Жозефина погибает от потери крови."},
[50695] = {name = "Обильное кровотечение", description = "[q]Потеряв всю кровь, пациент умрет."},
[50701] = {name = "Швыряние извести", description = "[q]Швыряет известь в глаза противнику, уменьшая вероятность того, что он попадет по цели, на 50% на 6 sec."},
[50702] = {name = "Чародейский залп", description = "[q]Атака находящихся поблизости противников магическими стрелами, наносящими урон от тайной магии."},
[50705] = {name = "Чародейская стрела", description = "[q]Выстрел в противника магической стрелой, наносящей урон от тайной магии."},
[50713] = {name = "Безжалостный натиск", description = "[q]Бешеный натиск при каждой атаке. Увеличение физического урона, наносимого заклинателем на 10 ед. на 30 sec."},
[50715] = {name = "Снежная буря", description = "[q]Вызывает снежную бурю, которая длится 6 sec., нанося 903 to 997 ед. урона от магии льда раз в 2 сек. и замедляя скорость передвижения всех противников в зоне действия."},
[50717] = {name = "Заряженная шкура", description = "[q]Заряжение кожи заклинателя энергией, наносящей 760 to 840 ед. урона от тайной магии ближайшим противникам раз в 3 сек. Время действия – 10 min."},
[50721] = {name = "Ледяная стрела", description = "[q]Нанесение противнику урона от магии льда."},
[50728] = {name = "Неистовый вой", description = "[q]Участники группы в радиусе 15 м получают возможность нанести дополнительный урон при следующей физической атаке. Время действия – 10 sec."},
[50729] = {name = "Укус хищника", description = "[q]Нанесение противнику физического урона раз в 3 сек. в течение 15 sec."},
[50730] = {name = "Жажда крови", description = "[q]Увеличение скорости ближнего и дальнего боя, а также произнесения заклинаний на 35% для всех участников группы. Время действия – 20 sec."},
[50731] = {name = "Удар булавой", description = "[q]Нанесение 110% от обычного урона ближнего боя противнику и оглушение его на 2 sec."},
[50732] = {name = "Водяная гробница", description = "[q]Заключает цель и ее ближайших противников в водяную гробницу, которая оглушает их, наносит 900 ед. урона от магии льда раз в 2 сек. и снижает скорость передвижения на 30% в течение 6 sec."},
[50739] = {name = "Угрожающее рычание", description = "[q]Понижение силы противника на 10% и его ловкости на 10% на 30 sec."},
[50740] = {name = "Стая воронов", description = "[q]Наносит 740 to 860 ед. урона от темной магии."},
[50743] = {name = "Нечестивое бешенство", description = "[q]Повышает скорость атаки союзника на 35% на 15 sec."},
[50744] = {name = "Пламень", description = "[q]Наносит противнику 850 to 1150 ед. урона от огня."},
[50750] = {name = "Исцеление ворона", description = "[q]Могущество ворона исцеляет заклинателя на 400 ед. урона раз в 1 сек. на 5 sec."},
[50752] = {name = "Буря Скорби", description = "[q]Темное облако, которое держится 20 sec., поражая всех противников в области действия с нанесением 1665 to 1935 ед. урона от темной магии и нанося 602 to 698 ед. дополнительного урона раз в 1 сек."},
[50760] = {name = "Шок от горя", description = "[q]Мгновенно поражает цель, нанося 1750 to 2250 ед. урона от темной магии и погружая всех находящихся рядом противников в состояние медитации, парализуя их на 6 sec. При нанесении урона эффект рассеивается."},
[50761] = {name = "Столп скорбей", description = "[q]Поражение противника ударом тени с нанесением дополнительного урона от темной магии раз в 2 сек. в течение 10 sec."},
[50774] = {name = "Грохочущий топот", description = "[q]Топот, наносящий физический урон всем противникам на большом расстоянии и подкидывающий их в воздух."},
[50785] = {name = "Ядерная энергия", description = "[q]Выпускает в противника магическую стрелу, нанося урон от тайной магии."},
[50795] = {name = "Врата Зул'Драка", description = nil},
[50796] = {name = "Стрела Хаоса", description = "[q]Поражает противника стрелой Хаоса, наносящей 1741 ед. урона от огня. Стрела Хаоса преодолевает сопротивление противника и игнорирует все эффекты поглощения."},
[50804] = {name = "Чародейский обстрел", description = "[q]Наносит урон от тайной магии противнику."},
[50810] = {name = "Дробление", description = "[q]Разбивает на куски всех противников, обращенных в камень, нанося урон их союзникам. Урон зависит от того, как близко они находятся к раскалываемым целям."},
[50829] = {name = "Призыв боевого коня", description = "[q]Призывает боевого коня"},
[50830] = {name = "Цепная молния", description = "[q]Последовательно наносит урон от сил природы цели и находящимся рядом с ней противникам. Поражает до 3 целей:целей."},
[50831] = {name = "Щит молний", description = "[q]10 шаровых молний, окружающих заклинателя. 50%-я вероятность нанести 1000 ед. урона противникам, атакующим в ближнем или дальнем бою. Каждый раз, как щит срабатывает, одна из молний рассеивается. Таким образом, щит перестает действовать через 10 min., либо после 10 срабатываний."},
[50832] = {name = "Бросок факела", description = "[q]Бросает в противника факел, причиняя 232 to 268 ед. урона от огня и нанося постоянный урон от огня в размере 280 ед. раз в 3 сек. в течение 30 sec."},
[50834] = {name = "Статический заряд", description = "[q]Заряжает противника статическим электричеством. Все время действия заклинания ближайших к цели персонажей бьет электрическими разрядами, которые наносят 1000 ед. урона от сил природы. Время действия – 10 sec."},
[50837] = {name = "Гнев карателя", description = "[q]Увеличивает весь наносимый урон на 30% на 10 sec."},
[50838] = {name = "Отравляющий град", description = "[q]Наносит противнику урон от сил природы."},
[50840] = {name = "Кольцо молний", description = "[q]Нанесение 943 to 1057 ед. урона от сил природы ближайшим противникам и увеличение получаемого урона от сил природы на 10% на 10 sec."},
[50843] = {name = "Бросок булыжника", description = "[q]Бросает в противника кристалл, нанося физический урон."},
[50853] = {name = "Насквозь!", description = "[q]Пропарывание противника, наносящее дополнительный физический урон раз в 3 сек. в течение 15 sec."},
[50854] = {name = "Пинок в бок", description = "[q]Пинок, наносящий 315 to 385 ед. урона и прерывающий произнесение заклинаний на 5 sec."},
[50868] = {name = "Топот", description = "[q]Нанесение 2375 to 2625 ед. физического урона ближайшим противникам."},
[50895] = {name = "Привязь молнии", description = "[q]Нанесение противнику урона от сил природы в течение 5 sec."},
[50896] = {name = "Бросок валуна", description = "[q]Метает горящий булыжник, который наносит 2999 to 3001 ед. урона противникам и 900 to 1100 ед. осадного урона расположенным поблизости строениям."},
[50900] = {name = "Поражение громом", description = "[q]Наносит противнику урон от сил природы."},
[50905] = {name = "Правосудие Алого Натиска", description = "[q]Передает 50% брони заклинателю на 10 sec."},
[50908] = {name = "Печать натиска", description = "[q]Дает заклинателю шанс обойти броню противника в ближнем бою."},
[50915] = {name = "Гневное освящение", description = "[q]Освящение земли, на которой стоит паладин, с нанесением 1800 ед. урона от светлой магии в течение 8 sec. противникам, которые входят на этот участок."},
[50921] = {name = "Безрассудный натиск", description = "[q]Безрассудный натиск при каждой атаке. Увеличение получаемого и наносимого заклинателем урона на 5% на 12 sec."},
[50933] = {name = "Бешенство", description = "[q]Увеличение скорости атаки заклинателя на 30% и наносимого им физического урона на 75 ед. на 2 min."},
[50978] = {name = "Неимоверный удар", description = "[q]Наносит противнику и его ближайшим союзникам урон от оружия, увеличенный на 4500 ед. Захватывает до 3 целей."},
[50980] = {name = "Катаклизм", description = "[q]Наносит 171 to 189 ед. урона всем находящимся поблизости силам Плети."},
[50983] = {name = "Титанический подъем", description = "[q]Восполняет 10% маны в секунду в течение 10 sec. Получаемый в это время урон повышается на 10%."},
[50985] = {name = "Воссоздание", description = "[q]Восполнение всего здоровья."},
[50989] = {name = "Огненное дыхание", description = "[q]Цели, находящиеся в конусе поражения перед заклинателем, получают 3000 ед. урона от огня в течение 3 sec"},
[50992] = {name = "Взрыв души", description = "[q]Взрывает враждебную цель, нанося ей урон от темной магии, равный 20% от общего запаса здоровья."},
[50994] = {name = "Жесткая шкура", description = "[q]Усиливает кору энта, увеличивая броню на 3000 ед. и сопротивлении магии на 60 ед. на 15 sec."},
[50995] = {name = "Могучая власть крови", description = "[q]Власть крови усиливает рыцаря смерти и членов его группы в радиусе 30 м, увеличивая урон и лечение на 15%. Рыцарь смерти может находиться под властью только одной рунической силы единовременно."},
[50997] = {name = "Кристальные цепи", description = "[q]Наносят противнику урон от магии льда и обездвиживают цель на 10 sec."},
[51001] = {name = "Темная сущность", description = "[q]Облако темной сущности окружает цель, увеличивая получаемый ею урон на 50%, уменьшая скорость передвижения на 30% и нанося 1885 to 2115 ед. урона от темной магии ближайшим противникам."},
[51002] = {name = "Призывание Капитана лазурного кольца", description = "[q]Призыв Капитана лазурного кольца, чтобы тот уничтожил ваших противников."},
[51009] = {name = "Отражение души", description = "[q]Что-то гнетет вашу душу, отражая на вас 100% урона, который вы наносите, в течение 3 sec."},
[51016] = {name = "Вампирская стрела", description = "[q]Похищает 2000 ед. здоровья у противника, восстанавливая такое же количество здоровья заклинателю."},
[51020] = {name = "Искажение времени", description = "[q]Оглушает цель на 2 sec., наносит 569 to 731 ед. урона от сил природы и сбрасывает уровень угрозы."},
[51054] = {name = "Усиление магии", description = "[q]Усиливает магию, направленную на выбранного участника группы, увеличивая получаемый им магический урон не более чем на 900 ед. и усиливая эффекты лечения на нем не более чем на 1800 ед. Время действия – 30 sec."},
[51074] = {name = "Заслон Тени", description = "[q]Нанесение урона от темной магии находящимся рядом противникам."},
[51103] = {name = "Ледяная бомба", description = "[q]Запускает ледяную бомбу, которая наносит урон от магии льда и замораживает землю поблизости, нанося периодический урон и замедляя скорость передвижения противников в зоне своего действия."},
[51110] = {name = "Мощный чародейский взрыв", description = "[q]Создает магическую взрывную волну, наносящую противникам урон от тайной магии."},
[51112] = {name = "Телепорт", description = "[q]Переносит заклинателя в центр Окулуса."},
[51121] = {name = "Часовая бомба", description = "[q]Наносит 3800 to 4200 ед. урона от тайной магии и устанавливает волшебную бомбу на противника. Через 6 сек. бомба взрывается, нанося дополнительный урон от тайной магии ближайшим противникам. Количество наносимого урона равно количеству недостающего здоровья цели."},
[51131] = {name = "Удушение", description = "[q]Нанесение 150 ед. урона от темной магии раз в секунду в течение 3 sec. Онемение противника, что не позволяет ему пользоваться заклинаниями в течение 3 sec."},
[51144] = {name = "Взмах крылом", description = "[q]Нанесение 563 to 937 ед. урона противникам в секторе перед заклинателем, отбрасывая их назад."},
[51152] = {name = "Оплетающие сети", description = "[q]Опутывает цель липкой паутиной."},
[51153] = {name = "Чародейский залп", description = "[q]Наносит урон от тайной магии противнику."},
[51162] = {name = "Сдвиг плоскости", description = "[q]Переносит заклинателя в астральный мир на 18 sec., делая его невосприимчивым к магическому урону. Заклинатель при этом не может передвигаться и атаковать."},
[51170] = {name = "Яростный натиск", description = "[q]Увеличивает наносимый урон на 25% и скорость произнесения заклинаний на 100% на 12 sec."},
[51202] = {name = "Уничтожение вурдалака", description = "[q]Уничтожает вурдалака и возвращает его сущность заклинателю."},
[51211] = {name = "Шквал клинков", description = "[q]Повышает скорость атаки на 20%, при этом также наносится удар ближайшему противнику. Время действия – 15 sec."},
[51218] = {name = "Огненное дыхание", description = "[q]Наносит 39 to 46 ед. урона от огня противникам, находящимся в конусе поражения перед заклинателем."},
[51219] = {name = "Огненное дыхание", description = "[q]Наносит урон от огня противникам в конусе поражения перед заклинателем."},
[51222] = {name = "Taxi Path Flavor", description = nil},
[51240] = {name = "Страх", description = "[q]Повергает противника в ужас, заставляя его бежать в течение 6 sec. Количество одновременно напуганных противников – 1."},
[51243] = {name = "Чародейская стрела", description = "[q]Выстрел в противника магической стрелой, наносящей урон от тайной магии."},
[51253] = {name = "Тяжелый шаг", description = "[q]Нанесение физического урона ближайшим противникам."},
[51259] = {name = "Пограничная тревога", description = "[q]Эта машина неудержима. Возвращайтесь на поле боя немедленно!"},
[51272] = {name = "Пограничная тревога", description = "[q]Эта машина неудержима. Возвращайтесь на поле боя немедленно!"},
[51285] = {name = "Кровавый удар", description = "[q]Похищение 694 to 806 ед. здоровья у противника и передача его заклинателю."},
[51287] = {name = "Стрела Хаоса", description = "[q]Выпускает в противника стрелу огня хаоса, наносящую ему урон от огня. Сопротивление стреле хаоса невозможно, и она пробивает защиту от магии."},
[51290] = {name = "Жажда душ", description = "[q]Атака противника, наносящая ему увеличенный до 120% стандартный урон от оружия. 5 последующие успешные:последующих успешных атак ближнего боя восполняют 119 to 131 ед. здоровья. Время действия – 10 sec."},
[51307] = {name = "Непоколебимая воля", description = "[q]Повышает скорость атаки и скорость произнесения заклинаний на 25%, повышает скорость передвижения на 20%. Время действия – 30 sec."},
[51315] = {name = "Прикосновение проказы", description = "[q]Поражение проказой. Нанесение 120 ед. урона от темной магии раз в 2 сек. и снижение скорости передвижения на 75% в течение 8 sec."},
[51316] = {name = "Лоботомия", description = "[q]Внедрение в мозг цели, что приводит к ее обезоруживанию и немоте. Цель не может атаковать и произносить заклинания в течение 2 sec."},
[51322] = {name = "Переплетение", description = nil},
[51334] = {name = "Мощный удар", description = "[q]Наносит мощный удар цели, причиняя 495 to 605 ед. урона и откидывая назад."},
[51336] = {name = "Магическое притяжение", description = "[q]Телепортирует всех находящихся поблизости противников к заклинателю."},
[51338] = {name = "Темное пламя", description = "[q]Цели, находящиеся в конусе поражения перед заклинателем, получают 764 to 836 ед. урона от темной магии и дополнительно 492 ед. урона от огня в течение 8 sec"},
[51339] = {name = "Истребление", description = "[q]Наносит урон от темной магии и увеличивает весь получаемый урон на 5% на 8 sec."},
[51340] = {name = "Проклятие бессилия", description = "[q]Ослабление урона, наносимого целью, на 20% в течение 15 sec."},
[51356] = {name = "Рвота Зловещего", description = "[q]Рвота наносит 602 to 698 ед. урона от сил природы и снижает вероятность попадания на 50% на 6 sec."},
[51357] = {name = "Вызов паука", description = nil},
[51363] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[51370] = {name = "Призыв призрака Драккари", description = nil},
[51384] = {name = "Молот гнева", description = nil},
[51399] = {name = "Хватка смерти", description = "[q]Используя нечестивую энергию, пронизывающую и объединяющую все сущее, притягивает противника к рыцарю смерти, заставляя его атаковать рыцаря в течение 3 sec."},
[51421] = {name = "Выстрел из пушки", description = "[q]Выстрел из огненной пушки."},
[51430] = {name = "Разрушительный удар", description = "[q]Наносит дополнительно 250 ед. урона от оружия."},
[51431] = {name = "Высвобождение силы", description = "[q]Высвобождение мощной магической силы наносит 296 to 344 ед. урона от темной магии раз в 1 с, но при этом увеличивает наносимый заклинателем урон на 20% на 5 sec."},
[51432] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[51437] = {name = "Дымящиеся кости", description = "[q]Нанесение 167 to 193 ед. урона от огня раз в 2 сек. ближайшим противникам."},
[51439] = {name = "Ответный удар", description = "[q]Наносит 120 ед. урона от огня нападающим при получении урона. Время действия – 6 sec."},
[51442] = {name = "Благословение сверкающего зайца", description = "[q]Повышает скорость передвижения на 50% на 1 min."},
[51454] = {name = "Бросок", description = "[q]Бросок оружия в противника, наносящий 2125 to 2875 ед. физического урона."},
[51467] = {name = "Устрашающий рев", description = "[q]Парализует ужасом выбранного противника на 4 sec. и заставляет остальных находящихся поблизости противников бежать в страхе."},
[51469] = {name = "Неистовство Бешеного Сердца", description = "[q]Выпускает на волю неистовство Бешеного Сердца, с силой подбрасывая противника в воздух."},
[51475] = {name = "Вызов элементаля воздуха", description = "[q]Призывает элементаля воздуха для сопровождения заклинателя до отмены заклинания."},
[51484] = {name = "Железная сила", description = "[q]Увеличивает урон на 15% на 1 min."},
[51491] = {name = "Неодолимый удар", description = "[q]Нанесение 1804 to 2096 ед. урона противнику, оглушение на 2 sec."},
[51492] = {name = "Рывок", description = "[q]Рывок к противнику, наносящий обычный урон, увеличенный на 75 ед."},
[51493] = {name = "Топот", description = "[q]Наносит 2188 to 2812 ед. физического урона находящимся рядом противникам и отбрасывает их назад."},
[51494] = {name = "Железный кулак", description = "[q]Увеличение физического урона, наносимого заклинателем, на 20% на 30 sec."},
[51496] = {name = "Разрушительный луч", description = "[q]Луч Тьмы ранит противника, нанося ему 1619 to 1881 ед. урона от темной магии и дополнительно 562 to 638 ед. урона от темной магии раз в 1 сек. в течение 4 sec."},
[51499] = {name = "Нестерпимая злость", description = "[q]Повышает скорость атаки цели на 60% и наносимый ею физический урон на 20% на 1 min."},
[51500] = {name = "Жаркий пламень", description = "[q]Наносит противнику 213 to 287 ед. урона от огня и создает жаркое пламя под ногами цели."},
[51503] = {name = "Власть", description = "[q]Подчинение противника-гуманоида 80 уровня и ниже на 8 sec."},
[51505] = {name = "Выброс лавы", description = "[q]Вы бросаете в противника раскаленную лаву, нанося 1587 ед. урона от огня. Если цель находится под действием эффекта \"Огненный шок\", то способность \"Выброс лавы\" нанесет критический удар."},
[51507] = {name = "Призывание осколыша", description = "[q]Вызывает выветренный осколыш, сопровождающий заклинателя до тех пор, пока заклинатель его не развеет."},
[51512] = {name = "Защита Нелтариона", description = "[q]Сила Нелтариона уменьшает получаемый урон на 20% и увеличивает наносимый урон на 20% в течение 1.50 min."},
[51584] = {name = "Ледяной натиск", description = "[q]Вас окутывает холод, который наносит 324 to 376 ед. урона от магии льда и повышает вероятность нанесения вам критического урона на 30% на 8 sec."},
[51586] = {name = "Волна исцеления", description = "[q]Призывает силы природы для исцеления союзника."},
[51587] = {name = "Молния", description = "[q]Поражение противника молнией, урон от сил природы."},
[51588] = {name = "Огненный шок", description = "[q]Поджигает противника, а затем наносит дополнительный урон от огня раз в 3 сек. в течение 12 sec."},
[51591] = {name = "Молот бури", description = "[q]Наносит 491 to 509 ед. урона и прерывает произнесение заклинаний."},
[51601] = {name = "Оплеуха", description = "[q]Удар по голове, наносящий 150% урона от оружия и уменьшающий вероятность уклонения или парирования атаки на 20% в течение 6 sec."},
[51605] = {name = "Доблесть", description = "[q]Наделяет цель силой, повышающей скорость произнесения заклинаний на 20%."},
[51612] = {name = "Отвод статического заряда", description = "[q]Противодействие заклинанию противника, нанесение 1520 to 1680 ед. урона от сил природы и предотвращение произнесения заклинаний противником снова в течение 4 sec."},
[51613] = {name = "Ржавление брони", description = "[q]Ослабление брони противника на 50% на 8 sec."},
[51622] = {name = "Разъяренные тени", description = "[q]Разбушевавшиеся тени наносят 694 to 806 ед. урона от темной магии, отбрасывая назад всех находящихся поблизости противников."},
[51648] = {name = "Призыв Застывшей земли", description = "[q]Призывает застывшего элементаля для защиты расселины."},
[51676] = {name = "Нерешительность", description = "[q]Увеличение промежутков между атаками на 25%, замедление произнесения заклинаний на 20%, и скорости перемещения на 50%. Время действия – 15 sec."},
[51678] = {name = "Таран", description = "[q]Наносит 3500 ед. урона противникам перед разрушителем и отталкивает их назад. Также наносит 2000 ед. осадного урона строениям."},
[51681] = {name = "Обрушивающийся топот", description = "[q]Топает по земле, нанося 300% нормального урона ближайшим противникам и вызывая у них головокружение."},
[51725] = {name = "Чародейский взрыв", description = "[q]Создание магической взрывной волны, наносящей противникам урон от тайной магии."},
[51727] = {name = "Разряд Чар", description = "[q]Этот разряд восполняет 20% маны ближайших противников."},
[51732] = {name = "Чародейский настой", description = "[q]Наделение цели энергией тайной магии. По истечении 4 секунд цель подвергается воздействию заклинания \"Перегрузка\", которое наносит 220 ед. урона от тайной магии раз в 2 с ближайшим существам."},
[51740] = {name = "Эффект обжигающей ловушки", description = nil},
[51742] = {name = "Чародейский выстрел", description = "[q]Мгновенный выстрел, наносящий (Ranged attack power * 0.15 + 523) ед. урона от тайной магии и снимает магические эффекты в количестве 1."},
[51750] = {name = "Крики мертвых", description = "[q]Леденящий кровь вопль, оглушающий всех противников на 8 sec."},
[51751] = {name = "Кровавая атака", description = "[q]Питомец бодает противника, нанося 2063 to 2937 ед. урона."},
[51752] = {name = "Топот Топтуна", description = "[q]Наносит находящимся рядом противникам обычный урон, усиленный на 0 ед., сбивает их с ног и оглушает на 3 sec."},
[51756] = {name = "Рывок", description = "[q]Броситься на противника и оглушить его (время восстановления: 20 сек.)."},
[51758] = {name = "Отражение Огня", description = "[q]Отражение заклинаний огня с помощью щита. Время действия – 6 sec."},
[51763] = {name = "Отражение во льду", description = "[q]Поднимает щит, отражающий направленные на вас заклинания магии льда. Длительность – 6 sec."},
[51764] = {name = "Отражение теней", description = "[q]Поднимает щит, отражающий направленные на вас заклинания темной магии. Длительность – 6 sec."},
[51766] = {name = "Чародейское отражение", description = "[q]Поднять свой щит для отражения заклинаний тайной магии, направленных против вас. Время действия – 6 sec."},
[51768] = {name = "Огненное дыхание", description = "[q]Наносит 509 to 591 ед. урона от огня противникам, находящимся в конусе поражения перед заклинателем."},
[51772] = {name = "Цапнуть", description = nil},
[51776] = {name = "Щит молний", description = "[q]3 шаровых молний, окружающих заклинателя. 50%-я вероятность нанести 650 ед. урона противникам, атакующим в ближнем или дальнем бою. Каждый раз, как щит срабатывает, одна из молний рассеивается. Таким образом, щит перестает действовать через 10 min., либо после 3 срабатываний."},
[51779] = {name = "Стрела ледяного огня", description = "[q]Наносит 48 to 55 ед. урона от ледяного огня, снижает скорость передвижения цели на 40% и наносит дополнительно 12 ед. урона от ледяного огня в течение 9 sec. Если цель более восприимчива к урону от магии льда, это заклинание будет наносить урон от магии льда вместо урона от огня."},
[51781] = {name = "Необузданный вихрь", description = "[q]Кружась в вихре, заклинатель атакует ближайших противников, нанося каждому двойной урон. Время действия – 3 sec."},
[51786] = {name = "Спектральный разящий удар", description = "[q]Наносит противнику урон от оружия, усиленный на 5 ед."},
[51787] = {name = "Мистическая стрела", description = "[q]Мистическая стрела вырывает вас из реальности, нанося 509 to 591 ед. урона от тайной магии и увеличивая вероятность нанести критический удар по цели на 5% на 1 min.\n Суммируется до 5 раз."},
[51797] = {name = "Чародейская вспышка", description = "[q]Поражает противника зарядом энергии, нанося ему 24 to 29 ед. урона от тайной магии. Каждый раз, когда вы применяете заклинание \"Чародейская вспышка\", время произнесения заклинания уменьшается, а расход маны увеличивается. Эффект суммируется до 4 раз и длится 6 sec."},
[51799] = {name = "Рунический интеллект", description = "[q]Увеличение интеллекта цели на 200 ед. и повышение рейтинга меткости заклинаний на 50 на 2 min."},
[51804] = {name = "Вытягивание мощи", description = "[q]Снижение урона от магии и ослабление лечения цели в течение 20 sec."},
[51805] = {name = "Рост кристаллов", description = "[q]Увеличение наносимого целью физического урона на 25% на 15 sec."},
[51817] = {name = "Тайфун", description = "[q]Вызов сильнейшего тайфуна, который наносит 602 to 698 ед. урона от сил природы противникам, попавшим в зону его действия, и отбрасывает их назад на 5 м."},
[51818] = {name = "Слово Тьмы: Смерть", description = "[q]Нанесение 24 to 29 ед. урона от темной магии как противнику, так и заклинателю."},
[51819] = {name = "Мощный рывок", description = "[q]Увеличение скорости атаки заклинателя на 15% и наносимого им физического урона на 15%."},
[51820] = {name = "Чародейский взрыв", description = "[q]Создание магической взрывной волны, наносящей противникам урон от тайной магии."},
[51832] = {name = "Короткое замыкание", description = "[q]Поражение шоком области вокруг заклинателя и нанесение 4625 to 5375 ед. урона от сил природы всем находящихся поблизости противникам, исключая заклинателя."},
[51842] = {name = "Рывок", description = "[q]Рывок к противнику, наносящий 1850 to 2150 ед. физического урона."},
[51857] = {name = "Морозный удар", description = "[q]Наносит противнику и его ближайшим союзникам урон от оружия, увеличенный на 40 ед., и снижает их скорость атаки и произнесения заклинаний на 30%. Захватывает до 5 целей."},
[51859] = {name = "Поглощение Акеруса", description = "[q]Собирает важную информацию о строении Алого Ордена и его окрестностях."},
[51873] = {name = "Кровавый червь", description = "[q]Создание кровавого червя, который атакует ближайшую цель. Заклинатель восстанавливает здоровье, когда червь наносит урон. Время действия – 8 sec."},
[51875] = {name = "Трепка", description = "[q]Увеличение урона последующей атаки на 5 ед."},
[51876] = {name = "Удар бури", description = "[q]Мгновенная атака обоими оружиями. Кроме того, две последующие атаки сил природы нанесут цели на 20% больше урона. Время действия – 12 sec."},
[51877] = {name = "Порыв ледяного ветра", description = "[q]Сильный порыв ветра наносит противнику урон от сил природы."},
[51878] = {name = "Ледяной выпад", description = "[q]Заклинание наносит противнику урон от магии льда и снижает скорость его передвижения на 50% на 10 sec."},
[51879] = {name = "Разъедание плоти", description = "[q]Разъедание плоти противника с нанесением урона от сил природы раз в 3 сек. в течение 15 sec."},
[51897] = {name = "Визг банши", description = "[q]Немота и разоружение противников, не позволяющие им использовать заклинания в течение 4 sec."},
[51899] = {name = "Проклятие банши", description = "[q]Снижает вероятность того, что противник попадет по цели, на 40% на 10 sec."},
[51901] = {name = "Хлыст сна", description = "[q]Нанесение 602 to 698 ед. урона от сил природы."},
[51904] = {name = "Призыв вурдалаков против Алого ордена", description = "[q]Призывает вурдалаков, и те нападают на противников, давая вам время спасти Око Акеруса."},
[51917] = {name = "Рассекающий удар", description = "[q]Нанесение 150% от обычного урона ближнего боя противнику и его ближайшему союзнику."},
[51918] = {name = "Оживление", description = "[q]Воскрешение павшего рыцаря смерти."},
[51922] = {name = "Быстрый сон", description = "[q]Сон снижает скорость атаки и скорость произнесения заклинаний на 100%."},
[51926] = {name = "Костюм корсара", description = "[q]Одеть цель в костюм корсара."},
[51937] = {name = "Удар когтем", description = "[q]Наносит противнику обычный урон, усиленный дополнительным."},
[51938] = {name = "Удар крылом", description = "[q]Поражение противников рядом с заклинателем. Их сбивает с ног, их движения замедляются на 50% в течение 8 sec."},
[51944] = {name = "Тяжелый шаг", description = "[q]Нанесение физического урона ближайшим противникам."},
[51946] = {name = "Маневр уклонения", description = "[q]Повышает вероятность уклонения заклинателя на 75% на 4 sec."},
[51951] = {name = "Водобоязнь", description = "[q]Поражает противника водобоязнью, нанося ему 210 ед. урона от сил природы раз в 3 сек. в течение 15 sec., а также повышая вероятность нанесения критического удара на 30% и увеличивая критический урон на 30% на 15 sec."},
[51959] = {name = "Сетка для ловли кур", description = "[q]Бросок тонкой сети. Позволяет поймать нерасторопную сбежавшую курицу на расстоянии до 20 м."},
[51963] = {name = "Удар горгульи", description = "[q]Наносит противнику урон от сил природы."},
[51972] = {name = "Спокойствие", description = "[q]Восстановление у всех находящихся поблизости участников группы 1388 to 1612 ед. здоровья раз в 2 сек. в течение 10 sec. Требуется поддержание заклинания."},
[52006] = {name = "Маскировка", description = "[q]Маскирует Око Акеруса. Любые действия и вступление в бой снимают маскировку."},
[52011] = {name = "Обновляющий луч", description = "[q]Восстанавливает 4% здоровья союзника раз в 2 сек. в течение 6 sec."},
[52022] = {name = "Железная форма", description = "[q]При активации придает невосприимчивость к эффектам кровотечения, яда и болезни. Броня увеличивается на 20%. Время действия – 8 sec."},
[52026] = {name = "Мощный удар", description = "[q]Сокрушительный удар, наносящий противнику 120% урона от оружия."},
[52027] = {name = "Вихрь", description = "[q]Кружась в вихре, заклинатель атакует ближайших противников, нанося 75% обычного урона на каждом обороте. Время действия – 8 sec."},
[52029] = {name = "Отталкивание", description = "[q]Наносит 20% обычного урона ближайшим противникам и отбрасывает их назад."},
[52058] = {name = "Удар Земли", description = "[q]Ударяет по земле, нанося 925 to 1075 ед. физического урона ближайшим противникам, подбрасывая их в воздух."},
[52060] = {name = "Невидимость", description = "[q]Заклинатель делается невидимым до отмены эффекта."},
[52067] = {name = "Аура регенерации", description = "[q]Восстанавливает здоровье и ману находящихся поблизости существ."},
[52071] = {name = "Убийственный гнев", description = "[q]Повышает скорость атаки на 100% на 6 sec."},
[52080] = {name = "Трескающиеся кости", description = "[q]Удар по земле, наносящий 200% ед. обычного урона находящимся рядом противникам и на 20 sec увеличивающий на 100% получаемый ими урон."},
[52086] = {name = "Кокон", description = "[q]Выпускает нить паутины, обездвиживая цель на 3 sec. При прекращении действия эффекта, цель будет опутана паутиной и оглушена на 10 sec. Цель сможет вырваться наружу при уничтожении паутины."},
[52087] = {name = "Кокон", description = "[q]Выпускает нить паутины, обездвиживая цель на 3 sec. При прекращении действия эффекта, цель будет опутана паутиной и оглушена на 10 sec. Цель сможет вырваться наружу при уничтожении паутины."},
[52088] = {name = "Рывок магнатавра", description = "[q]Атака противника с нанесением 200% урона от оружия."},
[52097] = {name = "Временный электрический заряд", description = "[q]Электризует дружественные цели на расстоянии 25 м от заклинателя, увеличивая их размер, а также наносимый урон на 30%."},
[52098] = {name = "Рывок вверх", description = "[q]Атака цели электричеством."},
[52101] = {name = "Пылающие кулаки", description = "[q]Ваши кулаки охватывает пламя. Атаки ближнего боя наносят 292 to 328 ед. урона от огня в радиусе 5 м."},
[52117] = {name = "Пульсирующие осколки", description = "[q]Раз в 5 сек. наносят 30 ед. урона от магии льда ближайшим противникам и в течение 15 sec. уменьшают их скорость атаки и произнесения заклинаний.\n Эффект суммируется до 10 раз."},
[52126] = {name = "Травля червями", description = "[q]Натравляет на ближайшие цели червей. Заклинатель восполняет здоровье, когда черви наносят урон. Время действия – 7 sec."},
[52139] = {name = "Удар гиганта", description = "[q]Наносит 200% от обычного урона и отбрасывает противника назад."},
[52164] = {name = "Scourge Invasion Blood Machine Beam, Vat", description = nil},
[52166] = {name = "Гром", description = "[q]Вы вызываете молнию, наносящую урон всем противникам в радиусе 10 м. Молния наносит 1587 to 1813 ед. урона от сил природы ближайшим противникам, откидывая их на 181.3 м."},
[52167] = {name = "Рывок магнатавра", description = "[q]Атака противника с нанесением 0% урона от оружия."},
[52174] = {name = "Героический прыжок", description = "[q]Воин совершает прыжок и сокрушает всех противников в радиусе 8 м от места приземления, нанося им (1 + 0.5 * Attack power) ед. урона."},
[52182] = {name = "Гробница Бессердечных", description = "[q]Заклинатель заключает противника в непроницаемую ледяную гробницу. Снять эффект может только сам заклинатель."},
[52185] = {name = "Узы подчинения", description = "[q]Привязывает того, на кого направлено заклинание, к заклинателю, защищая последнего от любого урона. Если привязанный убит, действие заклинания прекращается."},
[52206] = {name = "Размашистый тумак", description = "[q]Внезапный удар в лицо. Больно!"},
[52207] = {name = "Пожирание гуманоида", description = "[q]263 to 337 ед. урона раз в 1.5 сек."},
[52208] = {name = "Огненный след", description = "[q]Печать огня наносит 200 ед. урона от огня раз в 1 сек. в течение 8 sec."},
[52219] = {name = "Дыхание Хар'коа", description = "[q]Дает тому, на кого направлено заклинание, способность входить в нижний мир возле алтаря Кетц'лун."},
[52221] = {name = "Удар героя", description = "[q]Мощная атака, увеличивающая урон в ближнем бою на 11 ед. и значительно повышающая уровень создаваемой угрозы."},
