[52237] = {name = "Раскатистый топот", description = "[q]Наносит 2313 to 2687 ед. урона всем противникам и раскалывает всех хладных големов в пределах 99 м."},
[52238] = {name = "Пламенрав", description = "[q]Наносит удар по наковальне Волхана, создавая оплавленных големов."},
[52254] = {name = "Ракеты воздух-воздух", description = "[q]Выстрел по воздушному противнику."},
[52257] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[52262] = {name = "Бешенство загнанного", description = "[q]Увеличение скорости атаки заклинателя на 100% и наносимого им физического урона на 200 ед. на 2 min."},
[52265] = {name = "Соглашение о выкупе", description = "[q]Куда это ты ведешь эту лошадь?"},
[52268] = {name = "Галоп", description = "[q]Повышает скорость передвижения на 100% на 2 секунды."},
[52270] = {name = "Залп", description = "[q]Обрушивает на противника и его ближайших союзников град снарядов и поражает до 3 целей, нанося им 75 ед. дополнительного урона."},
[52271] = {name = "Медный таз", description = "[q]Взлетает в воздух и обрушивается на всех находящихся поблизости противников, нанося серьезный урон и оглушая их на 4 sec."},
[52272] = {name = "Бросок валуна", description = "[q]Бросает в противника валуном, нанося серьезный урон и прерывая любое произносимое им в этот момент заклинание."},
[52274] = {name = "Поглощение горлока", description = "[q]Поглощает находящегося рядом горлока, восполняя 5% здоровья каждые 2 сек. Действует 10 секунд."},
[52281] = {name = "Пламя провидца", description = "[q]Могущество провидца увеличивает урон от заклинаний на 400 ед. на 2 min."},
[52282] = {name = "Огненный шар", description = "[q]Нанесение урона от огня противнику."},
[52283] = {name = "Рев вождя", description = "[q]Рев вождя дает невосприимчивость к эффектам сковывания, оглушения и контроля."},
