[52316] = {name = "Сокрушение головы", description = "[q]Наносит жестокий удар по голове, причиняющий 145% обычного урона противникам в конусе поражения перед заклинателем."},
[52317] = {name = "Защита", description = "[q]Снижение получаемого урона на 50% на 4 sec."},
[52321] = {name = "Стойкость богов", description = "[q]Неуязвимость к оглушению. Все виды атак исцеляют заклинателя в течение 20 секунд."},
