[52334] = {name = "Сырая слизь", description = "[q]Наносит 602 to 698 ед. урона от темной магии."},
[52338] = {name = "Бросок валуна", description = "[q]Выстрел огромным валуном на большое расстояние. Валун наносит урон от огня и 1000 ед. осадного урона всем противникам в радиусе 10 м."},
[52340] = {name = "Дух Хар'коа", description = nil},
