[52352] = {name = "Пожирание плоти", description = "[q]Похищение 1980 to 2420 ед. здоровья и 5% выносливости у противника и передача их заклинателю."},
[52356] = {name = "Бросок", description = "[q]Бросок оружия в противника, наносящий 150% обычного урона."},
[52358] = {name = "На мягких лапах", description = "[q]Повышает скорость передвижения на 20% на 15 sec."},
[52362] = {name = "Вызов Всадника", description = "[q]Приведите коня смерти в Разлом Смерти и вызовите Саланара Всадника. Можно использовать только в Разломе Смерти."},
[52372] = {name = "Ледяное прикосновение", description = "[q]Наносит от 217 до 235 ед. урона от магии льда (в зависимости от силы атаки), понижает скорость дальнего, ближнего боя и применения противником заклинаний на 15% на 20 sec."},
[52373] = {name = "Удар чумы", description = "[q]Жестокий удар, наносящий противнику урон от оружия, усиленный на 37 ед. урона (величина зависит от силы атаки), и заражающий противника чумой, наносящей 292 ед. урона от темной магии за 12 sec."},
[52374] = {name = "Кровавый удар", description = "[q]Мгновенно атакует противника, нанося ему 60% урона от оружия и дополнительно 55 ед. урона за каждую болезнь."},
[52375] = {name = "Лик смерти", description = "[q]Выпускает заряд нечестивой энергии, наносящий 184 ед. урона от темной магии (урон зависит от силы атаки) противнику или излечивающий нежить-союзника на 276 ед. здоровья."},
[52383] = {name = "Цепная молния", description = "[q]Последовательно наносит урон от сил природы цели и находящимся рядом с ней противникам. Поражает до 3 целей:целей."},
[52387] = {name = "Жар", description = "[q]Увеличивает жар оплавленных големов, продлевая время их жизни на 20%."},
[52400] = {name = "Бешеная ярость", description = "[q]Увеличение скорости атаки заклинателя на 60% и наносимого им физического урона на 50 ед. на 2 min."},
[52401] = {name = "Кишкорез", description = "[q]Порезать противника, нанеся ему 800 to 1200 ед. урона и 1500 ед. дополнительного урона в течение 9 sec."},
[52402] = {name = "Потрясающая сила", description = "[q]Оглушает противника на 6 sec. От этого удара нельзя уклониться."},
[52425] = {name = "Черепно-мозговая травма", description = "[q]Сокрушительный удар по голове, наносящий 324 to 376 ед. физического урона и дополнительно 139 to 161 ед. физического урона раз в 1 сек. в течение 5 sec."},
[52429] = {name = "Дробление", description = "[q]Раскалывает остывшего голема, нанося 4625 to 5375 ед. урона ближайшим противникам."},
[52430] = {name = "Искаженный разум", description = "[q]Снижает скорость произнесения заклинаний на 75% на 6 sec."},
[52433] = {name = "Испепеляющий удар", description = "[q]Наносит противнику 450 ед. урона от огня, а затем дополнительно 1350 ед. урона от огня в течение 9 sec"},
[52435] = {name = "Пушка Алого ордена", description = "[q]Благодаря малым затратам горючего это орудие дальнего и среднего боя стало абсолютно незаменимым в бою. Цельтесь точнее, все остальное эта пушка сделает сама!"},
[52436] = {name = "Обстрел из пушек Алого ордена", description = "[q]БА-БАХ! Нанесение 13613 to 19387 ед. урона всем противникам в радиусе 12 м от точки попадания снаряда."},
[52442] = {name = "Аура благочестия", description = "[q]Усиливает броню всех находящихся рядом союзников на 1200."},
[52444] = {name = "Свет небес", description = "[q]Призывает силы света для исцеления союзника."},
[52445] = {name = "Экзорцизм", description = "[q]Наносит 2375 to 2625 ед. урона от светлой магии нежити и демонам."},
[52446] = {name = "Всплеск кислоты", description = "[q]Разъедающий фонтан крови извергается из заклинателя, нанося окружающим союзникам и противникам 200 ед. урона от сил природы в секунду. Время действия – 10 sec."},
[52451] = {name = "Призывание вурдалаков", description = "[q]Призывает рядом с заклинателем двоих вурдалаков-прислужников."},
[52457] = {name = "Солдаты Драк'агуула", description = "[q]Ожидают приказов Драк'агуула."},
[52460] = {name = "Атака берсерка", description = "[q]Бросок на противника, отбрасывающий его назад и наносящий обычный урон, усиленный на 150 ед."},
[52461] = {name = "Неукротимое бешенство", description = "[q]Увеличение скорости атаки заклинателя на 30% и наносимого им физического урона на 10% на 8 sec. Заклинателя невозможно остановить."},
[52466] = {name = "Проклятье Драккари", description = "[q]Наносит урон от темной магии противнику раз в 2 сек. на протяжении 10 sec."},
[52467] = {name = "Пожирание плоти", description = "[q]Похищение 1980 to 2420 ед. здоровья и 5% характеристик персонажа у противника и передача их заклинателю."},
[52468] = {name = "Пророчество крови", description = "[q]Увеличивает получаемый целью урон на 30 ед. и наносимый урон на 15 ед. в течение 10 sec."},
[52469] = {name = "Заразный укус", description = "[q]Крик'тир кусает цель, нанося 1885 to 2115 ед. урона от сил природы. Дополнительно цель получает 1000 ед. урона от сил природы раз в 3 сек. Получаемый физический урон увеличивается на 20%. Время действия – 9 sec."},
[52470] = {name = "Исступление", description = "[q]Повышает скорость атаки заклинателя на 300%, но уменьшает наносимый физический урон на 50% на 8 sec."},
[52480] = {name = "Взрыв вурдалака", description = "[q]Взрывает ближайшего вурдалака, нанося 3800 to 4200 ед. урона от огня противникам в пределах 10 м."},
[52491] = {name = "Паутинный взрыв", description = "[q]Обездвиживание ближайших противников на 4 sec."},
[52493] = {name = "Брызги яда", description = "[q]Наносит урон от сил природы, а затем дополнительный урон раз в 3 сек. в течение 10 sec."},
[52495] = {name = "Стихийное просветление", description = "[q]Увеличивает урон от заклинаний огня и льда на 20% на 2 min."},
[52496] = {name = "Могильные скарабеи", description = "[q]Призывает несколько некрорахнидов-скарабеев, помогающих заклинателю атаковать его цель."},
[52497] = {name = "Скопление газов", description = "[q]Сбивает с ног находящихся поблизости дробителей черепов Драккари и наносит урон противнику раз в 1 сек. в течение 2 sec."},
[52501] = {name = "Гнев", description = "[q]Противника поражает молния, нанося ему урон от сил природы."},
[52502] = {name = "Лунный огонь", description = "[q]Нанесение противнику урона от тайной магии и дополнительного урона в течение 12 sec."},
[52503] = {name = "Мощная аура лунного совуха", description = "[q]Увеличение вероятности критического эффекта на 10%."},
[52504] = {name = "Растерзать", description = "[q]Разрывает противника на части, нанося ему 463 to 537 ед. урона в момент удара и еще 750 ед. урона за счет кровотечения, продолжающегося 15 sec. Урон увеличивается при увеличении силы атаки. Удар значительно повышает уровень угрозы. Эффект суммируется до 5 раз на одной цели."},
[52506] = {name = "Трепка", description = "[q]Увеличение урона последующей атаки на 150 ед."},
[52507] = {name = "Присутствие Яролапа", description = "[q]Получаемый урон уменьшен на 10%."},
[52508] = {name = "Треск по всем швам", description = "[q]Вызывает взрыв поганища, убивающий всех, кто находится рядом с дробителями черепов Драккари."},
[52519] = {name = "Вурдалакотрясение", description = "[q]Мгновенно убивает непокорных вурдалаков, включая тех, которые считают возможным пререкаться с Готиком."},
[52521] = {name = "Подчинение вурдалака", description = "[q]Подчинение непокорного вурдалака."},
[52522] = {name = "Гемотоксин", description = "[q]Отравление цели и потеря ей 8% здоровья раз в 2 сек."},
[52524] = {name = "Опутывающая паутина", description = "[q]Выстреливает паутиной в глаза окружающих противников, ослепляя и дезориентируя их на 4 sec."},
[52525] = {name = "Болезнетворное облако", description = "[q]Гнилостное облако заразы окружает заклинателя, нанося 1000 ед. урона от сил природы находящимся поблизости противникам раз в 2 сек."},
[52527] = {name = "Гадостная желчь", description = "[q]Покрытие цели отвратительной пленкой желчи со снижением урона и исцеления, произведенного целью на 35% и нанесением 700 ед. урона от сил природы раз в 3 с в течение 15 sec."},
[52531] = {name = "Струя пара", description = "[q]Наносит урон от огня противникам, находящимся в секторе поражения перед заклинателем."},
[52532] = {name = "Разящий удар", description = "[q]Удар по противнику с нанесением увеличенного урона в ближнем бою."},
[52534] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[52535] = {name = "Кольцо Тьмы", description = "[q]Нанесение урона от темной магии находящимся рядом противникам."},
[52538] = {name = "Рывок", description = nil},
[52539] = {name = "Пушечное ядро", description = "[q]Бросок огненного шара, который наносит противнику 1 ед. урона от огня."},
[52540] = {name = "Удар в спину", description = "[q]Наносит противнику обычный и 300 ед. дополнительного урона при нападении со спины."},
[52551] = {name = "Жизнецвет", description = "[q]Восстанавливает 232 to 268 ед. здоровья каждую секунду и от 1573 до 1827 ед. здоровья в случае окончания действия или рассеивания эффекта."},
[52553] = {name = "Усиленное Древо Жизни", description = "[q]Создает ауру вокруг находящихся рядом союзников, увеличивающую эффекты лечения на 10%."},
[52554] = {name = "Покровительство Природы", description = "[q]Призывает силы природы для исцеления союзника."},
[52560] = {name = "Призывание крови древнего бога", description = "[q]Призывает кровь древнего бога на помощь Урсоку."},
[52564] = {name = "Порча древнего бога", description = "[q]Моментально наносит 1110 to 1290 ед. урона от темной магии и дополнительно 400 ед. урона от темной магии раз в 1 сек. в течение 6 sec."},
[52566] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[52576] = {name = "Электромагнитный импульс", description = "[q]Использование энергии пушки Алого ордена. Мощный залп электромагнитного импульса сотрет с лица земли всех противников на близком расстоянии."},
[52581] = {name = "Разламывание доспеха", description = "[q]Ослабление брони противника на 75% на 6 sec."},
[52583] = {name = "Влияние древнего бога", description = "[q]Под влиянием древнего бога. Цель периодически оглушается на 2 sec., и ей наносится 1018 to 1182 ед. урона от темной магии."},
[52586] = {name = "Пытка разума", description = "[q]Нанесение урона от темной магии противнику и снижение скорости его передвижения на 3 sec."},
[52592] = {name = "Проклятие усталости", description = "[q]Проклинает всех противников в радиусе 5 м от цели, нанося 2828 to 3172 ед. урона от темной магии, снижая скорость передвижения на 30%, скорость атаки на 30% и скорость произнесения заклинаний на 30%. Время действия – 10 sec."},
[52601] = {name = "Стремительный напор", description = "[q]Рывок к выбранному послушнику Мам'тота на расстояние до 5 to 45 м. Нельзя использовать, если цель находится ближе, чем в 5 м от вас."},
[52603] = {name = "Тяжелый шаг", description = "[q]Наносит от 4375 до 5625 ед. урона 4-м ближайшим послушникам Мам'тота."},
[52604] = {name = "Укус скорпида", description = "[q]Снижает вероятность того, что противник попадет по цели, на 5% на 15 sec. На каждую цель может действовать только один \"Укус\" каждого охотника единовременно."},
[52606] = {name = "Обжигающая ловушка", description = "[q]Устанавливает огненную ловушку, которая обжигает первого приблизившегося к ней противника, нанося ему 36 ед. урона от огня в течение 30 sec. Ловушка будет существовать 30 sec. Единовременно может быть установлена только одна ловушка."},
[52608] = {name = "Бросок светильника", description = "[q]Метает фонарь в противника, нанося 213 to 287 ед. урона от огня и оставляя горящий след под ногами цели."},
[52611] = {name = "Призыв скелетов", description = "[q]Рядом с заклинателем появляются два скелета."},
[52613] = {name = "Ледяной валун", description = nil},
[52624] = {name = "Пепелище", description = "[q]Повышает вероятность нанесения заклинателем критического удара в ближнем бою на 50% на 10 sec."},
[52626] = {name = "Расплавленная броня", description = "[q]Ослабление брони находящихся поблизости противников на 1000 ед. на 30 sec."},
[52628] = {name = "Руна возмездия", description = "[q]Увеличение наносимого урона 5% при получении урона. Этот эффект может срабатывать не чаще чем раз в 2 сек. и суммироваться до 5 раз."},
[52629] = {name = "Воздаяние", description = "[q]Увеличивает наносимый урона на 5%, эффект суммируется до 5 раз."},
[52630] = {name = "Боевые руны", description = "[q]Атакующим наносится 150 ед. физического урона."},
[52633] = {name = "Удар Пустоты", description = "[q]Нанесение противнику 125% урона от оружия, вопреки его броне."},
[52634] = {name = "Щит Бездны", description = "[q]Из щита заклинателя выделяется пузырь Бездны, уменьшая весь получаемый урон на 50% в течение 8 sec."},
[52635] = {name = "Увеличение скорости", description = "[q]Увеличение скорости атаки ближнего боя и произнесения заклинаний на 10%. Эффект суммируется до 20 раз."},
[52636] = {name = "Временной сдвиг", description = "[q]Телепортирует заклинателя в случайно выбранную точку неподалеку, снимая с заклинателя все воздействующие на него негативные эффекты."},
[52640] = {name = "Сила кузни", description = "[q]Нанесение 1203 to 1397 ед. физического урона противникам в конусе поражения перед заклинателем, оглушение противника на 3 sec."},
[52643] = {name = "Затупление оружия", description = "[q]Шанс затупить оружие противника в ближнем бою, уменьшая наносимый им урон."},
[52651] = {name = "Щит молний", description = "[q]20 шаровых молний, окружающих заклинателя. 25%-я вероятность нанести 250 ед. урона противникам, атакующим в ближнем или дальнем бою. Каждый раз, как щит срабатывает, одна из молний рассеивается. Таким образом, щит перестает действовать через 30 sec., либо после 20 срабатываний."},
[52657] = {name = "Временная воронка", description = "[q]Направляет воронку на противника, нанося ему 600 ед. урона от тайной магии раз в 1 сек. и снижая скорость передвижения, скорость атаки и скорость произнесения заклинаний на 70% на 5 sec."},
[52658] = {name = "Статическая перегрузка", description = "[q]Энергетический заряд наполняет противника, нанося 1249 to 1451 ед. урона от сил природы в течение 10 sec. Пока цель под влиянием заклинания, заряд периодически поражает ее ближайших союзников, нанося 1249 to 1451 ед. урона от сил природы. Через 10 sec. заряд провоцирует взрыв, наносящий 2313 to 2687 ед. урона от сил природы всем противникам в радиусе 8 м и отбрасывающий их назад."},
[52660] = {name = "Чародейская волна", description = "[q]На противников налетает магическая волна, нанося 2590 to 3010 ед. урона от тайной магии."},
[52666] = {name = "Облако заразы", description = "[q]Мясной Крюк изрыгает облако болезни, наносящее 713 to 787 ед. урона от сил природы и прерывающее процесс использования заклинаний окружающими противниками на 4 sec."},
[52671] = {name = "Искрящее пламя", description = "[q]Наносит цели 602 to 698 ед. урона от сил природы и повышает получаемый урон от сил природы на 2%."},
[52672] = {name = "Вурдалакотрясение", description = "[q]Мгновенно убивает мелких существ Плети, выбранных заклинателем. Обычно это выглядит весьма впечатляюще."},
[52694] = {name = "Возвращение Ока Акеруса", description = "[q]Отзовите Око Акеруса и возвращайтесь домой."},
[52695] = {name = "Кукла вуду", description = "[q]Нанесение 833 to 967 ед. физического урона."},
[52696] = {name = "Удушающие оковы", description = "[q]Сковывает цели, нанося 1000 ед. физического урона раз в 1 с и оглушая цель на 5 sec."},
[52699] = {name = "Выплавка руны", description = "[q]Исполнение цели руны Тордона."},
[52701] = {name = "Заряд молнии", description = "[q]Атака с вероятностью нанести урон от сил природы."},
[52702] = {name = "Клеймление", description = "[q]Сильный удар, наносящий 300% урона от оружия и отпечатывающий на коже цели руну."},
[52703] = {name = "Заряженный сторожевой тотем", description = "[q]Создание тотема молний со временем существования 14 sec., который периодически поражает находящихся поблизости противников."},
[52705] = {name = "Ограждающий разряд", description = "[q]Нанесение урона от сил природы противникам, находящимся в конусе перед заклинателем."},
[52708] = {name = "Похищение плоти", description = "[q]Похищение плоти враждебной цели, при котором урон, наносимый противником, уменьшается на 75%, а урон, наносимый заклинателем, увеличивается на 75%."},
[52710] = {name = "Заряд молнии", description = "[q]Атака с вероятностью нанести 24 to 29 ед. урона от сил природы."},
[52713] = {name = "Соткание рун", description = "[q]Нанесение 195 to 225 ед. урона от темной магии раз в 1 сек. и обездвиживание цели на 4 sec."},
[52714] = {name = "Исцеляющий луч", description = "[q]Исцеляет союзника от 2500 ед. урона через 5 sec."},
[52715] = {name = "Руна разрушения", description = "[q]Нанесение на противника огненной руны на 5 sec. с нанесением урона спустя 5 сек."},
[52716] = {name = "В ужасе", description = "[q]В ужасе!"},
[52717] = {name = "Гром и молния", description = "[q]Вы вызываете молнию, наносящую урон всем противникам в радиусе 10 м. Молния наносит 747 to 853 ед. урона от сил природы ближайшим противникам, откидывая их на 85.3 м."},
[52719] = {name = "Оглушающий удар", description = "[q]Жестокий удар, наносящий противнику урон от оружия и оглушающий его на 3 sec."},
[52720] = {name = "Темная стая", description = "[q]Огромная волна хаотический магии обрушивается на противников перед заклинателем, нанося 3230 to 3570 ед. урона от темной магии и еще 380 to 420 ед. урона от темной магии дополнительно раз в 3 сек. в течение 15 sec."},
[52721] = {name = "Сон", description = "[q]Усыпляет противника на 10 sec. Любой полученный урон пробуждает цель."},
[52722] = {name = "Взрыв разума", description = "[q]Нанесение 4163 to 4837 ед. урона от темной магии."},
[52723] = {name = "Прикосновение вампира", description = "[q]Восполняет здоровье заклинателя в размере половины урона, нанесенного в ближнем бою."},
[52740] = {name = "Бросок оружия", description = "[q]Бросок оружия, наносящий 2040 to 2760 ед. физического урона цели и 5425 ед. дополнительного урона в течение 21 sec."},
[52743] = {name = "Удар по голове", description = "[q]Нанесение противнику 13 to 16 ед. урона и оглушение его на 8 sec. Ваша атака автоматически прервется, и цель должна будет находиться перед вами. При получении оглушенной целью любого повреждения она вновь придет в себя."},
[52744] = {name = "Пронзительный вой", description = "[q]Вызывает головокружение у всех противников, находящихся рядом с воином, на 6 sec."},
[52754] = {name = "Импульсный патрон", description = "[q]Стреляет в противника, нанося физический урон.\n\n Выстрел отбрасывает жертву."},
[52755] = {name = "Многократный залп", description = "[q]Выпускает град стрел в противника и его ближайших союзников, нанося 2040 to 2760 ед. физического урона до 3 целей и отбрасывая их назад."},
[52758] = {name = "Пронзающая стрела", description = "[q]Выстрел, наносящий противнику физический урон."},
[52761] = {name = "Колючая сеть", description = "[q]Обездвиживание противника на 6 sec. и нанесение физического урона."},
[52766] = {name = "Искажение времени", description = "[q]Время замедляется, уменьшая скорость атаки, произнесения заклинаний и передвижения на 70% на 6 sec."},
[52770] = {name = "Рассеяние", description = "[q]Рассеять на искры Ионара"},
[52771] = {name = "Ранящий удар", description = "[q]Жестокое ранение цели – нанесение 100% урона от оружия, уменьшение эффекта лечения на 25% и нанесение 750 ед. физического урона раз в 1 сек. в течение 4 sec."},
[52772] = {name = "Проклятье усталости", description = "[q]Увеличение расхода маны на заклинания и способности на 100% на 10 sec."},
[52773] = {name = "Выпад молотом", description = "[q]Нанесение урона, равного 125% урона оружия и снижение скорости передвижения на 65% на 5 sec."},
[52774] = {name = "Обновление стали", description = "[q]Исцеляет 10% раз в 2 сек. в течение 6 sec. Используется только на железных врайкулах."},
[52778] = {name = "Сварочный луч", description = "[q]Нанесение противнику урона от сил природы в течение 5 sec."},
[52780] = {name = "Шаровая молния", description = "[q]Метает шаровую молнию в цель, нанося противнику 2013 to 2587 ед. урона от сил природы."},
[52791] = {name = "Пламя рождения", description = "[q]Сильный жар пламени позволяет созреть яйцу доисторического дракона."},
[52792] = {name = "Пламя рождения", description = "[q]Подготовка к проклевыванию."},
[52812] = {name = "Раскаленное неистовство", description = "[q]Наносит 1143 to 1397 ед. урона от огня и дополнительно 833 to 967 ед. урона от огня раз в 1 сек. в течение 5 sec. Оглушенная цель получает двойной урон."},
[52813] = {name = "Залп", description = "[q]Обрушивает на противника и его ближайших союзников град снарядов и поражает до 3 целей, нанося им 278 to 322 ед. дополнительного урона."},
[52814] = {name = "Арктические ветра", description = "[q]Холодный порыв ветра наносит 2035 to 2365 ед. урона от сил природы."},
[52835] = {name = "Рассекающий удар", description = "[q]Наносит урон от оружия, усиленный на 438 to 562 ед., противнику и его ближайшим союзникам. Захватывает до 4 целей."},
[52836] = {name = "Гнев небес", description = "[q]Посылает во все стороны заряды святой энергии, наносящие суммарно 818 to 1597 ед. урона от светлой магии демонам и нежити в радиусе 20 м."},
[52856] = {name = "Рывок", description = "[q]Рывок к противнику, наносящий обычный урон, оглушающий противника на 2 sec. и отбрасывающий назад всех противников в зоне действия."},
[52859] = {name = "Подчинение", description = "[q]Заставляет Акали успокоиться и подчиниться вашей воле, возвращая его за решетку в Гундраке."},
[52862] = {name = "Поглотить ветер", description = "[q]Поглотить раненого загробника бури, забирая его силы."},
[52864] = {name = "Поглотить воду", description = "[q]Поглощает раненого духа дождя, забирая его силы."},
[52869] = {name = "Прилив сил", description = "[q]Восполнение 10% маны в секунду в течение 10 sec. Урон, полученный во время использования заклинания \"Прилив сил\", повышается на 10%."},
[52870] = {name = "Ветрорез", description = "[q]Наносит 1125 to 1875 ед. урона противникам в конусе поражения перед заклинателем, отбрасывая их назад."},
[52873] = {name = "Открытая рана", description = "[q]Наносит физический урон противнику раз в 3 сек. и повышает получаемый им урон на 15% в течение 15 sec."},
[52878] = {name = "Одурманивание", description = "[q]Дурманит."},
[52879] = {name = "Отражение", description = "[q]Повышает вероятность парирования атак на 25%."},
[52883] = {name = "Контратака", description = "[q]Атака, обходящая защиту цели, заставляя ее бесцельно бродить в течение 8 sec. Может использоваться только после успешного парирования атаки."},
[52884] = {name = "Похищение мощи", description = "[q]Похищает остатки мощи Акали. Большая ее часть отправится к его хозяину, в Гулдрак под решеткой, но часть заберет себе пророк. В конце заклинания Акали умрет."},
[52885] = {name = "Смертельный бросок", description = "[q]Бросает кинжал в противника, нанося 2400%-й урон от оружия. Кроме того, прерывает процесс произнесения заклинаний этой школы на 3 sec."},
[52886] = {name = "Шипастая ловушка", description = "[q]Установка шипастой ловушки, которая взрывается при приближении, нанося серьезный урон находящимся поблизости противникам."},
[52889] = {name = "Отравляющий выстрел", description = "[q]Вызывает у противника головокружение, снижая скорость его передвижения на 50% на 5 sec."},
[52890] = {name = "Пронзающий удар", description = "[q]Удар, игнорирующий броню противника. Наносит 100% урона от оружия."},
[52891] = {name = "Отражение клинков", description = "[q]Уменьшает урон от атак ближнего и дальнего боя на 1000. Время действия – 4 sec."},
[52893] = {name = "Зона антимагии", description = "[q]Создание большой неподвижной антимагической зоны, которая уменьшает урон от заклинаний, получаемый членами группы внутри нее, на 75%. Антимагическая зона существует в течение 10 sec. или пока не поглотит 10000 ед. урона от заклинаний."},
[52895] = {name = "Бинты", description = "[q]Восполнение 3000 ед. здоровья в течение 6 sec."},
[52896] = {name = "Подавление чар", description = "[q]Уменьшает урон от заклинаний на 10% после получения критического удара от заклинания. Время действия – 4 sec."},
[52904] = {name = "Бросок", description = "[q]Бросок оружия в противника, наносящий 3500 ед. физического урона."},
[52905] = {name = "Громовая стрела", description = "[q]Противника поражает удар молнии, которая перескакивает на ближайших неприятелей. Заклинание поражает цель и находящихся поблизости ее союзников, нанося 2775 to 3225 ед. урона от сил природы каждой цели."},
[52909] = {name = "Подводное дыхание", description = "[q]Возможность дышать под водой для всех находящихся поблизости участников группы в течение 30 min."},
[52919] = {name = "Отравляющее облако", description = "[q]Раз в 2 сек. наносится урон от сил природы."},
[52921] = {name = "Дуговая молния", description = "[q]Мгновенное поражение цели молнией с нанесением 3238 to 3762 ед. урона от сил природы; состояние шока может передаться ближайшим союзникам цели."},
[52922] = {name = "Инквизиторская кара", description = "[q]Священная молния, отбрасывающая назад всех в радиусе действия."},
[52926] = {name = "Кара Валрота", description = "[q]Удар по противнику, нанесение урона от светлой магии."},
[52931] = {name = "Токсичный плевок", description = "[q]Наносит урон от сил природы, а затем дополнительный урон раз в 5 сек. в течение 10 sec."},
[52940] = {name = "Пора спать!", description = "[q]Восполнение 600 ед. здоровья в течение 6 sec."},
[52943] = {name = "Вихрь молний", description = "[q]Налететь вихрем на противника и поразить его болезненным разрядом молнии."},
[52944] = {name = "Поражение молнией", description = "[q]Призывает молнию, наносящую урон от сил природы и отбрасывающую всех ближайших противников."},
[52960] = {name = "Вспышка молнии", description = "[q]Выброс энергии, наносящий 6013 to 6987 ед. урона от сил природы противникам в пределах 20 м."},
[52964] = {name = "Искра бешенства", description = "[q]От заклинателя во все стороны летят искры, каждая из которых наносит противникам 18 to 21 ед. урона от сил природы."},
[52968] = {name = "Гнев Крови сердца", description = "[q]Увеличение скорости атаки заклинателя на 60% и наносимого им физического урона на 25 sec."},
[52969] = {name = "Вытягивание энергии", description = "[q]Вытягивает энергию из тел ближайших противников, восстанавливая здоровье заклинателя."},
[52972] = {name = "Рассеивание", description = "[q]Защита от всех видов физического урона и от всех заклинаний. Время действия – 4 сек."},
[52973] = {name = "Ледяное дыхание", description = "[q]Наносит урон от магии льда противникам, находящимся перед заклинателем, обездвиживая их на 6 sec."},
[52974] = {name = "Злобный рев", description = "[q]Снижение силы атаки ближнего боя находящихся рядом противников на 15 sec."},
[52977] = {name = "Вихрь", description = "[q]В вихре стали атакует ближайших противников, нанося каждому обычный урон. Атака значительно повышает уровень угрозы."},
[52979] = {name = "Поглощающий холод", description = "[q]Восстановление 5% здоровья каждую секунду в течение 8 сек. и дополнительные 5% здоровья при любом уроне, полученном во время действия заклинания."},
[52982] = {name = "Неуязвимость Акали", description = "[q]Наделяет небольшой частью силы Акали, которая дает полную неуязвимость ко всем видам урона и воздействия."},
[52989] = {name = "Оглушение Акали", description = "[q]Сила Акали лишает ваших противников возможности сопротивляться."},
[52993] = {name = "Ритуал уничтожения пророка Акали", description = "[q]Полное уничтожение души и тела противника. Необратимо."},
[53004] = {name = "Воля Хар'коа", description = "[q]Заставляет того, на кого направлено заклинание, подчиниться воле Хар'коа."},
[53029] = {name = "Вызов Хар'коа", description = "[q]Хар'коа бросает вызов пророку Акали."},
[53030] = {name = "Ядовитое похищение", description = "[q]Похищение 500 ед. здоровья у окружающих противников раз в секунду. Если одна из целей умирает во время действия эффекта, заклинатель восстанавливает 10% здоровья. Время действия – 10 sec."},
[53032] = {name = "Шквал когтей", description = "[q]Су-Холу дает волю своему гневу, неистово сражаясь со своими противниками в течение 10 sec."},
[53039] = {name = "Раскрытие парашюта", description = "[q]Задействует парашют, позволяющий не разбиться при падении на землю."},
[53043] = {name = "Электрошок", description = "[q]Приведение противника в состояние шока и нанесение ему урона от сил природы раз в 3 сек. в течение 12 sec."},
[53044] = {name = "Молния", description = "[q]Поражение противника молнией, урон от сил природы."},
[53045] = {name = "Сон", description = "[q]Усыпляет противника на 4 sec. Любой полученный урон пробуждает цель. Одновременно может быть усыплен только один противник."},
[53047] = {name = "Яростный вопль", description = "[q]Яростный рев, увеличивающий наносимый урон на 50%."},
[53048] = {name = "Пугающий рев", description = "[q]Снимает все эффекты сковывания, оглушения и контроля с ближайших союзников."},
[53049] = {name = "Шквальная атака", description = "[q]На ближайших противников обрушивается шквал ударов топором в течение 6 sec."},
[53052] = {name = "Исчезновение", description = "[q]Делает заклинателя невосприимчивым к враждебным атакам и заклинаниям, но он при этом не может двигаться и атаковать. Длительность – 20 sec."},
[53059] = {name = "Отравленное копье", description = "[q]Нанесение противнику физического урона раз в 3 сек. в течение 12 sec."},
[53068] = {name = "Клеймление", description = "[q]Сильный удар, наносящий 3150 to 3850 ед. урона и отпечатывающий на коже цели руну."},
[53070] = {name = "Приказ воргена", description = "[q]Контроль над сознанием цели. Время действия – 20 sec. или до момента падения здоровья цели ниже 35%."},
[53071] = {name = "Гром и молния", description = "[q]Удар молнии наносит всем противникам в радиусе 15 м 3700 to 4300 ед. урона от сил природы и отбрасывает их назад."},
[53072] = {name = "Грозовой удар", description = "[q]Наносит 3238 to 3762 ед. урона противнику."},
[53086] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[53093] = {name = "Серебряная пуля", description = "[q]Выстрел в противника пулей из истинного серебра."},
[53094] = {name = "Укус заразного воргена", description = "[q]Заражение противника, нанесение 150 ед. урона от сил природы раз в 5 сек., причем эффект суммируется до 3 раз. При уровне заражение 3 цель превращается в воргена."},
[53096] = {name = "Правосудие Кетц'лун", description = "[q]Признание драккари недостойным дальнейшего существования. Разумеется, до перевоплощения."},
[53110] = {name = "Пожирание гуманоида", description = "[q]Пожирание крестоносца Алого ордена для восстановления маны ледяного покорителя."},
[53114] = {name = "Проклятие заморозки", description = "[q]Выстреливает в противников проклятием заморозки, нанося 13125 to 16875 ед. урона всем людям и 35000 to 45000 ед. урона – баллистам."},
[53117] = {name = "Атака баллисты", description = "[q]Выстреливает метательным копьем, которое, достигнув цели, взрывается, нанося 9901 to 14099 ед. урона всем противникам в зоне действия заклинания."},
[53167] = {name = "Раздвоенная молния", description = "[q]Нанесение урона от сил природы противникам, находящимся в конусе перед заклинателем."},
[53177] = {name = "Главный вход в Паутину", description = nil},
[53185] = {name = "Боковой вход в Паутину", description = nil},
[53210] = {name = "Конец света", description = "[q]Падение небес на землю."},
[53218] = {name = "Ледяная хватка", description = "[q]Значительно уменьшает скорость передвижения."},
[53229] = {name = "Неистовая буря", description = "[q]Создание в указанной области неистового шторма, наносящего противникам 3700 to 4300 ед. урона от сил природы раз в 2 секунд и увеличивающего время между их атаками на 67%. Время действия – 15 sec."},
[53239] = {name = "Метание топора", description = "[q]Бросок нескольких топоров."},
[53273] = {name = "Взрыв приманки", description = "[q]Ранит противников разлетевшимися осколками, нанося им 1440 to 2160 ед. физического урона."},
[53278] = {name = "Вурдалакотрясение", description = "[q]БАБАХ!"},
[53314] = {name = "Молния", description = "[q]Поражение противника молнией, урон от сил природы."},
[53317] = {name = "Кровопускание", description = "[q]Нанесение противнику физического урона раз в 3 сек. в течение 15 sec."},
[53318] = {name = "Мощный удар", description = "[q]Наносит мощный удар цели, причиняя 5550 to 6450 ед. урона и подбрасывая вверх."},
[53322] = {name = "Сокрушающая сеть", description = "[q]Набрасывание паутины на противника, обездвиживая его и нанося 200 ед. урона в сек. в течение 6 sec."},
[53330] = {name = "Зараженная рана", description = "[q]Увеличение физического урона, получаемого противником, на 20 sec."},
[53333] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[53334] = {name = "Оживление костей", description = "[q]Швыряет кучу костей и оживляет их. Создает один комплект оживших костей раз в 3 сек. в течение 9 sec."},
[53345] = {name = "Поток стрел", description = "[q]Стрельба взрывной стрелой, наносящей 438 to 562 ед. урона всем противникам в радиусе 4 м."},
[53350] = {name = "Умиротворяющий туман", description = "[q]Наполнение воздуха холодным туманом, тушение эффектов пламени и исцеление находящихся поблизости друзей."},
[53361] = {name = "Бешенство", description = "[q]Повышает скорость атаки на 50% на 12 sec."},
[53363] = {name = "Взмах крылом", description = "[q]Наносит 9000 to 15000 ед. урона всем противникам в пределах сектора перед заклинателем, отбрасывая их назад и снижая скорость передвижения."},
[53394] = {name = "Зуботычина", description = "[q]Нанесение противнику 943 to 1057 ед. урона и лишение его на 5 sec. возможности произносить заклинания."},
[53395] = {name = "Удар героя", description = "[q]Сильная атака, которая наносит 2625 to 3375 ед. физического урона."},
[53399] = {name = "Сбивающий удар", description = "[q]Наносит противникам в секторе перед заклинателем обычный урон, усиленный на 50 ед., и сбивает их с ног."},
[53400] = {name = "Едкое облако", description = "[q]Едкое вещество, распыленное в районе нахождения цели, образует облако, наносящее 707 to 793 ед. урона от сил природы в секунду всем противникам, попавшим в зону его действия. Время действия – 1.50 min."},
[53401] = {name = "Раж", description = "[q]Ваш питомец впадает в боевое бешенство. Его удары, достигшие цели, могут повысить силу атаки еще на 5%. Эффект суммируется до 5 раз. Время действия – 20 sec."},
[53406] = {name = "Паукохват", description = "[q]Выпуск липкой паутины, наносящей 2357 to 2643 ед. урона, связывающей противников и подтягивающей их к заклинателю."},
[53418] = {name = "Пронзенная броня", description = "[q]Ослабление брони противника на 50% на 8 sec."},
[53425] = {name = "Бросок шкуры медведя", description = "[q]Нанесение противнику 693 to 806 ед. физического урона."},
[53426] = {name = "Зализывание ран", description = "[q]Ваш питомец восстанавливает 100% здоровья в течение 5 sec. при поддержании эффекта."},
[53432] = {name = "Капкан на медведя", description = "[q]Размещение медвежьего капкана, который обездвиживает первого приблизившегося противника, нанося 370 to 430 ед. урона от кровотечения в течение раз в 2 с в течение 5 sec. Нанесение урона может прервать действие ловушки. Ловушка устанавливается на 1 min. Единовременно может быть установлена только одна ловушка."},
[53434] = {name = "Зов дикой природы", description = "[q]Ваш питомец издает рык, увеличивающий его и вашу силу атаки ближнего и дальнего боя на 10%. Время действия – 20 sec."},
[53437] = {name = "Спинолом", description = "[q]Нанесение противнику урона от оружия и оглушение на 4 sec."},
[53438] = {name = "Наведение ужаса", description = "[q]Заставляет противника-цель бежать в ужасе в течение 2 sec. и наносит 450 ед. урона от темной магии раз в 1 сек."},
[53454] = {name = "Прокалывание", description = "[q]Выстреливает сквозь пол шип, пронзающий все цели, находящиеся в радиусе 4 м, наносящий 2828 to 3172 ед. урона и подбрасывающий в воздух. Этот удар игнорирует броню противника."},
[53463] = {name = "Возврат плоти", description = "[q]Возвращение плоти цели"},
[53467] = {name = "Жуки-трупоеды", description = "[q]Призывает рой жуков-трупоедов для защиты заклинателя. Эти жуки наносят 330 to 370 ед. урона от сил природы раз в секунду всем противникам в радиусе 60 м. Длительность – 15 sec."},
[53472] = {name = "Удар", description = "[q]Сокрушает всех противников в пределах 15 м перед заклинателем. Наносит 9425 to 10575 ед. урона и втаптывает их в землю на 6 sec."},
[53476] = {name = "Вмешательство", description = "[q]Питомец бросается к участнику вашей группы, снижая урон от следующей направленной против этого участника атаки на 50% от максимального запаса своего здоровья."},
[53477] = {name = "Провокация", description = "[q]Ваш питомец провоцирует противника на атаку в течение 3 sec."},
[53478] = {name = "Ни шагу назад", description = "[q]Максимальный запас здоровья вашего питомца повышается на 30% на 20 sec. По окончании действия эффекта дополнительное здоровье теряется."},
[53480] = {name = "Рев самопожертвования", description = "[q]Защищает союзника от критических ударов. Атаки против него не могут иметь критического эффекта, но 20% всего урона также получает питомец. Время действия – 12 sec."},
[53490] = {name = "Ослиное упрямство", description = "[q]Снимает все замедляющие передвижение эффекты и эффекты, вызывающие потерю контроля над питомцем. Уменьшает урон, получаемый питомцем, на 20% на 12 sec."},
[53493] = {name = "Испепеление", description = "[q]Нанесение 1480 to 1720 ед. урона от огня вашей цели."},
[53496] = {name = "Языки пламени", description = "[q]Из рук заклинателя вырываются языки пламени, нанося урон от огня ближайшим противникам."},
[53498] = {name = "Размах", description = "[q]Яростный замах, наносящий урон цели и трем находящимся поблизости противникам."},
[53499] = {name = "Глубокая рана", description = "[q]Наносит противнику 880 to 1320 ед. урона и дополнительно 1005 ед. урона в течение 9 sec."},
[53504] = {name = "Вспышки Пламени", description = "[q]Нанесение 200 ед. урона от огня раз в 3 сек. в течение 24 sec.\n Умиротворяющий туман ослабляет этот эффект."},
[53508] = {name = "Укус росомахи", description = "[q]После того как ваш питомец наносит критический удар, он может яростно укусить противника, нанеся [1 + ((Ranged attack power * 0.40) * 0.10)] ед. урона. Эту атаку невозможно парировать или блокировать, от нее нельзя уклониться."},
[53517] = {name = "Рев восстановления", description = "[q]Рев вашего питомца восстанавливает вам 30 ед. концентрации в течение 9 sec."},
[53520] = {name = "Жуки-падальщики", description = "[q]Призыв 2 жуков-падальщиков каждую секунду на 4 sec."},
[53536] = {name = "Воющий ветер", description = "[q]Наносит 278 to 302 ед. урона от магии льда всем противникам в радиусе 10 м. Наносит тройной урон замороженным целям."},
[53549] = {name = "Ледяное прикосновение", description = "[q]Наносит цели от 86 до 92 ед. урона от магии льда (величина зависит от силы атаки) и снижает скорость ближнего и дальнего боя и скорость произнесения заклинаний противника на 86 to 92% на until cancelled."},
[53550] = {name = "Заморозка разума", description = "[q]Охватывает холодом разум противника, нанося 100 ед. урона от магии льда (зависит от силы атаки ), прерывая применение заклинания и предотвращая использование заклинаний этой школы магии в течение 4 sec."},
[53570] = {name = "Ненасытная стужа", description = "[q]Использует всю свободную силу рун, чтобы вытянуть все тепло из земли вокруг рыцаря смерти. Враги на расстоянии 10 м примерзают к месту, не могут производить никаких действий в течение 1 секунды за каждые 5 ед. силы рун. Противники считаются замороженными, но любой урон разобьет ледяную ловушку."},
[53602] = {name = "Дротик", description = "[q]Осыпание противника дротиками и нанесение 750 ед. урона раз в 3 сек. Время действия – 12 sec."},
[53605] = {name = "Вырождение", description = "[q]Мгновенная атака на цель, наносящая 60% урона от оружия и заражающая болезнью, наносящей 1400 ед. урона в течение 21 sec. Все существующие способы исцеления действуют неправильно, значительно увеличивая потерю здоровья от болезни и не оказывая лечащего действия."},
[53607] = {name = "Омоложение", description = "[q]Исцеление союзника раз в 3 сек. в течение 12 sec."},
[53608] = {name = "Жизнецвет", description = nil},
[53616] = {name = "Ядовитый град", description = "[q]Наносит урон от сил природы, а затем дополнительный урон раз в 3 сек. в течение 9 sec."},
[53617] = {name = "Ядовитый удар", description = "[q]Наносит урон от сил природы, а затем дополнительный урон раз в 3 сек. в течение 9 sec."},
[53618] = {name = "Раскол брони", description = "[q]Ослабляет вражескую броню при каждом расколе. Суммируется до 20 раз. Время действия – 20 sec."},
[53625] = {name = "Героический прыжок", description = "[q]Рывок к противнику, наносящий обычный урон, увеличенный на 0 ед., и оглушающий цель на 2 sec."},
[53629] = {name = "Апперкот", description = "[q]Нанесение противнику увеличенного на 150 ед. стандартного урона оружия и отбрасывание его назад."},
[53630] = {name = "Гром", description = "[q]Вы вызываете молнию, наносящую урон всем противникам в радиусе 10 м. Молния наносит 14003 to 15997 ед. урона от сил природы ближайшим противникам, откидывая их на 1599.7 м."},
[53631] = {name = "Рассекающий удар", description = "[q]Наносит урон от оружия, усиленный на 30 ед., противнику и его ближайшим союзникам, поражая до 5 целей и отбрасывая их назад."},
[53632] = {name = "Вурдалакотрясение", description = "[q]БАБАХ!"},
[53633] = {name = "Рассекающий удар", description = "[q]Наносит урон от оружия, усиленный на 30 ед., противнику и его ближайшим союзникам, поражая до 5 целей и отбрасывая их назад."},
[53634] = {name = "Топот", description = "[q]Топот, наносящий физический урон всем противникам на большом расстоянии и подкидывающий их в воздух."},
[53635] = {name = "Объятия смерти", description = "[q]Наносит всем ближайшим противникам 13125 to 16875 ед. урона и исцеляет всех ближайших союзников на 13125 to 16875."},
[53638] = {name = "Гнев небес", description = "[q]Посылает во все стороны заряды святой энергии, наносящие 765 to 935 ед. урона от светлой магии всем противникам в радиусе 0 м. С каждым прыжком урон удваивается."},
[53639] = {name = "Удар смерти", description = "[q]Мгновенная атака на цель, наносящая 400% урона от оружия и заражающая болезнью, наносящей 8400 ед. урона в течение 21 sec. Все существующие способы исцеления действуют неправильно, значительно увеличивая потерю здоровья от болезни и не оказывая лечащего действия."},
[53640] = {name = "Нечестивая порча", description = "[q]Злобный рой нечестивых насекомых окружает заклинателя в радиусе 10 м. Наносит всем противникам в этом радиусе 0 ед. урона в секунду и поражает их болезнью, причем эффект может суммироваться до 3 раз. Время действия – 1 сек. за каждые 10 ед. силы рун. Накопление силы рун во время эффекта может его продлить."},
[53642] = {name = "Мощь Могрейна", description = "[q]Вас вдохновляет мощь Верховного лорда Дариона Могрейна. Наносимый вами урон, здоровье и восполнение здоровья существенно увеличены. Вы – неукротимая сила тьмы."},
[53643] = {name = "Священный удар", description = "[q]Наносит 7438 to 9562 ед. урона от светлой магии всем приспешникам Плети перед заклинателем."},
[53646] = {name = "Демонический союз", description = "[q]Увеличивает силу заклинаний на 2%. Также ваш прислужник накладывает на участников группы и рейда эффект \"Демонический союз\".\n\n Этот эффект увеличивает силу заклинаний на 10%."},
[53658] = {name = "Свет зари", description = "[q]И прежде чем наступит тьма, будет еще один рассвет..."},
[53694] = {name = "Удар чумы", description = "[q]Жестокий удар, наносящий цели урон от оружия и поражающий ее чумой с 2666.7 ед. урона от темной магии в течение 20 sec."},
[53717] = {name = "Взрыв трупа", description = "[q]Взрывает труп, нанося всем противникам в радиусе 20 м 60 ед. урона от сил природы (урон зависит от силы атаки). Если цель не мертва, то взрывается ближайший труп. Не работает для трупов механизмов и элементалей."},
[53721] = {name = "Смерть и разложение", description = "[q]Раз в 1 сек. всем целям в области действия заклинания наносится 10 ед. урона от темной магии (величина зависит от силы атаки). Время действия эффекта – 10 sec. С некоторой вероятностью также наложит на противников эффект \"Страх\"."},
[53765] = {name = "Открыть портал Тьмы", description = "[q]Открытие портала в мир теней для призвания оттуда прислужника."},
[53766] = {name = "Антимагический панцирь", description = "[q]Поглощает 75% урона, наносимого рыцарю смерти последующим вредоносным заклинанием. Урон, поглощаемый щитом, позволяет рыцарю смерти накопить дополнительную силу рун. Время действия – 5 sec."},
[53769] = {name = "Лик смерти", description = "[q]Выпускает заряд нечестивой энергии, наносящий урон от темной магии (урон зависит от силы атаки) противнику или излечивающий нежить-союзника."},
[53778] = {name = "Возложение рук", description = "[q]Могучее исцеление."},
[53788] = {name = "Выброс лавы", description = "[q]Вы бросаете в противника раскаленную лаву, нанося 1776 to 2264 ед. урона от огня. Если цель находится под действием эффекта \"Огненный шок\", то способность \"Выброс лавы\" поглотит этот эффект и нанесет критический удар."},
[53790] = {name = "Оборонительная стойка", description = "[q]Оборонительная стойка."},
[53791] = {name = "Стойка берсерка", description = "[q]Агрессивная стойка."},
[53792] = {name = "Боевая стойка", description = "[q]Сбалансированная боевая стойка."},
[53801] = {name = "Бешенство", description = "[q]Увеличение скорости атаки заклинателя на 25% и наносимого им физического урона на 50% на 10 min."},
[53813] = {name = "Чародейский щит", description = "[q]Окружает заклинателя волшебным щитом, оберегающим от урона."},
[53824] = {name = "Бросок", description = "[q]Бросок оружия в противника."},
[53850] = {name = "Теневой взрыв", description = "[q]Взрыв энергии, отбрасывающий противников назад и наносящий 960 to 1440 ед. урона от темной магии."},
[54028] = {name = "Нарушитель!", description = "[q]Маг-страж телепортировал вас за пределы Серебряного Анклава."},
[54029] = {name = "Нарушитель!", description = "[q]Маг-страж телепортировал вас за пределы Святилища Похитителя Солнца."},
[54049] = {name = "Укус Тьмы", description = "[q]Укусить противника, нанеся [ 166 + (1.228 * (Spell power * 0.5))] ед. урона от темной магии и дополнительно по 30 % за каждый эффект периодического урона, наложенный вами на противника."},
[54074] = {name = "Нейтрализующий яд", description = "[q]Отравляет противника, снижая на 10% действие эффектов лечения на нем в течение 15 sec."},
[54087] = {name = "Бросок кристалла гнили", description = "[q]Бросает кристалл гнили во враждебную цель. Кристалл от столкновения разлетается на осколки, нанося 2043 to 2257 ед. урона от сил природы и оставляя токсичный осадок, наносящий дополнительный урон в течение 45 sec."},
[54093] = {name = "Безмолвие", description = "[q]Вызывает немоту ближайших противников, предотвращая сотворение ими заклинаний в течение 6 sec."},
[54095] = {name = "Огненный шар", description = "[q]Наносит противнику 2025 to 2475 ед. урона от огня."},
[54102] = {name = "Вызов эфирной сферы", description = "[q]Призывает бесплотную сферу, которая будет помогать заклинателю в бою."},
[54105] = {name = "Туман порчи", description = "[q]Наносит урон от сил природы ближайшим противникам."},
[54113] = {name = "Стрела Тьмы", description = "[q]Выстреливает стрелой темной магии, наносящей урон от темной магии."},
[54115] = {name = "Взрыв кристалла гнили", description = "[q]Бросает кристалл гнили во враждебную цель. Кристалл от столкновения разлетается на осколки, нанося 2043 to 2257 ед. урона от сил природы и оставляя токсичный осадок, наносящий дополнительный урон в течение 45 sec."},
[54123] = {name = "Бешенство", description = "[q]Увеличение скорости атаки заклинателя на 50% и наносимого им физического урона на 50% на 10 min."},
[54132] = {name = "Оглушающий удар", description = "[q]Жестокий удар, наносящий 5000 ед. урона и оглушающий противника на 5 sec."},
[54135] = {name = "Разящий удар", description = "[q]Мощный удар, 15300 to 20700 ед. физического урона."},
[54136] = {name = "Насыщение гнилой кровью", description = "[q]Излечивает хозяина гнилокровного тролля на 2000 ед. раз в 1 сек. в течение 5 sec."},
[54160] = {name = "Мощь тайной магии", description = "[q]Повышение скорости атаки и скорости произнесения заклинаний на 75% и увеличение наносимого урона на 75%."},
[54164] = {name = "Вызов игроков", description = nil},
[54166] = {name = "Неприкосновенность Творца", description = "[q]Накладывает на Этимидиана щит, который защищает его от всех атак и заклинаний на 12 sec. Этимидиан выходит из боя, не может атаковать и использовать заклинания во время действия способности."},
[54170] = {name = "Парение", description = "[q]Повышает скорость полета на 350%."},
[54177] = {name = "Страсть Зепика", description = "[q]Дает вероятность нанесения урона, на короткое время увеличивает урон, наносимый Зепиком. Эффект суммируется до 5 раз."},
[54185] = {name = "Нанесенная когтем рана", description = "[q]Нанесение противнику 120 ед. физического урона."},
[54188] = {name = "Раскол брони", description = "[q]Ослабляет вражескую броню при каждом расколе. Суммируется до 5 раз. Время действия – 30 sec."},
[54193] = {name = "Неистовство земли", description = "[q]Подбрасывает всех находящихся поблизости противников в воздух, нанося урон и уменьшая их скорость передвижения. Время действия – 6 sec."},
[54194] = {name = "Укрепление здоровья", description = "[q]Восстановление здоровья союзнику."},
[54195] = {name = "Гнев земли", description = "[q]Бросает в противника комком зачарованной земли, нанося значительный урон."},
[54202] = {name = "Шквальный чародейских огонь", description = "[q]Выпускает волшебные стрелы в противника, нанося 1758 to 2042 ед. урона от тайной магии."},
[54210] = {name = "Огненный ливень", description = "[q]Вызов лавового дождя, наносящего всем противникам на выбранном участке 6 ед. урона от огня раз в 3 сек. в течение 5 sec."},
[54226] = {name = "Борьба Чар", description = "[q]Нанесение противнику 879 to 1021 ед. урона от тайной магии и увеличение получаемого им урона от тайной магии на 150 ед. на 20 sec."},
[54237] = {name = "Водный взрыв", description = "[q]Водный взрыв, наносящий противнику 2775 to 3225 ед. урона от магии льда и отбрасывающий его назад."},
[54241] = {name = "Залп водяных стрел", description = "[q]Атака ближайших противников в радиусе 45 м водяной стрелой, наносящей 1850 to 2150 ед. урона от магии льда."},
[54261] = {name = "Ледяное копье", description = "[q]Наносит 46 to 53 ед. урона от магии льда. Замороженным целям наносится тройной урон."},
[54287] = {name = "Исступление", description = "[q]Повышает скорость атаки заклинателя на 60% и наносимый им физический урон. Время действия – 2 min."},
[54290] = {name = "Паутинный выстрел", description = "[q]Наносит 1500 ед. урона от сил природы и уменьшает скорость передвижения на 10%. Действует 5 сек. эффект суммируется до 7 раз."},
[54306] = {name = "Защитный пузырь", description = "[q]Окружение заклинателя водным пузырем, снижающим получаемый урон на 99%. Любая атака заклинателя или поражение его заклинанием использует один заряд. Пузырь сохраняется в течение until cancelled. или до тех пор, пока не будет истрачено 35 заряда:зарядов."},
[54309] = {name = "Метка Тьмы", description = "[q]Поражает противника силой чистой тьмы, нанося ему 500 ед. урона от темной магии в секунду. Если цель находится под воздействием заклинания лечения, ближайшие союзники цели получают 1885 to 2115 ед. урона от темной магии. Длительность – 10 sec."},
[54311] = {name = "Могильные скарабеи", description = "[q]Призывает несколько некрорахнидов-скарабеев, помогающих заклинателю атаковать его цель."},
[54312] = {name = "Бешенство", description = "[q]Увеличение скорости заклинателя на 100% и наносимого им физического урона на 25% на 10 min."},
[54313] = {name = "Рой некрорахнидов-скарабеев", description = "[q]Призывает несколько некрорахнидов-скарабеев, помогающих заклинателю атаковать его цель."},
[54314] = {name = "Похищение мощи", description = "[q]Высасывает энергию у ближайших противников, уменьшая наносимый ими урон на 1% и увеличивая ваш урон на 2%."},
[54318] = {name = "Камень возвращения", description = "[q]Возвращает вас в зону \"<Hearthstone Location>\". Пункт назначения определяется тем, в какой таверне вы остановились."},
[54322] = {name = "Божественный щит", description = "[q]Защита заклинателя на 12 sec. от всех атак и заклинаний, но увеличение времени между его атаками на 100%."},
[54323] = {name = "Благословение Света", description = "[q]Цель обретает благословение и милость Небесного света."},
[54340] = {name = "Рвота Зловещего", description = "[q]Наносит 602 to 698 ед. урона от сил природы и снижает скорость передвижения противника на 50% в течение 10 sec."},
