[55098] = {name = "Преображение", description = "[q]Заклинатель превращается в могучего мамонта. Его физический урон увеличивается на 25%, и сам он становится невосприимчивым к эффектам оглушения."},
[55100] = {name = "Ошеломляющий рев", description = "[q]Громкий рев, ошеломляющий разум противников, слышащих его, и снижающий их скорость применения заклинания на 50% в течение 5 sec."},
[55101] = {name = "Сотрясение", description = "[q]Земля начинает мощно сотрясаться, нанося физический урон находящимся поблизости противникам."},
[55102] = {name = "Мощная кровавая атака", description = "[q]Удар противника клыками, наносящий физический урон и вызывающий кровотечение, которое наносит дополнительный урон в течение 21 sec. Этот удар невозможно заблокировать или парировать, равно как и уклониться от него."},
[55104] = {name = "Точный укол", description = "[q]Наносит противнику укол, причиняя физический урон. Эта атака не может быть заблокирована или парирована, и от нее нельзя уклониться."},
[55106] = {name = "Ошеломляющий вопль", description = "[q]Громкий рев, ошеломляющий разум противников, слышащих его, и снижающий их скорость применения заклинания на 25% в течение 5 sec."},
[55128] = {name = "Клубок змей", description = nil},
