[55888] = {name = "Шар Воды", description = "[q]Бросок большого сгустка воды."},
[55889] = {name = "Сфера льда", description = "[q]Наносит цели 800 ед. урона от магии льда, уменьшая скорость ее передвижения на 20%."},
[55909] = {name = "Сокрушительная волна", description = "[q]Поток воды выплескивается на противников, находящихся в 65-метровом секторе перед заклинателем, нанося 2313 to 2687 ед. урона от магии льда."},
[55912] = {name = "Взрыв воздуха", description = "[q]Могучий порыв ветра, сбивающий с ног всех противников."},
[55916] = {name = "Магматическая волна", description = "[q]Поток магмы выплескивается на противников, находящихся в 65-метровом секторе перед заклинателем, нанося 2313 to 2687 ед. урона от огня."},
[55918] = {name = "Ударная волна", description = "[q]Посылает перед воином силовую волну, наносящую (25 / 100 * Attack power) ед. урона и оглушающую всех противников на расстоянии 10 м перед ним на 4 sec."},
[55929] = {name = "Прокалывание", description = "[q]Устремляется к противнику и пронзает его."},
[55931] = {name = "Создание сферы огня", description = "[q]Сотворение сферы первородного огня, которая движется к цели, поражая пылающими лучами всех противников, который оказываются в радиусе 18 м от нее."},
[55936] = {name = "Налет", description = "[q]Нападение на змея Грозовой Гряды, наносящее 5850 to 7150 ед. урона."},
[55937] = {name = "Рыбных запах", description = "[q]Вы покрыты жирным палтусовым соусом."},
[55946] = {name = "Призыв Усача", description = "[q]Подзывает своего верного питомца, Усача."},
[55948] = {name = "Расти!", description = "[q]Вы растете. Наносимый урон увеличивается на 10%."},
[55958] = {name = "Удар бури", description = "[q]Бросок грозового молота в змея Грозовой Гряды, наносящее 8750 to 11250 ед. урона и оглушающее цель на 4 sec. Затем молот возвращается к метнувшему."},
[55959] = {name = "Объятия вампира", description = "[q]Поглощает жизненную силу цели, оглушая ее и нанося ей 1500 ед. урона от темной магии раз в 2 сек. в течение 20 sec. или пока здоровье принца Талдарам не опускается на 20000 ед."},
[55960] = {name = "Kill Credit", description = nil},
[55964] = {name = "Исчезновение", description = "[q]Приобретение заклинателем незаметности на 2.50 sec."},
[55968] = {name = "Кровожадность", description = "[q]Мгновенная атака цели, наносящая 120% от обычного урона оружия. Кроме этого, следующие 3 успешные атаки ближнего боя восстанавливают 1% здоровья. Время действия эффекта – 10 sec."},
[55973] = {name = "Кровавая чума", description = "[q]Жестокий удар, наносящий урон от оружия и поражающий цель чумой, нанося 2000 ед. урона от темной магии в течение 12 sec."},
[55980] = {name = "Топот мамонта", description = "[q]Наносит 5266 to 7734 ед. урона противникам, находящимся в радиусе 10 м."},
[55982] = {name = "Рывок мамонта", description = "[q]Рывок к противнику, наносящий обычный урон, увеличенный на 2647 to 2953 ед., и оглушающий цель на 3 sec."},
[55984] = {name = "Стрела Тьмы", description = "[q]Поражение противника теневой молнией, наносящей урон от темной магии и увеличивающей получаемый им урон от темной магии на 10% на 10 sec."},
[55987] = {name = "Обволакивающий огненный шар", description = "[q]Создание огненного шара, который окружает противников, нанося 3063 to 3937 ед. урона всем вурдалакам и 10500 to 13500 ед. урона великанам."},
[56036] = {name = "Руна разрушения", description = "[q]Нанесение на противника огненной руны на 5 sec. с нанесением урона спустя 5 сек."},
[56038] = {name = "Чумная стрела", description = "[q]Удар чумы прямо из сердца варгула. Используется для пыток. Наносит 833 to 967 ед. урона от темной магии."},
[56061] = {name = "Уничтожение", description = nil},
[56062] = {name = "Громыхающий топот", description = "[q]Испускает силовую волну перед заклинателем, наносящую 3150 to 3850 ед. урона и откидывающую всех противников на расстоянии 10 м."},
[56063] = {name = "Чародейский взрыв", description = "[q]Заклинатель пускает магическую взрывную волну, наносящую 1350 to 1650 ед. урона от тайной магии ближайшим противникам."},
[56064] = {name = "Залп стрел Тьмы", description = "[q]Наносит 1575 to 1925 ед. урона от темной магии находящимся поблизости противникам."},
[56091] = {name = "Пламенный шип", description = "[q]Быстрый выстрел небольшим сгустком пламени, наносящий 943 to 1057 ед. урона от огня. Добавляет 1 прием в серию."},
[56092] = {name = "Окутать пламенем", description = "[q]Выпускает сноп пламени, поджигающий цель и наносящий 1500 ед. урона от огня раз в 3 сек. Эффект суммируется. Время действия зависит от длины серии:\n 1 прием: 6 секунд\n 2 приема: 10 секунд\n 3 приема: 14 секунд\n 4 приема: 18 секунд\n 5 приемов: 22 секунды"},
[56095] = {name = "Ледяной дротик", description = "[q]Выстрел ледяной стрелой, которая наносит серьезный урон и снижает скорость передвижения на 6 sec."},
[56105] = {name = "Воронка", description = "[q]Создание кружащегося вихря чародейской энергии, наносящего урон от тайной магии всем противникам, попавшим в него."},
[56107] = {name = "Атака берсерка", description = "[q]Бросок на противника, отбрасывающий его назад и наносящий обычный урон, усиленный на 5100 to 6900 ед."},
[56130] = {name = "Чумное поветрие", description = "[q]Насылает на противников болезнь, наносящую им 12750 ед. урона от сил природы в течение 30 sec."},
[56149] = {name = "Смертельный яд", description = "[q]Наносит урон от сил природы 3 сек. в течение 12 sec."},
[56152] = {name = "Искра мощи", description = "[q]Увеличивает наносимый цели урон на 50%, принося в жертву заклинателя."},
[56153] = {name = "Охранная аура", description = "[q]Защищает членистоногов на большом пространстве вокруг заклинателя, делая их невосприимчивыми к урону."},
[56219] = {name = "Дар глашатая", description = "[q]Увеличивает наносимый заклинателем урон на 200% в течение 20 sec."},
[56223] = {name = "Веревка из паутины", description = nil},
[56272] = {name = "Дыхание Чар", description = "[q]Наносит 18850 to 21150 ед. урона от тайной магии в 40-метровом секторе перед заклинателем. По истечении 5 sec. после получения урона, затронутые цели взорвутся от энергии, нанося 9425 to 10575 ед. урона от тайной магии ближайшим союзникам."},
[56310] = {name = "Нарушенная клятва", description = "[q]Увеличение скорости атаки заклинателя на 30% и наносимого им физического урона на 10% на 10 sec."},
[56319] = {name = "Шаровая молния", description = "[q]Наносит 850 ед. урона от сил природы раз в 1 сек. в течение 8 sec. противникам, которые входят в выбранную область."},
[56322] = {name = "Искра бешенства", description = "[q]От заклинателя во все стороны разлетаются искры, каждая из которых наносит 329 to 381 ед. урона от сил природы."},
[56326] = {name = "Молния", description = "[q]Поражение противника молнией, урон от сил природы."},
[56330] = {name = "Погибель железа", description = "[q]Снижает расход маны на заклинания и способности на 50% и увеличивает наносимый урон на 25% в течение 2 min."},
[56352] = {name = "Порыв бури", description = "[q]Наносит 120% урона от оружия и причиняет дополнительный урон от сил природы в размере 300 ед. раз в 3 сек. в течение 12 sec."},
[56354] = {name = "Спринт", description = "[q]Повышает скорость передвижения заклинателя на 200% в течение 15 sec."},
[56356] = {name = "Железный мех", description = "[q]Снижает получаемый урон на 10% на 6 sec."},
[56397] = {name = "Чародейский обстрел", description = "[q]Запускает несколько волшебных стрел в противника, нанося 0 ед. урона от тайной магии."},
[56402] = {name = "Мощный удар с наскока", description = "[q]Взлетает в воздух и обрушивается на всех находящихся поблизости противников, нанося 1018 to 1182 ед. физического урона."},
[56408] = {name = "Вихрь", description = "[q]Кружась в вихре, заклинатель атакует ближайших противников, нанося каждому обычный урон."},
[56410] = {name = "Слепая вера", description = "[q]Повышает вероятность критического эффекта на 20% на 2 min."},
[56418] = {name = "Появление из снега", description = nil},
[56425] = {name = "Земной захват", description = "[q]Замуровывает противника в камень, обездвиживая его не более чем на 10 sec."},
[56426] = {name = "Казнь", description = "[q]Попытка прикончить раненого противника с нанесением 120% обычного урона. Можно применять только к противникам, чей уровень здоровья не превышает 20%."},
[56451] = {name = "Щит земли", description = "[q]Защита цели земляным щитом. При атаке ближнего боя защищенная цель исцеляется. Эффект может срабатывать только раз в несколько секунд. 10 атаки. Время действия – 2 min."},
[56490] = {name = "Топот", description = "[q]Удар по земле, наносящий находящихся рядом противникам обычный урон и сбивающий их с ног."},
[56491] = {name = "Выброс лавы", description = "[q]Вы бросаете в противника раскаленную лаву, нанося урон от огня."},
[56504] = {name = "Погружение", description = "[q]Погрузиться в снег, не потеряв при этом способности передвигаться."},
[56505] = {name = "Прилив мощи", description = "[q]Поражает противника лучом чистой магии, нанося 5000 ед. урона от тайной магии в секунду всем окружающим цель противникам в течение 5 sec."},
[56506] = {name = "Земной шок", description = "[q]Противника бьет ударной волной с нанесением урона от сил природы и лишением его возможности наложения заклинаний на 2 sec."},
[56508] = {name = "Появление", description = "[q]Появляется из-под земли. Ваш йормунгар будет привязан к месту, но сможет атаковать."},
[56513] = {name = "Удар йормунгара", description = "[q]Мощная атака, наносящая 5775 to 7425 ед. урона и сильно повышающая уровень угрозы. (Восстановление – 2 сек.)"},
[56520] = {name = "Благословение могущества", description = "[q]Благословляет дружественную цель, увеличивая силу атаки на 350. Время действия – 30 min. На персонажа единовременно может действовать только одно благословение от каждого паладина."},
[56524] = {name = "Кислотное дыхание", description = "[q]Окатывает кислотой всех противников, стоящих напротив вас, нанося им 1330 to 1470 ед. урона и дополнительно 855 to 945 ед. урона раз в 2 сек. в течение 15 sec. (Восстановление – 6 сек.)"},
[56580] = {name = "Клейкий яд", description = "[q]Отравляет противника, нанося 1000 ед. урона от сил природы и увеличивает затраты на применение заклинаний и способностей на 10% в течение 45 sec."},
[56581] = {name = "Убийственное жало", description = "[q]Дополнительный урон от сил природы раз в 2 сек. в течение 12 sec. Чтобы применить такое заклинание, заклинатель должен быть очень тяжело ранен."},
[56584] = {name = "Ядовитая смесь", description = "[q]Токсины, содержащиеся в крови глубинного ползуна, могут попасть на оружие и смешаться с уже наложенными вами ядами, что дает шанс нанести дополнительный урон от сил природы, когда ваш яд наносит урон противнику."},
[56605] = {name = "Жестокий яд", description = "[q]Наносит 2500 ед. урона от сил природы раз в 3 сек. в течение 24 sec. Эффект суммируется до 3 раз."},
[56620] = {name = "Кипящее пламя", description = "[q]Наносит 197 to 253 ед. урона от огня раз в 2.79900002 сек. в течение 3 sec."},
[56621] = {name = "Шар молний", description = "[q]Бросок шара молний в цель, нанося 1651 to 2349 ед. урона."},
[56625] = {name = "Бешенство", description = "[q]Увеличение скорости атаки заклинателя на 50% и наносимого им физического урона на 50% на 2 min."},
[56626] = {name = "Ужалить", description = "[q]Оса жалит противника, оглушая его на 2 sec."},
[56632] = {name = "Липкие путы", description = "[q]Выстреливает липкую паутину под ноги всем противникам, находящимся в радиусе 25 м, обездвиживая их на 8 sec."},
[56640] = {name = "Паукохват", description = "[q]Выпускает толстую нить паутины в цель, нанося 1650 to 1850 ед. урона и захватывает противника, притягивая его к заклинателю."},
[56643] = {name = "Тройной разрез", description = "[q]Три раза наносит противнику 50% от обычного урона от оружия."},
[56644] = {name = "Ледяная глыба", description = nil},
[56646] = {name = "Исступление", description = "[q]Увеличение скорости атаки заклинателя на 30% на 6 sec."},
[56648] = {name = "Мощная грибница", description = "[q]Увеличивает наносимый цели урон на 100%."},
[56683] = {name = "Подобрать плененного рыцаря", description = "[q]Ваш небесный коготь Серебряного Авангарда попытается подобрать плененного рыцаря Серебряного Авангарда."},
[56684] = {name = "Высадить плененного рыцаря", description = "[q]Высадить плененного рыцаря у лазарета Оплота Серебряного Авангарда."},
[56690] = {name = "Выпад с копьем", description = "[q]Укол копьем, наносящий дикому змею 1000 to 1300 ед. физического урона.\n Эта способность ослабляет \"Захват\" дикого змея на 5 ед."},
[56698] = {name = "Теневой разряд", description = "[q]Поражение противника темной молнией, наносящей урон от темной магии, равный 50% от максимального здоровья."},
[56702] = {name = "Темный серп", description = "[q]Испускает кружащиеся тени, которые ранят произвольно выбранных противников в течение 6 sec. Поддерживаемый эффект."},
[56704] = {name = "Избежать когтей", description = "[q]Подтянуться к брюху змея, чтобы избежать ударов когтей в течение 2 sec."},
[56706] = {name = "Раскрыть пасть", description = "[q]Заставляет дикого змея раскрыть пасть и показать глотку. Повышает вероятность попадания \"Решающего удара\" на 5% за каждое применение. Время действия – 10 sec."},
[56707] = {name = "Заразная гниль", description = "[q]Увеличение времени между атаками противника на 33%, снижение скорости произнесения заклинаний на 25%. Повышение до 5% вероятности заражения союзника при получении удара. Время действия – 2 min."},
[56709] = {name = "Аура потерянной надежды", description = "[q]Уменьшает максимальный запас здоровья противников на большом расстоянии от заклинателя на 0% раз в несколько секунд. Эффект суммируется."},
[56712] = {name = "Парящий полет", description = "[q]Скорость полета увеличена на 385%."},
[56715] = {name = "Отъем здоровья", description = "[q]Периодически похищает 500 ед. здоровья у противника, передавая его заклинателю."},
[56716] = {name = "Ледяные ветра", description = "[q]В течение 2 min. заклинателя овевают ледяные ветра. Холод замедляет скорость применения заклинаний на 50%, но повышает урон от магии льда на 75%."},
[56728] = {name = "Взгляд во тьму", description = "[q]Позволяет Глазу Талдарама отразить заклинание цели."},
[56729] = {name = "Безумный скачок", description = "[q]Заклинатель прыгает к цели, и скорость его атаки повышается на 200%."},
[56730] = {name = "Мрачная антимагия", description = "[q]Наносит 1000 ед. урона от темной магии и противодействует заклинанию противника, предотвращая использование им этого заклинания снова в течение 8 sec."},
[56733] = {name = "Неистовство Тьмы", description = "[q]Сбивание с ног всех противников в радиусе 10 м и нанесение им 1369 to 1631 ед. урона от темной магии в течение until cancelled."},
[56736] = {name = "Тяжелый шаг", description = "[q]Нанесение физического урона ближайшим противникам."},
[56737] = {name = "Топот", description = "[q]Ударяет по земле, подбрасывая в воздух всех противников в радиусе 50 м."},
[56746] = {name = "Тычок", description = "[q]Нергельд сотрясает землю, нанося 6660 to 7740 ед. урона всем противникам в радиусе 3 м."},
[56747] = {name = "Топот", description = "[q]Нергельд сотрясает землю, нанося 5941 to 8459 ед. урона всем противникам в радиусе 15 м, сбивая их с ног и оглушая."},
[56748] = {name = "Рык", description = "[q]Нергельд испускает могучий рык, который наносит 2971 to 4229 ед. урона и откидывает противников перед ним на расстоянии 30 м."},
[56750] = {name = "Сбор снега", description = "[q]Храппи набирает снег из ближайшего сугроба."},
[56753] = {name = "Бросок снежка", description = "[q]Приказать Храппи метнуть снежный ком в пылающего загробника"},
[56778] = {name = "Щит маны", description = "[q]Окружает заклинателя щитом, действующим в течение 1 min. и поглощающим до 30000 ед. физического урона, сжигая ману. Сжигает 1 ед. маны на 1 ед. поглощенного урона."},
[56785] = {name = "Летучая зараза", description = "[q]Заражает противника на 30 sec. Наносит урон от сил природы его ближайшим союзникам раз в 3 сек."},
[56837] = {name = "Ледяная стрела", description = "[q]Нанесение ближайшим противникам урона от магии льда и снижение скорости их передвижения на 4 sec."},
[56843] = {name = "Град стрел", description = "[q]Постоянный обстрел указанной области залпами снарядов с нанесением 1157 to 1343 ед. урона от тайной магии всем вражеским целям в радиусе 8 м раз в секунду в течение 6 sec."},
[56855] = {name = "Порыв вихря", description = "[q]Кружится, поражая находящихся рядом противников, нанося урон от оружия и еще 150% урона и отбрасывая их."},
[56858] = {name = "Огненный столб", description = "[q]Обжигает всех противников на выбранном участке и наносит дополнительный урон от огня раз в 2 сек. в течение 8 sec."},
[56861] = {name = "Борей", description = "[q]Вызов сильнейшего тайфуна, который наносит 602 to 698 ед. урона от сил природы противникам в зоне досягаемости и отбрасывает их назад на 5 м."},
[56862] = {name = "Водяная стрела", description = "[q]Выстрел в противника водяной стрелой, наносящей 124 to 145 ед. урона от магии льда."},
[56867] = {name = "Ядовитое облако", description = "[q]950 ед. урона от сил природы находящимся поблизости противникам раз в 2 сек. в течение 10 sec."},
[56891] = {name = "Молния", description = "[q]Поражение противника молнией, урон от сил природы."},
[56896] = {name = "Рывок люторога", description = "[q]Сильная атака."},
[56897] = {name = "Зов бури", description = "[q]Наносит целям в указанном месте 2200 ед. урона от сил природы раз в 1 сек. в течение 5 sec. (Восстановление: 15 сек.)"},
[56898] = {name = "Порча", description = "[q]Нанесение находящимся рядом противникам урона от темной магии раз в 3 сек. в течение 15 sec."},
[56908] = {name = "Огненное дыхание", description = "[q]Наносит 8750 to 11250 ед. урона от огня противникам, находящимся в конусе поражения перед заклинателем."},
[56909] = {name = "Рассекающий удар", description = "[q]Нанесение противнику и его ближайшим союзникам 35% обычного урона. Действует на 10 цели:целей."},
[56910] = {name = "Удар хвостом", description = "[q]Размашистый удар хвостом, поражающий всех противников, находящихся за драконом. Наносит 3063 to 3937 ед. урона и оглушает на 2 sec."},
[56920] = {name = "Слово Тьмы: Смерть", description = "[q]Нанесение 4163 to 4837 ед. урона от темной магии как противнику, так и заклинателю."},
[56925] = {name = "Ледяные щупальца", description = "[q]Обжигающе ледяные щупальца охватывают цель, нанося 2188 to 2812 ед. урона от магии льда."},
[56926] = {name = "Громовой удар", description = "[q]Грозовое облако, которое держится 10 sec., поражая всех противников в избранной игровой зоне с нанесением 1575 to 2025 ед. урона от сил природы и нанося 1575 to 2025 ед. дополнительного урона раз в 2 сек."},
[57055] = {name = "Мини", description = "[q]Снижает наносимый ближайшими противниками урон на 75% и понижает их защиту на 500."},
[57057] = {name = "Сдерживание Торвальда", description = "[q]При использовании снижает весь получаемый урон на 60%. Время действия – 10 sec."},
[57058] = {name = "Чародейское потрясение", description = "[q]Поражение противника тайной магией, нанося 9425 to 10575 ед. урона от тайной магии."},
[57060] = {name = "Ускорение", description = "[q]Повышает скорость атаки и произнесения заклинаний на 100%."},
[57061] = {name = "Ядовитое облако", description = "[q]3238 to 3762 ед. урона от сил природы находящимся поблизости противникам раз в 2 сек. в течение 8 sec."},
[57063] = {name = "Чародейская привлекательность", description = "[q]Увеличение получаемого урона от тайной магии на 2000 на 10 sec."},
[57066] = {name = "Тяжелый шаг", description = "[q]Нанесение 3299 to 3701 ед. физического урона ближайшим противникам."},
[57076] = {name = "Разъедание плоти", description = "[q]Разъедание плоти противника с нанесением 825 ед. урона от сил природы раз в 3 сек. в течение 15 sec."},
[57088] = {name = "Залп ядовитых стрел", description = "[q]Аманитар стреляет в игроков ядом, наносящим урон от сил природы."},
[57090] = {name = "Реанимация", description = "[q]Исцеляет цель на 500 ед. здоровья в секунду. Время действия – 10 sec. Добавляет один прием в серию. Эффект суммируется до 5 раз."},
[57092] = {name = "Молниеносность", description = "[q]Повышает скорость полета на 500% на 8 sec."},
[57094] = {name = "Мощный удар", description = "[q]Наносит противнику 4163 to 4837 ед. физического урона."},
[57095] = {name = "Гнев деревьев", description = "[q]Противника опутывают корни, нанося урон от сил природы раз в 2 сек. и не давая цели двигаться на время до 10 sec."},
[57108] = {name = "Пламенный щит", description = "[q]Окружает заклинателя щитом пламени, уменьшающим на 80% весь получаемый урон. Время действия зависит от количества приемов серии:\n 1 прием: 2 секунды\n 2 приема: 3 секунды\n 3 приема: 4 секунды\n 4 приема: 5 секунд\n 5 приемов: 6 секунд."},
[57143] = {name = "Дыхание жизни", description = "[q]Создает вспышку исцеляющей энергии, которая восстанавливает здоровье дружественным целям, находящимся в радиусе 60 м, и повышает эффективность исцеляющих эффектов заклинателя на 50%. Количество восстановленного здоровья и длительность эффекта зависит от длины серии:\n 1 прием: 5,000 ед./ 5 с\n 2 приема: 7,500 ед. / 10 с\n 3 приема: 10,000 ед. / 15 с\n 4 приема: 12,500 ед. / 20 с\n 5 приемов: 15,000 ед./ 25 с"},
[57322] = {name = "Вспышка молнии", description = nil},
[57374] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[57376] = {name = "Священная молния", description = "[q]Поражает цель зарядом магии, нанося урон от светлой магии."},
[57377] = {name = "Осуждение", description = "[q]Выбрасывает магические хлысты в каждого противника, нанося 5655 to 6345 ед. урона от светлой магии."},
[57381] = {name = "Неутолимая боль", description = "[q]Волна темной магии обрушивается на каждого противника, нанося 5655 to 6345 ед. урона от темной магии."},
[57385] = {name = "Пушка Серебряного Авангарда", description = "[q]Пускай пламя охватит приспешников Плети."},
[57386] = {name = "Обращение в бегство", description = "[q]Ваш люторог набрасывается на ближайшего противника, после чего урон, получаемый целью от эффектов кровотечения, увеличивается на 30 % на 30 sec."},
[57395] = {name = "Отчаянный порыв", description = "[q]Заклинатель использует все свои внутренние ресурсы, нанося 200% урона от оружия. После этого он в течение испытывает 3 sec. испытывает оглушение."},
[57408] = {name = "Грозовая туча", description = "[q]Призывает грозовую тучу, которая нависает над заклинателем и наносит 300 ед. урона от сил природы с каждой атакой."},
[57412] = {name = "Бомба расплаты", description = "[q]Карает Плеть, нанося просто-таки смешной урон. Некоторые, пожалуй, даже назовут его смехотворным."},
[57413] = {name = "Беспокойный сон", description = "[q]Погружение в изумрудный кошмар."},
[57428] = {name = "Статическое поле", description = "[q]Создание поля сверкающей энергии в случайном месте. Поле наносит 9425 to 10575 ед. урона от тайной магии всем противникам в радиусе 30 м."},
[57430] = {name = "Статическое поле", description = "[q]Создание поля сверкающей энергии в случайном месте. Поле наносит 9425 to 10575 ед. урона от тайной магии всем противникам в радиусе 30 м."},
[57432] = {name = "Чародейский импульс", description = "[q]Мгновенный выброс энергии тайной магии, наносящий 28275 to 31725 ед. урона от тайной магии всем противникам в радиусе 30 м."},
[57454] = {name = "Ледяное острие", description = "[q]Ледяное копье, наносящее 1295 to 1505 ед. урона от магии льда и подбрасывающее противника в воздух."},
[57456] = {name = "Обморожение", description = "[q]Наносит 48 to 52 ед. урона от магии льда раз в 3 сек. в течение 6 sec.\n Скорость передвижения снижается на 10% на 6 sec."},
[57459] = {name = "Чародейская буря", description = "[q]Выстреливает залпом волшебных стрел в воздух. Стрелы выбирают случайную ближайшую цель и устремляются к ней, нанося 9425 to 10575 ед. урона от тайной магии при столкновении."},
[57468] = {name = "Смута", description = "[q]Наседает на противника, нанося 200% от обычного урона."},
[57477] = {name = "Мерзлое дыхание", description = "[q]Нанесение противникам, находящихся в пределах конуса холода перед заклинателем, урона от магии льда, похищение их маны и снижение скорости их передвижения в течение 10 sec."},
[57480] = {name = "Призывание луча", description = "[q]Наносит 720 to 880 ед. урона от сил природы и притягивает цель к заклинателю."},
[57488] = {name = "Шквал", description = "[q]Рывок к противнику, наносящий обычный урон, увеличенный на 428 to 472 ед."},
[57491] = {name = "Огненное цунами", description = "[q]Наносит урон от огня ближайшим противникам и покрывает их лавой, замедляющей их передвижение и наносящей периодический урон от огня в течение 10 sec."},
[57493] = {name = "Внезапный порыв", description = "[q]Скорость полета увеличивается на 100% на 8 sec."},
[57524] = {name = "Метанойя", description = "[q]Повышает получаемый и наносимый критический урон на 50% и увеличивает вероятность нанести критический урон на 25% в течение 2 min."},
[57546] = {name = "Великое изгнание зла", description = "[q]Обращает в бегство противника-нежить или демона на 20 sec. Нанесенный урон может прервать действие эффекта. Обратить в бегство можно только одну цель единовременно."},
[57547] = {name = "Прикосновение валь'киры", description = "[q]Наносит 324 to 376 ед. урона от темной магии раз в 1 сек. в течение 5 sec."},
[57557] = {name = "Огнепых", description = "[q]Наносит противнику урон от огня и повышает получаемый им урона от огня в течение 1 min."},
[57570] = {name = "Темное дыхание", description = "[q]Наносит 6938 to 8062 ед. урона от огня противникам, находящимся в конусе поражения перед заклинателем."},
[57579] = {name = "Расщелина тьмы", description = "[q]Через 5 секунд наносит 6188 to 8812 ед. урона от темной магии всем противникам в расщелине тьмы."},
[57580] = {name = "Подзарядка молнией", description = "[q]Заряжает цель энергией молнии, вызывая перегрузку, которая по прошествии 5 sec. наносит 1411 to 1589 ед. урона от сил природы по области вокруг цели."},
[57581] = {name = "Взрыв Бездны", description = "[q]Через 5 секунд наносит 6188 to 8812 ед. урона от темной магии всем противникам в расщелине тьмы."},
[57589] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[57592] = {name = "Ремонт здания", description = "[q]Ремонт поврежденных зданий."},
[57599] = {name = "Удар чумы", description = "[q]Жестокий удар, наносящий 60% урона от оружия, 27 ед. урона и заражающий цель кровавой чумой – болезнью, наносящей периодический урон от темной магии. Удар отменяет один эффект периодического лечения, наложенный на цель."},
[57601] = {name = "Кровавая чума", description = "[q]Болезнь, наносящая урон от темной магии в течение 12 sec. Каждый раз, нанося урон, с 50%-й вероятностью может отменить один эффект периодического лечения."},
[57609] = {name = "Выстрел из пушки", description = "[q]Выстрел из пушки, наносящий до 5997 to 6003 ед. урона и 1300 ед. осадного урона близлежащим строениям."},
[57616] = {name = "Кристальный шип", description = "[q]Кристальный шип поражает противника, нанося 666 to 774 ед. урона от магии льда."},
[57617] = {name = "Морозный яд", description = "[q]Яд, наносящий урон от сил природы."},
[57628] = {name = "Огненный шар", description = "[q]Нанесение урона от огня противнику."},
[57629] = {name = "Кольцо льда", description = "[q]Наносит ближайшим противникам урон от магии льда, обездвиживая их на 5 sec."},
[57635] = {name = "Отрыв", description = "[q]Попытка оторваться от противника, снизив создаваемую угрозу. Прекращение атаки."},
[57640] = {name = "Коварный удар", description = "[q]Наносит противнику 125% урона от оружия."},
[57641] = {name = "Потрошение", description = "[q]Завершающий удар, наносящий урон."},
[57643] = {name = "Отражение заклинания", description = "[q]Следующее направленное против вас заклинание будет отражено щитом. Время действия – 5 sec."},
[57644] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[57645] = {name = "Порча", description = "[q]Нанесение находящимся рядом противникам урона от темной магии раз в 3 сек. в течение 24 sec."},
[57647] = {name = "Лунный огонь", description = "[q]Нанесение противнику урона от тайной магии и дополнительного урона в течение 12 sec."},
[57648] = {name = "Гнев", description = "[q]Противника поражает молния, нанося ему урон от сил природы."},
[57652] = {name = "Сокрушительная волна", description = "[q]Сокрушительная волна, наносящая 4163 to 4837 ед. урона от сил природы при столкновении с враждебными целями, которые находятся перед вами в пределах 20 м, и отбрасывающая их назад. Замороженная цель получает двойной урон."},
[57655] = {name = "Облик кошки", description = "[q]Превратиться в кошку. Сила атаки ближнего боя повысится на величину ловкости и 0 ед. В этом облике заклинатель невосприимчив к заклинаниям превращения и может использовать различные способности, свойственные кошке.\n\n При трансформации заклинатель освобождается от действия эффектов превращения и затрудняющих передвижение эффектов."},
[57657] = {name = "Увечье", description = "[q]Увечит цель, нанося ей 100% обычного урона и дополнительно 25, кроме того цель получает 30% дополнительного урона от эффекта приемов \"Трепка\", \"Полоснуть\" и кровотечения на 12 sec. Длина серии приемов увеличивается на 1."},
[57661] = {name = "Разорвать", description = "[q]Завершающий удар, наносящий периодический урон."},
[57665] = {name = "Ледяная стрела", description = "[q]Заклинание наносит противнику урон от магии льда и замедляет скорость его передвижения на 4 sec."},
[57668] = {name = "Кольцо льда", description = "[q]Наносит ближайшим противникам урон от магии льда, обездвиживая их на 8 sec."},
[57676] = {name = "Портал в Шаттрат", description = "[q]Создает портал, переносящий вошедших в него членов группы в Шаттрат."},
[57688] = {name = "Средоточие Силы", description = "[q]Став мишенью для заклинаний или атак, заклинатель впадает в ярость. Наносимый им урон и скорость его атаки увеличиваются."},
[57723] = {name = "Изнеможение", description = "[q]Невозможно получить эффект \"Героизм\" и аналогичные ему."},
[57724] = {name = "Пресыщение", description = "[q]Невозможно получить эффект \"Жажда крови\" и аналогичные ему."},
[57725] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[57728] = {name = "Ударная волна", description = "[q]Заклинатель посылает силовую волну, которая наносит 4050 to 4950 ед. урона и оглушает всех противников, находящихся в секторе радиусом 15 м перед ним. Время действия – 2 sec."},
[57733] = {name = "Драконья ярость", description = "[q]Увеличение скорости ближнего и дальнего боя, а также произнесения заклинаний на 35% для всех участников группы. Время действия – 30 sec."},
[57740] = {name = "Аура благочестия", description = "[q]Усиление брони ближайших участников группы на 10 min."},
[57741] = {name = "Ударная волна", description = "[q]Посылает силовую волну перед големом, которая наносит 3150 to 3850 ед. урона и оглушает все враждебные цели, находящиеся в секторе радиусом 10 м перед ним. Время действия – 2 sec."},
[57742] = {name = "Мстительная ярость", description = "[q]Вводит ближайшие дружественных цели в состояние исступления, наделяя их невосприимчивостью ко всем эффектам, ограничивающим передвижение или свободу действий."},
[57753] = {name = "Создание огненной сферы", description = "[q]Сотворение сферы первородного огня, которая движется к цели, поражая пылающими лучами всех противников, который оказываются в радиусе 18 м от нее."},
[57757] = {name = "Огненный ливень", description = "[q]Вызов лавового дождя, наносящего всем противникам на выбранном участке 2775 to 3225 ед. урона от огня раз в 2 сек. в течение 6 sec."},
[57759] = {name = "Удар молота", description = "[q]Бьет противника молотом по голове, уменьшая его размер, характеристики и наносимый физический урон на 5%. Эффект суммируется."},
[57762] = {name = "Жизнецвет", description = "[q]Восстанавливает 142 to 158 ед. здоровья раз в секунду и 463 to 537 ед. здоровья, когда эффект прекращает свое действие или рассеивается."},
[57765] = {name = "Покровительство Природы", description = "[q]Исцеляет дружественную цель на 694 to 806. Эффект усиливается на 20%, если цель находится под воздействием заклинаний \"Омоложение\", \"Восстановление\", или \"Жизнецвет\"."},
[57767] = {name = "Очищение", description = "[q]Очищает дружественную цель, снимая 1 эффект яда, 1 эффект болезни и 1 магический эффект."},
[57769] = {name = "Печать повиновения", description = "[q]Паладин получает шанс нанести дополнительно 70 ед. урона от светлой магии. Единовременно паладин может привести в действие только одно заклинание категории \"Печать\". Время действия – 2 min."},
[57774] = {name = "Правосудие света", description = "[q]Высвобождает энергию печати и поражает ею противника на until cancelled., в течение которых каждая атака ближнего боя, направленная против него, может исцелить атакующего."},
[57775] = {name = "Великое исцеление", description = "[q]Призывает силы света для исцеления союзника."},
[57777] = {name = "Обновление", description = "[q]Восстанавливает цели 2360 to 2640 ед. здоровья за 15 sec."},
[57778] = {name = "Слово Тьмы: Боль", description = "[q]Произнесение слова Тьмы, которое наносит противнику урон от темной магии раз в 3 сек. в течение 18 sec."},
[57779] = {name = "Пытка разума", description = "[q]Нанесение урона от темной магии противнику и снижение скорости его передвижения на 3 sec."},
[57780] = {name = "Молния", description = "[q]Поражение противника молнией, урон от сил природы."},
[57781] = {name = "Молния", description = "[q]Поражает противника молнией, наносящей 234 to 266 ед. урона от сил природы."},
[57783] = {name = "Земной шок", description = "[q]Противника бьет ударной волной с нанесением урона от сил природы и лишением его возможности наложения заклинаний на 2 sec."},
[57784] = {name = "Гром и молния", description = "[q]Удар молнии наносит всем противникам в радиусе 15 м 185 to 215 ед. урона от сил природы и отбрасывает их назад."},
[57785] = {name = "Волна исцеления", description = "[q]Призывает силы природы для исцеления союзника."},
[57789] = {name = "Смертельный удар", description = "[q]Наносит противнику 100% урона от оружия и оставляет на его теле серьезную рану, снижающую эффективность любых исцеляющих заклинаний на 50% на 5 sec."},
[57790] = {name = "Кровожадность", description = "[q]Мгновенная атака, наносящая противнику (Attack power * 142 / 100) ед. урона (величина зависит от силы атаки). Следующие 3 успешные атаки ближнего боя:успешных атак ближнего боя восстанавливают 1,5% здоровья. Время действия эффекта – 8 sec."},
[57791] = {name = "Кровожадность", description = "[q]Мгновенная атака, наносящая цели 30 ед. урона. Кроме того, следующие 3 атаки ближнего боя, попавшие по цели,:атак ближнего боя, попавших по цели, восстанавливают 1,5% максимального запаса здоровья. Время действия эффекта – 8 sec."},
[57794] = {name = "Героический прыжок", description = "[q]Атака в прыжке, наносящая 50% урона от оружия всем противникам в радиусе 8 м от выбранной точки и оглушающая их на 2 sec."},
[57795] = {name = "Сокрушение", description = "[q]Удар, повреждающий доспех противника и накладывающий эффект \"Раскол брони\". Удар также наносит 20% урона от оружия и по 50 за каждый действующий эффект \"Раскол брони\". Этот эффект может суммироваться до 5 раз."},
[57798] = {name = "Освящение", description = "[q]Освящение земли, на которой стоит паладин, с нанесением 240 ед. урона от светлой магии в течение 8 sec. противникам, которые входят на этот участок."},
[57799] = {name = "Щит мстителя", description = "[q]Бросок в противника священным щитом, наносящий 180 to 220 ед. урона от светлой магии и вызывающий головокружение. Затем щит перескакивает на других находящихся поблизости противников. Способен воздействовать на 2 цели:целей."},
[57801] = {name = "Огненное дыхание", description = "[q]Наносит 3063 to 3937 ед. урона от огня противникам, находящимся в конусе поражения перед заклинателем."},
[57802] = {name = "Щит земли", description = "[q]Защита цели земляным щитом. При атаке ближнего боя защищенная цель исцеляется. Эффект может срабатывать только раз в несколько секунд. 6 атаки. Время действия – 2 min."},
[57807] = {name = "Раскол брони", description = "[q]Ослабляет вражескую броню при каждом расколе. Суммируется до 5 раз. Время действия – 20 sec."},
[57815] = {name = "Пронзительный визг", description = "[q]Нанесение 24 to 29 ед. урона от темной магии находящимся поблизости противникам."},
[57825] = {name = "Ледяная стрела", description = "[q]Заклинание наносит противнику урон от магии льда и замедляет скорость его передвижения на 4 sec."},
[57832] = {name = "Удар грома", description = "[q]Поражает находящихся рядом противников, увеличивая время между их атаками на 20% на 30 sec. и нанося им 200 ед. урона. Урон увеличен за счет силы атаки. Способность повышает уровень создаваемой вами угрозы и может воздействовать на 4 цели:целей."},
[57833] = {name = "Ледяной плевок", description = "[q]Наносит 264 to 306 уд. урона от магии льда раз в 1 сек."},
[57835] = {name = "Дар Сумерек", description = "[q]Увеличивает наносимый целью урон от темной магии на 50% и снижает получаемый урон на 100%."},
[57843] = {name = "Колдунская защита от огня", description = "[q]Окружает заклинателя щитом с временем действия 1.50 min. Повышает наносимый урон на 30% и может поглотить определенное количество урона от огня."},
[57846] = {name = "Удар героя", description = "[q]Мощная атака, увеличивающая урон в ближнем бою на 10%."},
[57861] = {name = "Рык Рунока", description = "[q]Позволяет испустить рык, увеличивающий получаемый урон на 20% в течение 30 sec."},
[57882] = {name = "Прыжок", description = "[q]Гибкий ловец прыгает в указанное место, не ближе 5 и не далее 45 м. Прыгнув, гибкий ловец покинет бой."},
[57912] = {name = "Чародейская молния", description = "[q]Защитные системы Аметистовой крепости наносят 13000 ед. урона от тайной магии формам жизни, которые не имеют права находиться в ней."},
[57941] = {name = "Пытка разума", description = "[q]Нанесение урона от темной магии противнику и снижение скорости его передвижения на 3 sec."},
[57942] = {name = "Залп стрел Тьмы", description = "[q]Наносит урон от темной магии находящимся поблизости противникам."},
[57949] = {name = "Трепет", description = "[q]При повреждении наносит 1414 to 1586 ед. урона от темной магии. Перемещается к находящемуся ближе всех члену рейда."},
[57959] = {name = "Планарные аномалии", description = nil},
[57976] = {name = "Планарный заряд", description = "[q]Создает магическую взрывную волну, наносящую противникам урон от тайной магии."},
[57984] = {name = "Огненный взрыв", description = "[q]Нанесение урона от огня противнику."},
[58025] = {name = "Ледяная пощечина", description = "[q]Увеличивает получаемый целью урон от магии льда на 5 sec."},
[58026] = {name = "Благословение священной войны", description = nil},
[58040] = {name = "Разрушить печать на двери", description = "[q]Ослабляет печать на двери тюрьмы."},
[58053] = {name = "Свет небес", description = "[q]Восстанавливает 4936 to 5493 ед. здоровья союзнику."},
[58054] = {name = "Благословение королей", description = "[q]Благословляет дружественную цель, на 30 min. повышая все ее характеристики на 10%. На одного игрока единовременно может действовать лишь одно благословение от каждого паладина."},
[58105] = {name = "Сила Шадрона", description = "[q]Присутствие Шадрона увеличивает наносимый противникам урон от огня на 100%."},
[58127] = {name = "Божественная буря", description = "[q]Мгновенная атака оружием, наносящая урон от светлой магии 4 противникам:противникам в радиусе 8 м."},
[58130] = {name = "Незыблемость льда", description = "[q]Рыцарь смерти заставляет свою кровь застыть в жилах, становится невосприимчивым к эффектам оглушения и в течение 12 sec. получает на 50% меньше урона. Не снимает уже наложенных эффектов оглушения."},
[58137] = {name = "Вурдалакотрясение", description = "[q]Вурдалак взрывается, нанося 1388 to 1612 ед. урона от темной магии по всей зоне вокруг заклинателя."},
[58153] = {name = "Нечестивое озарение", description = "[q]Восстанавливает 4936 to 5493 ед. здоровья союзнику."},
[58154] = {name = "Молот несправедливости", description = "[q]Оглушение цели на 6 sec."},
[58187] = {name = "Передача силы", description = "[q]Повышение скорости атаки союзника на 1% на 1 sec."},
[58203] = {name = "Железная цепь", description = "[q]Прикрепление цепи к бомбе Плети, чтобы подтащить ее к ближайшему неповоротливому страшилищу. Пока цепь закреплена, никакие другие действия невозможны."},
[58282] = {name = "Толчок", description = "[q]Отталкивает цель, нанося 900 to 1100 ед. урона. Атака особенно опасна для горгулий, обращенных в камень."},
[58283] = {name = "Бросок камня", description = "[q]Камень наносит противнику 405 to 495 ед. урона и дезориентирует цель на 2 sec. Враги вряд ли заметят, кто бросил камень."},
[58291] = {name = "Разрушение щита", description = "[q]Ослабляет магию, держа пленника на расстоянии."},
[58438] = {name = "Воспламенение", description = "[q]Злобно поджигает мирного древня."},
[58455] = {name = "Чародейский взрыв", description = "[q]Заклинатель пускает магическую взрывную волну, наносящую 648 to 752 ед. урона от тайной магии ближайшим противникам."},
[58456] = {name = "Чародейский обстрел", description = "[q]Запускает несколько волшебных стрел в противника, нанося 720 to 880 ед. урона от тайной магии."},
[58457] = {name = "Ледяная стрела", description = "[q]Наносит 723 to 977 ед. урона от магии льда ближайшим противникам, замедляя их скорость передвижения на 4 sec."},
[58459] = {name = "Прокалывание", description = "[q]Наносит противнику 250 ед. физического урона раз в 3 сек. в течение 12 sec."},
[58460] = {name = "Жестокий удар", description = "[q]Наносит 150% урон от оружия."},
[58461] = {name = "Раскол брони", description = "[q]Ослабляет вражескую броню на 10% при каждом расколе. Суммируется до 5 раз. Время действия – 20 sec."},
[58462] = {name = "Чародейская вспышка", description = "[q]Поражает противника энергией, нанося ему 833 to 967 ед. урона от тайной магии. Каждый раз, когда вы применяете заклинание \"Чародейская вспышка\", время произнесения заклинания уменьшается. Эффект суммируется до 5 раз. Время действия – 8 sec."},
[58463] = {name = "Конус холода", description = "[q]Наносит 50 ед. урона от магии льда противникам в конусе перед заклинателем, снижая их скорость передвижения на 6 sec."},
[58464] = {name = "Ледяные оковы", description = "[q]Сковывание противника ледяными цепями, не позволяющими ему двигаться с места в течение 4 sec."},
[58469] = {name = "Чародейское могущество", description = "[q]Повышает скорость атаки ближнего и дальнего боя и скорость произнесения заклинаний на 50% на 10 sec."},
[58470] = {name = "Тактический скачок", description = "[q]Телепортация заклинателя за цель. Освобождения заклинателя от любых оков."},
[58471] = {name = "Удар в спину", description = "[q]Наносит 175% урона от оружия противнику, но только при атаке сзади."},
[58504] = {name = "Припадок", description = "[q]Наносит 100% от урона оружия, заставляя цель мучиться от боли, что увеличивает получаемый целью урон на 20% на 5 sec."},
[58506] = {name = "Незаметность", description = "[q]Заклинатель переходит в режим незаметности. Продолжается до отмены заклинателем."},
[58508] = {name = "Боковой удар", description = "[q]Наносит 60% урона от оружия ближайшей цели."},
[58510] = {name = "Подчинение магии", description = "[q]На 6 sec. снижает у противника скорость произнесения заклинаний на 50%."},
[58511] = {name = "Запах гнилого яблока", description = "[q]От вас плохо пахнет."},
[58514] = {name = "Запах гнилого банана", description = "[q]Этот запах напоминает нечто среднее между запахом ног вурдалака и запахом свиного зада."},
[58516] = {name = "Превосходство", description = "[q]Дает превосходство над противником, мгновенно нанося 75% урона от оружия. Способность может быть применена, только если цель уклонилась от атаки. \"Превосходство\" невозможно блокировать или парировать, от него нельзя уклониться."},
[58517] = {name = "Тяжкая рана", description = "[q]Яростная атака, наносящая 100% урона от оружия и вызывающая у цели обширное кровотечение, пока она не будет полностью исцелена. Наносит 694 to 806 ед. урона раз в 3 сек. до полного исцеления."},
[58519] = {name = "Плевок", description = "[q]Плевать. Вас все равно никто не любит."},
[58526] = {name = "Лазурные оковы", description = "[q]Оплетает противника магическими щупальцами, предотвращая применения им любого заклинания или способности в течение 4 sec."},
[58531] = {name = "Чародейские стрелы", description = "[q]Атака противника магическим снарядом, наносящим урон от тайной магии."},
[58532] = {name = "Залп ледяных стрел", description = "[q]Наносит 1800 to 2200 ед. урона от магии льда ближайшим противникам, замедляя их скорость передвижения на 4 sec."},
[58534] = {name = "Глубокая заморозка", description = "[q]Наносит 1018 to 1182 ед. урона от магии льда и оглушает противника на 4 sec."},
[58535] = {name = "Ледяная стрела", description = "[q]Заклинание наносит противнику урон от магии льда и замедляет скорость его передвижения на 4 sec."},
[58536] = {name = "Чародейский залп", description = "[q]Метает волшебную стрелу в ближайших противников, нанося 2035 to 2365 ед. урона от тайной магии."},
[58537] = {name = "Превращение", description = "[q]Превращает противника в овцу, вынуждая его бродить вокруг в течение 5 sec. Овца не может атаковать или произносить заклинания, но быстро восстанавливает здоровье. Любой нанесенный урон прекратит действие заклинания. Одновременно заклинание превращения можно наложить только на одну цель. Действует только на животных, драконов, великанов, гуманоидов и зверьков."},
[58540] = {name = "Призрачная темница", description = nil},
[58541] = {name = "Ловкий прыжок", description = "[q]Перенос тени на 30 м вперед в случае, если на пути нет никаких препятствий. Освобождение тени от любых уз."},
[58542] = {name = "Высидеть яйца", description = "[q]Высиживает сумеречные яйца..."},
[58543] = {name = "Иное измерение", description = "[q]Временное исчезновение из обычного мира. Выход из боя, увеличение скорости передвижения на 10 sec."},
[58544] = {name = "Кровавый насос", description = "[q]Похищение у противника здоровья в течение 6 sec. и передача его тени. Этот эффект чрезвычайно слаб, поскольку тень не приспособлена к участию в открытом бою."},
[58548] = {name = "Эфириал", description = "[q]Врагам крайне сложно его обнаружить."},
[58567] = {name = "Раскол брони", description = "[q]Удар, повреждающий доспех противника. Ослабляет броню на 4%, может суммироваться до 3 раз и действует в течение 30 sec. Удар также значительно повышает угрозу (уровень угрозы прямо зависит от силы атаки)."},
[58579] = {name = "Жаркий пламень", description = "[q]Печать огня наносит 150 ед. урона от огня раз в 1 сек. в течение 30 sec."},
[58604] = {name = "Лавовое дыхание", description = "[q]Ваш питомец осыпает противника брызгами раскаленной лавы, снижая его скорость произнесения заклинаний на 25 % на 10 sec."},
[58619] = {name = "Рывок", description = "[q]Рывок к противнику, наносящий обычный урон, увеличенный на 120 ед., и отбрасывающий цель назад."},
[58624] = {name = "Искаженное омоложение", description = "[q]Подвергает цель влиянию оскверненной волшебной энергии, нанося 360 to 440 ед. урона от тайной магии раз в 3 с в течение 12 sec."},
[58658] = {name = "Изгнание смертоносного кристалла", description = "[q]Смертоносный кристалл изгоняется в тот мир, из которого был призван."},
[58663] = {name = "Топот", description = "[q]Топот, наносящий 3770 to 4230 ед. физического урона противнику и сбивающий его с ног на 2 sec."},
[58667] = {name = "Энергетическое проклятие", description = "[q]Направляет энергию окружающих энергетических потоков, проклиная противника, нанося ему 250 ед. урона раз в 3 сек. и повышая полученный им магический урон на 20% в течение 30 sec."},
[58678] = {name = "Каменные осколки", description = "[q]Град из острых камней, наносящий цели 3 sec. ед. урона раз в 0.1 с и повреждающий всех других противников в радиусе 5 м от цели."},
[58683] = {name = "Жар битвы", description = nil},
[58688] = {name = "Неконтролируемая энергия", description = "[q]Высвобождает вихрь чародейской энергии, которая наносит 3800 to 4200 ед. урона от тайной магии противникам, находящимся напротив заклинателя. Цели противника получают 1000 ед. урона от тайной магии раз в 3 сек. Длительность – 15 sec."},
[58690] = {name = "Взмах хвостом", description = "[q]Наносит 1575 to 1925 ед. урона противникам, оказавшимся в конусе поражения за спиной дракона, прерывая произнесение ими заклинаний на 2 sec."},
[58693] = {name = "Снежная буря", description = "[q]Начинается снежная буря, длящаяся 8 sec., наносящая 1500 ед. урона от магии льда раз в 2 сек. и снижающая скорость передвижения противников в зоне поражения на 40%."},
