[62625] = {name = "Удар Земли", description = "[q]Нанесение урона от сил природы в 65801 to 74199 ед. противникам, находящимся в конусе поражения перед заклинателем."},
[62632] = {name = "Смерч", description = "[q]Кружась в вихре, заклинатель атакует ближайших противников, нанося каждому обычный урон и дополнительно 110 ед. урона. Время действия – 15 sec."},
[62634] = {name = "Пушечный залп", description = "[q]Выстрел ракетой, которая при взрыве наносит урон от огня ближайшим противникам."},
[62649] = {name = "Грозовой удар", description = "[q]Наносит 6013 to 6987 ед. урона противнику."},
[62650] = {name = "Стойкость льдов", description = "[q]Повышает наносимый урона на 10% и снижает урон, получаемый от всех источников, на 20%."},
[62653] = {name = "Приливная волна", description = "[q]Перемещается вперед на 40 м через ряды противников."},
[62660] = {name = "Темное сокрушение", description = "[q]Выстреливает по случайной цели. Заряд наносит 11310 to 12690 ед. урона темной магией всем противникам в радиусе 10 м от места попадания."},
[62661] = {name = "Жгучее пламя", description = "[q]Наносит всем противникам в радиусе 100 м 13875 to 16125 ед. урона от огня, раскаляя их броню и снижая ее показатель защиты на 75% на 10 sec."},
[62662] = {name = "Всплеск Тьмы", description = "[q]Наделяет заклинателя темной силой, повышающей физический урон на 100%, но снижающей скорость передвижения на 55% в течение 10 sec."},
[62663] = {name = "Грубая кора", description = "[q]Атаки против плеточника провоцируют рост защитной коры, повышая урон и уменьшая скорость передвижения. Эффект суммируется."},
[62666] = {name = "Взмах крылом", description = "[q]Опрокидывает противников вокруг заклинателя."},
[62670] = {name = "Устойчивость природы", description = "[q]Увеличивает весь наносимый урон на 10%, а все получаемое исцеление на 20%."},
[62671] = {name = "Скорость изобретения", description = "[q]Повышает весь наносимый урон на 10% и скорость передвижения на 20%."},
[62678] = {name = "Призыв союзников природы", description = "[q]Призывает союзников Фрейи на защиту Ульдуара."},
[62680] = {name = "Струи пламени", description = "[q]Игнис вызывает огненное извержение под всеми противниками. Фонтаны наносят 5655 to 6345 ед. урона от огня, подбрасывая цели в воздух и прерывая все заклинания на 8 sec. В дополнение цель получает 1000 ед. урона от огня в секунду в течение 8 sec."},
[62692] = {name = "Аура отчаяния", description = "[q]Восстановление маны от почти всех природных эффектов невозможно. \"Дух гадюки\", \"Мудрое правосудие\", \"Ярость шамана\" и \"Духовное созвучие\" сохраняют частичную эффективность. Скорость атаки ближнего боя снижена на 20%."},
[62702] = {name = "Гнев бури", description = "[q]Увеличивает наносимый урон на 10%, а общее количество здоровья цели на 20%."},
[62709] = {name = "Контратака!", description = "[q]Контратака, наносящая 35000 ед. урона и снимающая с противника один уровень защиты."},
[62710] = {name = "Strong Guy Sundering Thrust Aura", description = nil},
[62756] = {name = "Стена пылающего огня", description = nil},
[62775] = {name = "Раскаты ярости", description = "[q]Наносит урон всем противникам в радиусе 500 м и на 4 sec. снижает скорость их передвижения на 10%."},
[62793] = {name = "Гнев", description = "[q]Поражение противника молнией, урон от сил природы."},
[62796] = {name = "Огненный шар", description = "[q]Нанесение урона от огня противнику."},
[62804] = {name = "Омоложение", description = "[q]Исцеление союзника раз в 3 сек. в течение 12 sec."},
[62809] = {name = "Великое исцеление", description = "[q]Призывает силы света для исцеления всех находящихся поблизости союзников."},
[62818] = {name = "Выстрел", description = "[q]Выстрел в крушителя или захватчика, который наносит физический урон."},
[62821] = {name = "Яркий огонек", description = "[q]Согревает ближайшие дружественные цели, не давая им замерзнуть от \"Холода\" и \"Трескучего мороза\" и повышая дух на 4. Ваши заклинания и атаки дальнего боя могут опалить противников. Пламя может загасить снегопад или \"Ледяная вспышка\"."},
[62822] = {name = "Глоток исцеляющего зелья", description = "[q]Использует священный эликсир для того, чтобы исцелить часового."},
[62823] = {name = "Зажечь яркий огонек", description = "[q]Разжигает яркий огонек, согревающий дружественные цели. Его может загасить снегопад или \"Ледяная вспышка\"."},
[62831] = {name = "Перезарядка робота", description = "[q]Перезарядка робота на куче хлама, возвращающая его к жизни."},
[62834] = {name = "Бум", description = "[q]После смерти взрывается, нанося урон огнем ближайшим целям."},
[62845] = {name = "Подрезать сухожилия", description = "[q]Нанесение противнику 20% урона от оружия и снижение скорости его передвижения на 60% в течение 10 sec."},
[62856] = {name = "Обнаружение невидимости", description = "[q]Позволяет Оруксу Трижды Проклятому обнаруживать простую невидимость."},
[62863] = {name = "Дуэль", description = "[q]Вызовите другого бойца на верховой поединок!"},
[62906] = {name = "Оберег Фрейи", description = "[q]Наносит 13500 to 16500 ед. урона от сил природы ближайшим противникам и призывает стражей природы окружить противника."},
[62909] = {name = "Адский огонь Мимирона", description = "[q]Наносит 13500 to 16500 ед. урона от огня ближайшим противникам и оставляет выжженные участки на земле."},
[62911] = {name = "Молот Торима", description = "[q]\"Удар бури\" наносит 13500 to 16500 ед. урона всем ближайшим противникам."},
[62939] = {name = "Искрящаяся плеть", description = "[q]Последовательно наносит урон от сил природы цели и находящимся рядом с ней противникам. Поражает до 5 целей:целей."},
[62942] = {name = "Руническая защита", description = "[q]Укрепляет ближайших союзников"},
[62944] = {name = "Топот кодо", description = nil},
[62960] = {name = "Рывок", description = "[q]Атака, наносящая 8500 ед. урона и снимающая 1 уровень защиты с противника."},
[62974] = {name = "Ультразвуковой рожок", description = "[q]Чоппер выпускает мощную волну, наносящую 6300 to 7700 ед. урона всем противникам в конусе поражения на расстоянии до 35 м."},
[62975] = {name = "Стрела Тьмы", description = "[q]Поражение противника стрелой тьмы, наносящей урон от темной магии."},
[62997] = {name = "Взрыв плазмы", description = "[q]Плазменный луч наносит 17000 д. урона от чар огня в секунду в течение 6 sec. Сопротивление эффекту невозможно."},
[63003] = {name = "Рывок", description = "[q]Атака, наносящая 17000 ед. урона и снимающая 1 уровень защиты с противника."},
[63005] = {name = "Вызов на бой", description = nil},
[63007] = {name = "Защитные феромоны", description = "[q]Выброс облака феромонов вокруг заклинателя."},
[63009] = {name = "Взрыв", description = "[q]Наносит 12000 ед. урона от огня всем противникам в радиусе 3 м."},
[63010] = {name = "Рывок", description = "[q]Атака, наносящая 8500 ед. урона и снимающая 1 уровень защиты с противника."},
[63018] = {name = "Опаляющий свет", description = "[q]Заряжает цель священной энергией. Цель обжигает ближайших союзников в течение 9 sec."},
[63024] = {name = "Гравитационная бомба", description = "[q]Наполняет цель темной энергией и заставляет ее взорваться и наброситься на ближайших союзников спустя 10 sec."},
[63027] = {name = "Мины ближнего действия", description = "[q]Случайным образом разбрасывает мины ближнего действия, которые падают на землю на расстоянии 15 м от заклинателя. Мины срабатывают, когда противник приближается к ним на 3 м, нанося 12000 ед. урона от огня всем противникам в радиусе 3 м."},
[63028] = {name = "Заклинание Силы", description = nil},
[63032] = {name = "Звездный огонь", description = "[q]Нанесение противнику 694 to 806 ед. урона от тайной магии."},
[63038] = {name = "Темный залп", description = "[q]Наносит урон от темной магии находящимся поблизости противникам и понижает эффективность любого лечения на 25% в течение 10 sec."},
[63042] = {name = "Господство над разумом", description = "[q]Захватывает разум цели, временно заставляя ее подчиняться Йогг-Сарону. Также снижает здравомыслие цели на 4%."},
[63044] = {name = "Displace Roots", description = nil},
[63047] = {name = "Кнут Стража", description = "[q]Наносит 11100 to 12900 ед. урона от сил природы и дополнительно 2313 to 2687 ед. урона от сил природы раз в 1 сек. в течение 6 sec."},
[63059] = {name = "Опыление", description = "[q]Опыляет стража-плеточника, исцеляя его на 15% и увеличивая наносимый им урон на 25% в течение 30 sec."},
[63082] = {name = "Связывание жизни", description = "[q]Исцеляет на 9250 to 10750 ед. здоровья раз в 1 сек. в течение 6 sec."},
[63111] = {name = "Ледяное копье", description = "[q]Ледяное копье наносит 7863 to 9137 ед. урона от магии льда и замедляет скорость атаки и прочтения заклинаний на 100%. Наносит 100 ед. урона от магии льда раз в 5 в течение until cancelled."},
[63134] = {name = "Благословение Сары", description = "[q]Исцеляет дружественную цель на 27000 to 33000 ед., но при этом причиняет ей 60000 ед. урона от темной магии в течение 20 sec."},
[63136] = {name = "Объятия зимы", description = "[q]Ледяной щит, который поглощает урон и повышает наносимый физический урон на 100% в течение 1 min."},
[63138] = {name = "Рвение Сары", description = "[q]Увеличивает урон союзника на 20%, но также повышает наносимый ему урон на 100%. Длительность – 15 sec."},
[63147] = {name = "Гнев Сары", description = "[q]Наносит 12500 ед. урона от темной магии раз в 3 сек., но повышает наносимый физический урон на 12000. Время действия – 12 sec."},
[63148] = {name = "Божественный щит", description = "[q]Защищает паладина от урона и заклинаний на 12 sec., но увеличивает интервал между атаками на 100%. После применения данного заклинания цель нельзя снова сделать неуязвимой при помощи заклинаний \"Божественный щит\", \"Божественная защита\" и \"Длань защиты\" на протяжении 1 min."},
[63149] = {name = "Обезумевшая земля", description = "[q]Обезумевшая земля раскалывается под ногами целей, опрокидывая их и нанося 9713 to 11287 ед. урона от сил природы всем противникам и союзникам в радиусе 4 м от цели."},
[63169] = {name = "Окаменевшие суставы", description = "[q]Наносит урон от сил природы и снижает скорость передвижения на 25% в течение 20 sec."},
[63208] = {name = "Поездка Горки", description = nil},
[63226] = {name = "Тлетворное дыхание", description = "[q]Наносит 7863 to 9137 ед. урона от сил природы противникам перед заклинателем и 3700 to 4300 ед. урона от сил природы дополнительно раз в 3 с в течение 12 sec."},
[63227] = {name = "Исступление", description = "[q]Повышает скорость атаки на 100%. Увеличивает наносимый урон на 50% на 8 sec."},
[63233] = {name = "Некрокуция", description = "[q]Атака дальнего боя, наносящая 814 ед. урона и снимающая с противника один уровень защиты."},
[63236] = {name = "Лавовая бомба", description = "[q]Бросает в противника лавовую бомбу, наносящую урон от огня."},
[63240] = {name = "Железные шипы", description = "[q]На дружественной цели прорастают шипы, которые наносят 650 ед. урона от сил природы противникам за каждую атаку ближнего боя. Время действия – 10 sec."},
[63241] = {name = "Спокойствие", description = "[q]Восстановление у всех находящихся поблизости участников группы 13875 to 16125 ед. здоровья раз в 2 сек. в течение 10 sec. Требуется поддержание заклинания."},
[63242] = {name = "Покровительство природы", description = "[q]Исцеляет дружественную цель на 23125 to 26875 ед. здоровья. Эффект усиливается на 20%, если цель находится под воздействием заклинаний \"Омоложение\", \"Восстановление\" или \"Жизнецвет\"."},
[63247] = {name = "Живое цунами", description = "[q]Призывает живое цунами, которое наносит 6013 to 6987 ед. урона от сил природы при соприкосновении с противниками, и дополнительно 1850 to 2150 ед. урона от сил природы раз в 2 сек. в течение 10 sec."},
[63272] = {name = "Ураган", description = "[q]Создание в указанной области неистового шторма, в течение 10 sec. наносящего противникам 7863 to 9137 ед. урона от сил природы раз в 2 сек. и увеличивающего время между их атаками на 67%. Эффект заклинания необходимо поддерживать."},
[63275] = {name = "Рыбная ловля", description = "[q]Возьмите в руки удочку и найдите водоем, в котором можно порыбачить. Когда послышится всплеск, щелкните по поплавку правой кнопкой мыши, чтобы вытащить пойманную рыбу. Чем выше ваш навык, тем больше шансов поймать рыбу в водоемах зон, рассчитанных на высокий уровень персонажа."},
[63276] = {name = "Метка Безликого", description = "[q]Насылает на цель древнее проклятие, которое вытягивает 5000 ед. здоровья раз в 1 сек. у противников, окружающих цель. Время действия – 10 sec."},
[63317] = {name = "Огненное дыхание", description = "[q]Нанесение 13125 to 16875 ед. урона от огня противникам, находящимся в конусе поражения впереди заклинателя."},
[63341] = {name = "Реактивный ранец", description = "[q]Это птица… нет, самолет… да это Мимирон!"},
[63356] = {name = "Удар с размаха", description = "[q]Крепкий удар, наносящий физический урон и ослабляющий броню на 20% на 20 sec."},
[63364] = {name = "Саронитовая преграда", description = "[q]Создает саронитовую преграду, ослабляющую весь полученный урон на 99%."},
[63414] = {name = "Вращение", description = "[q]Спаренные лазерные орудия раскручиваются в течение 4 sec. перед тем, как начать беспорядочный огонь, нанося 20000 ед. урона от чар огня раз в 0.3 с всем попавшим в ловушку противникам. Время действия – 10 sec."},
[63420] = {name = "Погружение во тьму", description = "[q]Наносит 750 ед. урона от темной магии всем противникам и повышает наносимый им урон от темной магии на 10% за каждое применение."},
[63499] = {name = "Рассеивание заклинаний", description = "[q]Рассеивает магию, действующую на союзника, снимая 1 отрицательный эффект."},
[63507] = {name = "Погибель гнилостных зверей", description = "[q]Быстрый удар по Ануб'арским гнилостным зверям, наносящий 4050 to 4950 ед. урона. Эта атака может применяться очень быстро."},
[63520] = {name = "Лезвие крошшера Песни Войны", description = "[q]Выбор бочонка ордынской взрывчатки, который вы хотите уничтожить. Кроме того, лезвие можно бросить в любого ордынца из клана Песни Войны, нанеся ему 158 to 192 ед. урона. За убитого таким образом ордынца вы не получите ни опыта, ни добычи. Нельзя использовать на чужой цели."},
[63527] = {name = "Призыв заряженной сферы", description = "[q]Призывает электрический разряд, который служит союзнику источником энергии."},
[63528] = {name = "Суперзарядка", description = "[q]Увеличивает урон, наносимый целью, на 60% и сопровождает все атаки цели электрическими разрядами. В процессе заклинатель гибнет."},
[63539] = {name = "Боль разлуки", description = "[q]Разлука с компаньоном вызывает небывалый подъем сил."},
[63541] = {name = "Раздвоенная молния", description = "[q]Нанесение урона от сил природы противникам, находящимся в конусе поражения перед заклинателем."},
[63546] = {name = "Топот", description = "[q]Ударяет по земле, нанося обычный урон находящимся поблизости противникам."},
[63573] = {name = "Удар с размаха одной рукой", description = "[q]Тяжелый удар, наносящий физический урон и ослабляющий броню на 20% на 20 sec."},
[63575] = {name = "Дымящийся след", description = "[q]Пассажир взлетает из-за отдачи после взрыва."},
[63601] = {name = "Крепкие железные корни", description = nil},
[63603] = {name = "Стукнуть гаечным ключом!", description = "[q]Для того, чтобы восстановить крошшеру 225 to 275 ед. здоровья, достаточно постучать по нему гаечным ключом. Главное, найти нужное место. Или стучать столько, сколько нужно. Нельзя использовать в бою или при передвижении."},
[63612] = {name = "Грозовое клеймо", description = "[q]Поражает цель электричеством, нанося 2500 ед. урона от сил природы и повышая получаемый урон от сил природы на 40% в течение 20 sec."},
[63615] = {name = "Разорвать броню", description = "[q]Ослабляет броню цели на 5%."},
[63618] = {name = "Перегрузка", description = "[q]Выстреливает орбитальным зарядом, мгновенно убивая всех противников при попадании за пределы Плаца."},
[63619] = {name = "Стелющаяся Тьма", description = "[q]Исчадие Тьмы крадется сквозь тени к выбранному противнику. Наносимый исчадием урон увеличивается на 15% на 5 sec."},
[63630] = {name = "Мстительное излучение", description = "[q]Смерть товарища порождает волну негодования, которая повышает наносимый урон на 100%, а скорость атаки на 50%."},
[63631] = {name = "Шоковый удар", description = "[q]Наносит 100000 ед. урона от сил природы всем целям в радиусе 15 м."},
[63666] = {name = "Заряд напалма", description = "[q]Выстрел зарядом напалма в случайного противника. Взрыв наносит 7540 to 8460 ед. урона от огня мгновенно и 32000 ед. урона в течение 8 sec. дополнительно. При выборе цели приоритет отдается противникам, находящимся на расстоянии более 15 м."},
[63677] = {name = "Волна жара", description = "[q]Волна жара наносит всем противникам 1885 to 2115 ед. урона от огня моментально и 10000 ед. урона от огня в течение 5 sec. дополнительно."},
[63689] = {name = "Шар плазмы", description = "[q]Выстреливает плазматическим шаром по цели, нанося ей 9425 to 10575 ед. урона от чародейского огня."},
[63703] = {name = "Волна Бездны", description = "[q]Выпускает волну негативной энергии раз в 5 сек., которая забирает ману у ближайших противников."},
[63710] = {name = "Ограда Бездны", description = "[q]Окружает заклинателя непроницаемым щитом, сдерживающим любые атаки."},
[63713] = {name = "Господство над разумом", description = "[q]Подчинение противника-гуманоида своему влиянию на 20 sec."},
[63716] = {name = "Крик камня", description = "[q]Град битого камня наносит урон противникам раз в 0.2 сек. в течение 2 sec."},
[63722] = {name = "Темное сокрушение", description = "[q]Выстреливает в случайного противника. Когда выстрел попадает в цель, всем противникам в радиусе 0 м наносится 11310 to 12690 ед. урона от темной магии."},
[63723] = {name = "Кольцо Тьмы", description = "[q]Наносит 6938 to 8062 ед. урона от темной магии всем противникам в радиусе 15 м, отбрасывая их назад."},
[63753] = {name = "Веер клинков", description = "[q]Мгновенный бросок обоих оружий во всех противников в радиусе 10 м, наносящий 100% от урона оружия."},
[63754] = {name = "Удар в спину", description = "[q]Наносит противнику 200% обычного урона, но только при нападении со спины."},
[63755] = {name = "Смертельный яд", description = "[q]Наносит урон от сил природы 3 сек. в течение 12 sec."},
[63757] = {name = "Удар грома", description = "[q]Наносит всем находящимся поблизости противникам 4250 to 5750 ед. урона от сил природы и снижает скорость их атаки на 20%."},
[63758] = {name = "Залп ледяных стрел", description = "[q]Выпускает ледяные заряды, наносящие 3150 to 3850 ед. урона магией льда ближайшим противникам."},
[63760] = {name = "Великое исцеление", description = "[q]Призывает силы света для исцеления союзника."},
[63761] = {name = "Поиск цели A", description = "[q]Случайным образом ищет цель A."},
[63762] = {name = "Поиск цели И", description = "[q]Случайным образом ищет цель B."},
[63763] = {name = "Поиск цели C", description = "[q]Случайным образом ищет цель С."},
[63764] = {name = "Поиск цели D", description = "[q]Случайным образом ищет цель D."},
[63774] = {name = "Призыв элементаля огня", description = "[q]Призывает 0 элементалей огня, помогающих заклинателю до тех пор, пока он не откажется от сопровождения."},
[63775] = {name = "Огненный столб", description = "[q]Обжигает всех противников на выбранном участке и наносит дополнительный урон от огня раз в 2 сек. в течение 8 sec."},
[63784] = {name = "Вихрь клинков", description = "[q]Налететь на ближайших противников вихрем и атаковать их в течение 8 sec. раз в 1 сек. Находясь в вихре клинков, вы можете двигаться, но не можете применять другие способности; вам неведомы жалость, раскаяние или страх, и только смерть может остановить вас."},
[63789] = {name = "Огненный шар", description = "[q]Нанесение урона от огня противнику."},
[63795] = {name = "Психоз", description = "[q]Наносит 7500 ед. урона от темной магии. Снижает здравомыслие на 9%."},
[63802] = {name = "Схожее мышление", description = "[q]Соединяет умы двух противников, причиняя им 3000 ед. урона от темной магии и отнимая 2% здравомыслия раз в 1 сек., если цели находятся на расстоянии больше 20 м друг от друга."},
[63806] = {name = "Вихрь", description = "[q]Кружась в вихре, заклинатель атакует ближайших противников, нанося каждому обычный урон и дополнительно 300 ед. урона. Время действия – 4 sec."},
[63818] = {name = "Топот", description = "[q]Грохочущий Обломок наносит 3238 to 3762 ед. физического урона целям в радиусе 7 м от заклинателя."},
[63830] = {name = "Душевная болезнь", description = "[q]Наносит 5000 ед. урона от темной магии и заставляет противника бежать в страхе в течение 4 sec., после чего пытается перейти на следующего противника в радиусе 10 м. Также понижает здравомыслие на 3%."},
[63849] = {name = "Обнаженное сердце", description = "[q]Сердце XT-002 обнажено и очень уязвимо."},
[63861] = {name = "Цепи закона", description = "[q]Обездвиживает противника на 5 sec."},
[63891] = {name = "Луч смерти", description = "[q]Высвобождает четыре потока смертоносной энергии, уничтожающие любого противника, которого они коснутся."},
[63894] = {name = "Теневой барьер", description = "[q]Защищает заклинателя от всех атак и заклинаний."},
[63900] = {name = "Грохочущие шаги", description = "[q]Грохот, сотрясающий землю и наносящий [5 + ((Ranged attack power * 0.40) * 0.0245)] ед. урона от сил природы всем противникам в пределах 8 м. Эта способность также слегка увеличивает уровень угрозы."},
[63912] = {name = "Кольцо льда", description = "[q]Наносит ближайшим противникам урон от магии льда, обездвиживая их на 8 sec."},
[63913] = {name = "Ледяная стрела", description = "[q]Заклинание наносит противнику урон от магии льда и замедляет скорость его передвижения на 4 sec."},
[63939] = {name = "Порча Темных берегов", description = nil},
[63978] = {name = "Каменная волна", description = "[q]Каменная волна наносит 5550 to 6450 ед. физического урона целям в радиусе 10 м от заклинателя, повышая получаемый урон на 5% на 10 sec.\n Эффект суммируется."},
[64010] = {name = "Нечто неописуемое", description = "[q]С этим существом творится что-то странное…"},
[64013] = {name = "Нечто неописуемое", description = "[q]С этим существом творится что-то странное…"},
[64016] = {name = "Удар пламени", description = "[q]Увеличивает наносимый противнику урон от огня на 1000 в течение 1 min."},
[64041] = {name = "Бросок копья", description = "[q]Бросает в цель гарпун, наносящий 53 to 77 ед. урона."},
[64058] = {name = "Глубинный ужас", description = "[q]Вы пугаете цель, заставляя ее выронить оружие из правой руки и оружие дальнего боя на 10 sec. и дрожать от страха в течение 3 sec."},
[64059] = {name = "Доведение до помешательства", description = "[q]Сводит с ума все ближайшие цели, снижая Благоразумие на 100%"},
[64102] = {name = "Черная стрела", description = "[q]Проклинает противника, нанося ему периодический урон от темной магии в течение 1 min. Если цель умирает во время действия Черной стрелы, из ее тела можно будет вызвать скелета."},
[64122] = {name = "Взрыв черной дыры", description = "[q]Противникам наносится 16088 to 16912 ед. урона от темной магии."},
[64140] = {name = "Грохот", description = "[q]Нанесение увеличенного на 3 ед. стандартного урона ближайшим противникам, лишая их возможности произносить заклинания в течение 3 sec."},
[64141] = {name = "Ледяная вспышка", description = "[q]Заклинание наносит ближайшим противникам урон от магии льда и примораживает их к месту максимум на 5 sec."},
[64144] = {name = "Извержение", description = "[q]Из земли появляется щупальце, которое наносит 1488 to 2012 ед. физического урона всем противникам на небольшом участке и отбрасывает их назад."},
[64145] = {name = "Ослабление", description = "[q]Ослабляет противников, находящихся вокруг заклинателя, снижая наносимый ими урон на 20%. Требуется поддержание заклинания."},
[64152] = {name = "Иссушающий яд", description = "[q]Наносит 21000 ед. урона от сил природы противнику и отнимает 21000 ед. маны в течение 18 sec."},
[64153] = {name = "Черная чума", description = "[q]Заражает цель чумой, от чего ее периодически тошнит."},
[64156] = {name = "Апатия", description = "[q]Снижает скорость атаки и произнесения заклинаний противника на 60% и замедляет его скорость передвижения на 60%. Длительность – 20 sec."},
[64157] = {name = "Проклятие рока", description = "[q]Проклинает цель, нанося ей 20000 ед. урона от темной магии через 12 sec."},
[64159] = {name = "Похищение жизни", description = "[q]Передача 2000 ед. здоровья от цели к заклинателю раз в 1 сек. Время действия – 5 sec."},
[64162] = {name = "Слабость", description = "[q]Бессмертные стражи становятся уязвимы к Ярости бури, если у них низкий уровень здоровья."},
[64163] = {name = "Взгляд безумца", description = "[q]Взгляд в бездну безумия расшатывает ваш разум, нанося 5700 to 6300 ед. урона от темной магии и снижая здравомыслие на 4% раз в 1 сек., пока вы глядите на заклинателя."},
[64167] = {name = "Взгляд безумца", description = "[q]Вид хохочущего черепа сводит вас с ума, нанося 1750 ед. урона от темной магии и снижая ваше здравомыслие на 2% раз в 1 с, пока вы глядите на заклинателя."},
[64169] = {name = "Колодец здравомыслия", description = "[q]Вблизи колодца ваши мысли проясняются. Вы восстанавливаете 20% здравомыслия раз в 2 сек. Получаемый урон уменьшается на 50% до тех пор, пока вы находитесь рядом с колодцем."},
[64170] = {name = "Колодец здравомыслия", description = "[q]Призывает колодец здравомыслия, который восстанавливает здравомыслие всех находящихся в нем целей."},
[64171] = {name = "Буря титанов", description = "[q]Поддерживает разрушительный вихрь энергии титанов, который периодически поражает противников шаровыми молниями, способными уничтожить ослабленных бессмертных созданий."},
[64173] = {name = "Разрушенные иллюзии", description = "[q]Иллюзии Йогг-Сарона рушатся, и он застывает в ужасе…"},
[64174] = {name = "Защитный взгляд Ходира", description = "[q]Позволяет Ходиру спасти союзника от гибели, заключая его в непробиваемый ледяной кокон на until cancelled. После использования этой способности Ходир должен отдохнуть."},
[64187] = {name = "Щит бури", description = "[q]Заклинатель окружает себя электрическим щитом, нанося 250 ед. урона от сил природы атакующим, когда по нему проходит удар. Также заклинатель становится невосприимчивым к эффектам оглушения."},
[64189] = {name = "Оглушающий рев", description = "[q]Нанесение 7600 to 8400 ед. физического урона и лишение противника дара речи на 4 sec."},
[64193] = {name = "Разрыв сердца", description = "[q]Сердце Разрушителя XT-002 было разломано, и все боевые ограничения Разрушителя XT-002 исчезли. Урон увеличен на 15%. Здоровье увеличено на 60%."},
[64213] = {name = "Цепная молния", description = "[q]Поражает противника разрядом молнии, наносящим 4625 to 5375 ед. урона от сил природы. Разряд перейдет на соседние цели, нанося каждой последующей на 50% больше урона."},
[64216] = {name = "Вспышка молнии", description = "[q]Выброс энергии, наносящий 20813 to 24187 ед. урона от сил природы противникам в пределах 20 м."},
[64217] = {name = "Перегрузка", description = "[q]Наделяет неконтролируемой мощью, увеличивая наносимый урон на 20% за каждый эффект \"Перегрузки\"."},
[64218] = {name = "Перезарядка", description = "[q]Наделяет ближайшего служителя бури штормовым зарядом."},
[64227] = {name = "Статический заряд", description = "[q]Раз в 3 сек. наносит всем противникам в радиусе 500 м урон от сил природы."},
[64230] = {name = "Шок", description = "[q]Наносит урон от сил природы. Поражает цель и до 0 ее союзников."},
[64238] = {name = "Берсерк", description = "[q]На 30 min. повышает скорость атаки заклинателя на 150%, а весь наносимый им урон – на 900%."},
[64319] = {name = "Отпор", description = "[q]Отталкивает назад всех противников, находящихся перед заклинателем."},
[64348] = {name = "Импульсный излучатель", description = "[q]Посылает энергетический импульс в случайного противника, нанося 4242 to 4758 ед. урона от чар огня всем противникам, находящимся в конусе, направленном в сторону цели заклинания."},
[64359] = {name = "Ритуальная связь", description = "[q]Осуществляет связь."},
[64362] = {name = "Ритуальное грозовое облако", description = nil},
[64363] = {name = "Шок", description = "[q]Удар молнии, наносящий урон от сил природы."},
[64374] = {name = "Дикий наскок", description = "[q]Дикий наскок, причиняющий 5550 to 6450 ед. физического урона и дополнительно 3700 to 4300 ед. физического урона раз в 1 сек. в течение 5 sec."},
[64375] = {name = "Разрыв плоти", description = "[q]Смертоносная атака, наносящая 2313 to 2687 ед. физического урона раз в 2 сек. в течение 20 sec. Эффект суммируется до 100 раз."},
[64379] = {name = "Перезарядка", description = "[q]Наделяет штормовым зарядом."},
[64381] = {name = "Воля стаи", description = "[q]Присутствие других членов стаи повышает наносимый урон на 30% для всех членов стаи в радиусе 10 м."},
[64383] = {name = "Самовосстановление", description = "[q]Восстанавливает 50% запаса здоровья заклинателя. Это автоматический процесс, запускаемый, когда здоровье заклинателя опускается ниже определенного порога. Если все три детали уничтожены до завершения процесса, самовосстановление прерывается и заклинатель гибнет."},
[64386] = {name = "Ужасающий визг", description = "[q]Этот визг вселяет ужас в сердца противников, заставляя их бежать без оглядки в течение 5 sec."},
[64389] = {name = "Удар часового", description = "[q]Мощный взрыв, наносящий 3500 ед. урона от темной магии и увеличивающий получаемый целью урон от темной магии на 100% на 5 sec."},
[64395] = {name = "Квантовый удар", description = "[q]Бьет в противника, нанося 15675 to 17325 ед. физического урона."},
[64396] = {name = "Крадущиеся стражи", description = "[q]Нацеливается на игрока, которого затем атакует крадущийся страж."},
[64412] = {name = "Фазовый удар", description = "[q]Тяжелый удар, который наносит 8788 to 10212 ед. урона и постепенно переносит цель в другую реальность."},
[64420] = {name = "Навыки снайпера", description = "[q]Повышает вероятность критического эффекта способности \"Убийственный выстрел\" на 15%. Если охотник не двигается в течение 6 сек., урон от способностей \"Верный выстрел\" и \"Выстрел кобры\" увеличивается на 6% на 15 sec."},
[64422] = {name = "Ультразвуковой визг", description = "[q]Звуковая волна наносит 60125 to 69875 ед. физического урона всем противникам на своем пути. Наносимый урон распределяется поровну между всеми целями на пути волны."},
[64429] = {name = "Хватка смерти", description = "[q]Используя нечестивую энергию, пронизывающую и объединяющую все сущее, притягивает противника к заклинателю."},
[64431] = {name = "Хватка смерти", description = "[q]Захватывает цель лучом нечестивой энергии и подтягивает ее к вам."},
[64437] = {name = "Flame Arrow Aura", description = "[q]Выстрел ледяной стрелой, которая наносит серьезный урон и снижает скорость передвижения."},
[64439] = {name = "Огненная стрела", description = "[q]Выстрел огненной стрелы, которая наносит значительный урон в течение 8 sec."},
[64443] = {name = "Большой взрыв", description = "[q]Нанесение 76313 to 88687 ед. физического урона ближайшим противникам."},
[64455] = {name = "Дикая сущность", description = "[q]Каждая сущность Увеличивает урон, наносимый диким защитником, на 50%. Дикий защитник может оживить себя ценой одной из диких сущностей."},
[64465] = {name = "Маяк тени", description = "[q]Цель становится маяком для \"Сгущения тьмы\" Йогг-Сарона."},
[64468] = {name = "Сгущение тьмы", description = "[q]Придает сил союзникам в радиусе 20 м от цели, восстанавливая им 225000 ед. здоровья в течение 20 sec."},
[64478] = {name = "Дикий прыжок", description = "[q]Дикий защитник напрыгивает на цель, оглушая ее на 4 sec. и нанося урон темной магией раз в 1 сек."},
[64482] = {name = "Башня Жизни", description = "[q]Заряженная система орбитальной защиты делает Левиафана сильнее, увеличивая запас здоровья, но уменьшая наносимый урон от огня."},
[64487] = {name = "Вознесение в небеса", description = "[q]Наносит 655500 to 724500 ед. урона от тайной магии противникам."},
[64501] = {name = "Force Cast Signal to Argent Charger", description = nil},
[64505] = {name = "Темный щит", description = "[q]Защита заклинателя на 8 sec. от всех атак и заклинаний, но увеличение времени между его атаками на 100%."},
[64528] = {name = "Расплавленный лед", description = "[q]Пытается растопить ближайшую ледяную ловушку, нанося урон от огня, равный 100% максимального здоровья, в течение 10 sec."},
