[65481] = {name = "Назад на базу!", description = "[q]Приказать ветрокрылу вернуться в Дозор Адского Крика."},
[65488] = {name = "Пытка разума", description = "[q]Наносит противнику урон от темной магии и снижает скорость его передвижения на 3 sec."},
[65489] = {name = "Бросить взрывчатку", description = "[q]Уничтожает эльфов и их ценности в Астранааре."},
[65490] = {name = "Прикосновение вампира", description = "[q]Наносит противнику 33750 to 41250 ед. урона от темной магии в течение 15 sec."},
[65492] = {name = "Взрыв разума", description = "[q]Наносит цели 7088 to 7912 ед. урона от темной магии."},
[65494] = {name = "Вызов вьючного кодо", description = nil},
[65516] = {name = "Снежок", description = "[q]Брось, пока не растаял!"},
[65541] = {name = "Слово Тьмы: Боль", description = "[q]Произнесение слова Тьмы, которое наносит урон от темной магии противнику раз в 3 сек. в течение 15 sec."},
[65542] = {name = "Немота", description = "[q]Вызывает у противника немоту, предотвращая сотворение им заклинаний в течение 5 sec."},
[65543] = {name = "Ментальный крик", description = "[q]Сверхъестественный крик, заставляющий ближайших противников разбежаться в ужасе на 8 sec."},
[65544] = {name = "Слияние с Тьмой", description = "[q]Вы растворяетесь во Тьме. Весь получаемый вами урон снижается на 90%. Вы не можете атаковать и произносить заклинания, но восполняете 6% маны раз в секунду в течение 6 sec. Заклинание можно использовать, даже если на вас наложены эффекты оглушения, страха или немоты. При использовании рассеивает сковывающие и затрудняющие движение эффекты, а также дает невосприимчивость к ним на время действия заклинания."},
[65546] = {name = "Рассеивание заклинаний", description = "[q]Рассеивает магию, действующую на цель, снимая 2 отрицательных эффекта:отрицательных эффектов с союзника или 2 положительных эффекта:положительных эффектов с противника."},
[65547] = {name = "PvP-аксессуар", description = "[q]Снимает все эффекты замедления движения и все эффекты, вызывающие потерю контроля над персонажем."},
[65571] = {name = "Подношение из кристаллов", description = "[q]Призывает кристальное подношение для Великих."},
[65579] = {name = "Приземлиться", description = "[q]Приказать Тессере приземлиться."},
[65605] = {name = "Зерно нестабильности", description = "[q]Заражение противника зерном нестабильности. Через 4 сек. зерно взрывается, нанося противнику и его союзникам 3200 ед. урона от сил природы."},
[65611] = {name = "Бросок тряпичной куклы", description = "[q]Бросает тряпичную куклу в задиристого волчера."},
[65628] = {name = "Праведник", description = "[q]Вы хороший ."},
[65629] = {name = "Гадкий", description = "[q]Вы гадкий ."},
[65667] = {name = "Жар", description = "[q]Обдает ближайшие железные создания волной жара, увеличивая их скорость передвижения на 5 и скорость атаки на 5. Эффект суммируется до 10 раз. Если количество эффектов достигнет 10, создание раскаляется."},
[65719] = {name = "Кольцо Тьмы", description = "[q]Наносит беспорядочный урон темной магией всем существам в радиусе 15 м."},
[65735] = {name = "Омоложение", description = "[q]Исцеление союзника раз в 3 сек. в течение 12 sec."},
[65753] = {name = "Глаза Тонги", description = "[q]Тонга Рунический Тотем следит за вами с воздуха."},
[65762] = {name = "Невероятно острые когти", description = "[q]Тенумбра, крайне раздосадованная вторжением бойцов клана Боевой песни в Приют в Ночных лесах, наносит им 110 to 140 ед. дополнительного физического урона."},
[65766] = {name = "Всплеск света", description = "[q]Всплеск Света наносит 4500 ед. урона раз в 2 сек. темным противникам."},
[65768] = {name = "Всплеск Тьмы", description = "[q]Всплеск Тьмы наносит 4500 ед. урона раз в 2 сек. светлым противникам."},
[65775] = {name = "Ядовитые жвалы", description = "[q]Кислота, капающая со жвал скарабея, наносит 1800 ед. урона от сил природы раз в 3 сек. Время действия – 1 min."},
[65790] = {name = "Антимагия", description = "[q]Противодействует заклинанию противника, предотвращая произнесение любых заклинаний данной школы магии в течение 8 sec. Значительно повышает уровень угрозы."},
[65791] = {name = "Чародейская вспышка", description = "[q]Поражает противника зарядом энергии, нанося ему 12488 to 14512 ед. урона от тайной магии. При каждом применении заклинания \"Чародейская вспышка\" затраты маны на последующее такое заклинание увеличиваются на 10%, а весь урон, наносимый заклинаниями тайной магии, повышается на 150%. Эффект суммируется до 4 раз, длится 6 sec. или до произнесения любого заклинания тайной магии, кроме \"Чародейской вспышки\"."},
[65792] = {name = "Кольцо льда", description = "[q]Противники, находящиеся недалеко от заклинателя, получают 365 to 415 ед. урона от магии льда и примерзают к месту на срок до 8 sec. Нанесенный урон может прервать действие эффекта."},
[65793] = {name = "Скачок", description = "[q]Переносит заклинателя на 20 м вперед, если на пути нет препятствий. Освобождает заклинателя от эффектов оглушения и эффектов, ограничивающих передвижение."},
[65795] = {name = "Освобожденный Свет", description = "[q]Световой шар высвобождает свою энергию, нанося 14625 to 15375 ед. урона от Света противникам в радиусе 6 м."},
[65799] = {name = "Чародейский обстрел", description = "[q]Запускает несколько волшебных стрел в противника, нанося 10450 to 11550 ед. урона от тайной магии."},
[65800] = {name = "Чародейский взрыв", description = "[q]Порождает взрыв тайной магии вокруг заклинателя, который наносит 5280 to 5720 ед. урона от тайной магии всем целям в радиусе 10 м."},
[65801] = {name = "Превращение", description = "[q]Превращает противника в овцу, которая растерянно бродит в течение 10 sec. Овца не может атаковать или произносить заклинания. Любой нанесенный урон прекратит действие заклинания. Одновременно заклинание превращения можно наложить только на одну цель. Действует только на животных, гуманоидов и зверьков."},
[65802] = {name = "Ледяная глыба", description = "[q]Вы оказываетесь в середине ледяной глыбы, которая защищает вас от всех физических атак и заклинаний на 5 sec., но в это время вы не можете никого атаковать, двигаться или произносить заклинания. Вследствие гипотермии вы не можете повторно использовать заклинание \"Ледяная глыба\" в течение 30 sec."},
[65804] = {name = "Водрузить знамя Орды", description = "[q]За Орду!"},
[65807] = {name = "Ледяная стрела", description = "[q]Наносит противнику 11563 to 13437 ед. урона от магии льда и снижает скорость его передвижения на 40% на 9 sec."},
[65808] = {name = "Освобожденная тьма", description = "[q]Сгусток Тьмы выпускает заряд энергии, наносящий 14625 to 15375 ед. урона от Тьмы противникам в радиусе 6 м."},
[65809] = {name = "Страх", description = "[q]Повергает противника в ужас, заставляя его бежать в течение 10 sec. Нанесенный урон может прервать действие эффекта. Одновременно можно испугать не более одного противника."},
[65810] = {name = "Порча", description = "[q]Наслать на цель порчу, нанеся ей 30000 ед. урона от темной магии за 18 sec"},
[65812] = {name = "Нестабильное колдовство", description = "[q]Темная энергия медленно уничтожает цель, нанося ей 23125 to 26875 ед. урона в течение 15 sec. Кроме того, тот, кто рассеет заклинание \"Нестабильное колдовство\", получит 13875 to 16125 ед. урона и онемеет на 4 sec."},
[65814] = {name = "Проклятие агонии", description = "[q]Цель бьется в агонии, получая 42000 ед. урона от темной магии в течение 24 sec. Урон увеличивается постепенно и достигает своего пика к концу действия проклятия. Чернокнижник может наложить на каждую цель только одно проклятие."},
[65815] = {name = "Проклятие изнеможения", description = "[q]Снижает скорость передвижения цели на 50% на 12 sec. Чернокнижник может наложить на каждую цель только одно проклятие."},
[65816] = {name = "Адское пламя", description = "[q]Погружает заклинателя и находящихся рядом с ним противников в волны пламени, наносящие заклинателю 1500 ед. урона от огня, а его противникам – 9000 ед. урона от огня раз в 1 сек. Время действия заклинания – 15 sec"},
[65819] = {name = "Жгучая боль", description = "[q]Причиняет жгучую боль противнику, нанося 5490 to 6510 ед. урона от огня."},
[65821] = {name = "Стрела Тьмы", description = "[q]Поражает противника стрелой Тьмы, наносящей 11813 to 13187 ед. урона от темной магии."},
[65837] = {name = "Удар лозы", description = "[q]Поражает до 5 целей, нанося каждой 43 to 57 ед. урона от сил природы."},
[65843] = {name = "Ударная волна", description = "[q]Перед древним защитником распространяется силовая волна, наносящая всем противникам в радиусе 30 м 324 to 376 ед. урона и отбрасывающая их назад."},
[65854] = {name = "Звездный огонь", description = "[q]Наносит противнику 12387 to 14613 ед. урона от тайной магии."},
[65855] = {name = "Рой насекомых", description = "[q]Противника атакует рой насекомых, снижающий вероятность того, что он попадет по цели в ближнем и дальнем бою, на 3% и наносящий ему 22200 to 25800 ед. урона от сил природы за 12 sec."},
[65856] = {name = "Лунный огонь", description = "[q]Обжигает противника, нанося ему 4140 to 4860 ед. урона от тайной магии и 7000 ед. дополнительного урона от тайной магии в течение 12 sec."},
[65857] = {name = "Гнев деревьев", description = "[q]Оплетает цель корнями, которые удерживают ее на месте и наносят 1576.7 to 1756.7 ед. урона от сил природы в течение 10 sec. Сторонний урон может прервать этот эффект."},
[65858] = {name = "Щит Света", description = "[q]Щит Света защищает сестру от повреждений, поглощая 1200000 ед. урона и предотвращая прерывание произнесения заклинаний в течение 16 sec."},
[65859] = {name = "Смерч", description = "[q]Подбрасывает противника в воздух, предотвращая любые его действия. При этом он становится неуязвимым на 6 sec. Единовременно вы можете наложить заклинание \"Смерч\" только на одну цель."},
[65860] = {name = "Дубовая кожа", description = "[q]Кожа друида становится прочной, как кора. Весь получаемый урон снижается на 40%. Это заклинание можно использовать, будучи оглушенным, замороженным, парализованным, устрашенным или спящим. Можно использовать во всех обликах. Время действия – 12 sec."},
[65861] = {name = "Сила Природы", description = "[q]Призывает 3 древней:древней, которые будут атаковать противников в течение 30 sec."},
[65862] = {name = "Гнев", description = "[q]Наносит противнику 8460 to 9540 ед. урона от сил природы."},
[65863] = {name = "Волшебный огонь", description = "[q]Ослабляет броню цели на 15% на 40 sec. Во время действия заклинания цель не может становиться незаметной или невидимой."},
[65866] = {name = "Разрывной выстрел", description = "[q]Выстрел разрывным снарядом, наносящим противнику 4533 to 5467 ед. урона от огня. Заряд продолжает наносить цели урон каждую секунду в течение 2 sec."},
[65867] = {name = "Верный выстрел", description = "[q]Верный выстрел, наносящий цели урон от оружия."},
[65868] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[65869] = {name = "Отрыв", description = "[q]Вы отпрыгиваете назад, пытаясь выйти из боя."},
[65871] = {name = "Сдерживание", description = "[q]При использовании повышает вероятность парирования на 100% и вероятность уклонения от заклинаний противников, находящихся перед вами, на 100%. Под действием этой способности вы не можете атаковать. Время действия – 5 sec."},
[65874] = {name = "Щит Тьмы", description = "[q]Щит Тьмы защищает одну из сестер от повреждений, поглощая 1200000 ед. урона и предотвращая прерывание произнесения заклинаний в течение 16 sec."},
[65875] = {name = "Договор близнецов", description = "[q]Заклинание восстанавливает близнецам 50% от максимального запаса здоровья."},
[65876] = {name = "Договор близнецов", description = "[q]Заклинание восстанавливает близнецам 50% от максимального запаса здоровья."},
[65877] = {name = "Укус виверны", description = "[q]Жалящий выстрел, усыпляющий цель на 8 sec. Любой нанесенный урон снимает эффект. Когда цель просыпается, \"Укус\" наносит ей 2.6 ед. урона от сил природы за 8 sec. На каждую цель может действовать только один \"Укус\" каждого охотника единовременно."},
[65879] = {name = "Сила близнецов", description = "[q]Объединенные усилия близнецов повышают наносимый ими физический урон на 20% и позволяют произнесшей заклинание вести бой с оружием в каждой руке в течение 15 sec."},
[65880] = {name = "Ледяная ловушка", description = "[q]Установить ледяную ловушку, которая создает вокруг себя толстый слой льда на 30 sec., когда к ней приближается первый противник. Все противники в радиусе 10 м замедляются на 50%, пока остаются в зоне действия эффекта. Ловушка существует 1 min. Единовременно может быть установлена только одна ловушка."},
[65883] = {name = "Прицельный выстрел", description = "[q]Прицельный выстрел, наносящий 6000 ед. дополнительного урона и ослабляющий получаемое целью исцеление на 50%. Время действия – 10 sec."},
[65916] = {name = "Сила близнецов", description = "[q]Объединенные усилия близнецов повышают наносимый ими физический урон на 20% и позволяют произнесшей заклинание вести бой с оружием в каждой руке в течение 15 sec."},
[65918] = {name = "Оглушение", description = "[q]Выпавшая из седла цель оглушена на 10 sec."},
[65924] = {name = "Превосходство", description = "[q]Дает превосходство над противником, мгновенно нанося урон от оружия, усиленный на 35 ед. Способность может быть применена, только если цель уклонилась от атаки. \"Превосходство\" невозможно блокировать или парировать, от него нельзя уклониться."},
[65925] = {name = "Неумолимая атака", description = "[q]Если способность \"Превосходство\" наносит урон персонажу, произносящему заклинание, эффективность его исцеляющих и атакующих заклинаний снизится на 50% на 6 sec."},
[65926] = {name = "Смертельный удар", description = "[q]Жестокий удар, наносящий противнику урон от оружия, усиленный на 1000 ед., и оставляющий на его теле серьезную рану, снижающую эффективность всех исцеляющих заклинаний на 50% на 10 sec."},
[65927] = {name = "Рывок", description = "[q]Рывок к противнику и оглушение его на 1.50 sec."},
[65929] = {name = "Рывок и оглушение", description = "[q]Рывок к противнику и оглушение его на 1.50 sec."},
[65930] = {name = "Устрашающий крик", description = "[q]Воин издает крик, который заставляет противников в радиусе 8 м дрожать от страха. Время действия – 8 sec."},
[65932] = {name = "Возмездие", description = "[q]В течение 12 sec. вы мгновенно контратакуете противника, атаковавшего вас в ближнем бою (но не со спины). Максимальное количество срабатываний — 20."},
[65935] = {name = "Обезоруживание", description = "[q]Лишить противника оружия, носимого в правой руке, или оружия дальнего боя на 10 sec."},
[65936] = {name = "Раскол брони", description = "[q]Удар, повреждающий доспех противника. Ослабляет броню на 4%, может суммироваться до 5 раз и действует в течение 20 sec. Удар также значительно повышает угрозу (уровень угрозы прямо зависит от силы атаки)."},
[65940] = {name = "Сокрушительный бросок", description = "[q]Бросок оружия, наносящий противнику урон от оружия. Ослабляет броню противника на 20% на 10 sec. либо снимает все эффекты невосприимчивости."},
[65946] = {name = "Вихрь клинков", description = "[q]Вихрь клинков атакует противников в радиусе 8 м, нанося им 50% урона от оружия."},
[65949] = {name = "Расторопность валь'киры", description = "[q]Сила близнецов повышает скорость атаки на 10% в течение 15 sec. Эффект суммируется."},
[65950] = {name = "Касание Света", description = "[q]Вы находитесь под действием \"Касания Света\" валь'киры. Касание Света наносит 5850 to 6150 ед. урона от Света раз в 2 сек. всем игрокам, находящимся под действием эффекта \"Темная сущность\". Касание Света не причиняет вреда светлым целям."},
[65954] = {name = "Кровоизлияние", description = "[q]Резкий удар, наносящий 110% урона от оружия. Увеличивает урон от эффектов кровотечения на 75. Действует в течение 10 удара:ударов или 15 sec. Длина серии приемов увеличивается на 1."},
[65955] = {name = "Веер клинков", description = "[q]Мгновенный бросок обоих оружий во всех противников в радиусе 10 м, наносящий 90% от урона оружия."},
[65956] = {name = "Шквал клинков", description = "[q]Повышает скорость атаки на 20%, при этом также наносится удар ближайшему противнику. Время действия – 15 sec."},
[65957] = {name = "Потрошение", description = "[q]Завершающий удар, который наносит 8750 to 11250 ед. физического урона."},
[65960] = {name = "Ослепление", description = "[q]Ослепление цели, вследствие чего она бесцельно блуждает в течение 1 min. Любой нанесенный урон снимает эффект."},
[65961] = {name = "Плащ Теней", description = "[q]Снимает все эффекты вредоносных заклинаний и повышает вероятность противостоять всем заклинаниям на 90% на 5 sec. Не снимает эффекты, не позволяющие использовать способность \"Плащ Теней\"."},
[65962] = {name = "Нейтрализующий яд", description = "[q]Отравляет противника, снижая на 50% действие эффектов лечения на нем в течение 15 sec."},
[65970] = {name = "Удар бури", description = "[q]Мгновенная атака обоими оружиями. 4 последующие атаки:последующих атак от сил природы нанесут цели на 20% больше урона. Время действия – 12 sec."},
[65973] = {name = "Земной шок", description = "[q]Мгновенно поражает цель ударной волной, наносящей 3213 to 3387 ед. урона от сил природы. Ударная волна также прерывает произнесение заклинания и не дает произносить заклинания той же школы в течение 2 sec."},
[65974] = {name = "Вскипание лавы", description = "[q]Вы заряжаете оружие в левой руке энергией лавы и мгновенно наносите противнику урон в размере 100% от урона этого оружия. Если это оружие зачаровано \"Языком пламени\", наносимый урон увеличивается на 25%."},
[65976] = {name = "Неистовство ветра", description = "[q]Вас подхватывает неистовый ветер и позволяет наносить 2 дополнительных удара:дополнительных ударов."},
[65980] = {name = "Жажда крови", description = "[q]Увеличение скорости ближнего и дальнего боя, а также произнесения заклинаний на 30% для всех участников группы. Время действия – 40 sec."},
[65983] = {name = "Героизм", description = "[q]Повышает скорость произнесения заклинаний и скорость ближнего и дальнего боя на 30% всех участников группы или рейда. Время действия – 40 sec."},
[65986] = {name = "Оружие Водоворота", description = "[q]Уменьшает время произнесения следующего заклинания \"Молния\", \"Цепная молния\", \"Малая волна исцеления\", \"Волна исцеления\" и \"Цепное исцеление\" на 20%."},
[65998] = {name = "Иссушение", description = "[q]Объект получает урон от огня в размере 4950 to 6050 ед."},
[66001] = {name = "Касание тьмы", description = "[q]Вы находитесь под действием \"Касания Тьмы\" валь'киры. Оно наносит 5850 to 6150 ед. урона от Тьмы раз в 2 сек. всем игрокам, находящимся под действием эффекта \"Светлая сущность\". Касание Тьмы не причиняет вреда темным целям."},
[66003] = {name = "Удар воина Света", description = "[q]Мгновенный удар, наносящий 110% урона от оружия."},
[66004] = {name = "Печать повиновения", description = "[q]Все атаки ближнего боя могут нанести дополнительный урон от светлой магии."},
[66005] = {name = "Правосудие повиновения", description = "[q]Обрушивает на противника правосудие, нанося 8550 to 9450 ед. урона от светлой магии."},
[66006] = {name = "Божественная буря", description = "[q]Мгновенная атака, наносящая 110% урона от оружия 4 противникам в радиусе 8 м."},
[66007] = {name = "Молот правосудия", description = "[q]Оглушение цели на 6 sec."},
[66008] = {name = "Покаяние", description = "[q]Вводит противника в состояние глубокой задумчивости, лишающее возможности действовать на 10 sec. Любой нанесенный урон выводит цель из этого состояния. Действует против демонов, драконов, гуманоидов, великанов и нежити."},
[66009] = {name = "Длань защиты", description = "[q]Защищает участника группы или рейда от любых физических атак на 10 sec."},
[66010] = {name = "Божественный щит", description = "[q]Защищает паладина от физического и магического урона на 12 sec., но уменьшает весь наносимый им урон на 50%."},
[66011] = {name = "Гнев карателя", description = "[q]Увеличивает весь наносимый урон и эффективность исцеления на 20% на 20 sec."},
[66012] = {name = "Замораживающий выпад", description = "[q]Наносит противнику 25% урона от удара льда и вмораживает его в глыбу льда на 3 sec."},
[66013] = {name = "Пронизывающий холод", description = "[q]Пронизывающий холод поражает несколько целей, нанося им 6000 ед. урона от магии льда раз в 3 сек. в течение 18 sec."},
[66017] = {name = "Хватка смерти", description = "[q]Используя нечестивую энергию, пронизывающую и объединяющую все сущее, притягивает противника к рыцарю смерти, заставляя его атаковать рыцаря в течение 3 sec."},
[66018] = {name = "Удушение", description = "[q]Удушает противника, заставляя его замолчать на 5 sec. и причиняя ему 180 ед. урона от темной магии при завершении действия."},
[66019] = {name = "Лик смерти", description = "[q]Выпускает заряд нечестивой энергии, наносящий 6475 to 7525 ед. урона от темной магии противнику или излечивающий нежить-союзника на 9713 ед. здоровья."},
[66020] = {name = "Ледяные оковы", description = "[q]Ледяные цепи сковывают противника, снижая скорость его передвижения до 5% от нормальной. Каждую секунду в течение 10 sec. скорость движения противника восстанавливается на 10%."},
[66021] = {name = "Ледяное прикосновение", description = "[q]Наносит противнику от 5272 до 5728 ед. урона от магии льда и поражает его болезнью \"Озноб\", наносящей периодический урон и снижающей скорость атаки в ближнем и дальнем бою на 20% в течение 21 sec."},
[66023] = {name = "Незыблемость льда", description = "[q]Рыцарь смерти заставляет свою кровь застыть в жилах, становится невосприимчивым к эффектам оглушения и получает на 45% меньше урона. Время действия – 12 sec."},
[66032] = {name = "Сеть", description = "[q]Обездвиживание противника на 10 sec."},
[66042] = {name = "Огненный шар", description = "[q]Нанесение урона от огня противнику."},
[66043] = {name = "Превращение", description = "[q]Превращает противника в овцу, которая растерянно бродит в течение 4 sec. Овца не может атаковать или произносить заклинания. При получении любого урона цель обретает первоначальный облик. Одновременно заклинание превращения можно наложить только на одну цель. Действует только на животных, драконов, великанов, гуманоидов и зверьков."},
[66044] = {name = "Взрывная волна", description = "[q]Высвобождает волну пламени, наносящую урон от огня находящимся рядом противникам и снижающую их скорость передвижения на 4 sec."},
[66045] = {name = "Ускорение", description = "[q]Повышает скорость атаки и произнесения заклинаний на 100%."},
[66046] = {name = "Светлая воронка", description = "[q]Фьола Погибель Света начинает призыв мощного вихря Света. Способность поддерживается в течение 5 сек. и наносит 15600 to 16400 ед. урона раз в 1 сек. в течение 5 sec."},
[66047] = {name = "Ледяной удар", description = "[q]Мгновенно наносит противнику 100% урона от оружия в качестве урона от магии льда и дополнительно и дополнительно 250 ед. урона в качестве урона от магии льда. От данной атаки невозможно уклониться и ее нельзя блокировать или парировать."},
[66053] = {name = "Быстрина", description = "[q]Восстанавливает союзнику 57600 to 62400 ед. здоровья в момент произнесения заклинания и еще 138750 to 161250 ед. в течение 15 sec."},
[66054] = {name = "Сглаз", description = "[q]Превращает противника в лягушку. Превращенная цель не может атаковать и произносить заклинания. Нанесенный урон может прервать действие эффекта. Время действия – 8 sec. Только одна цель может быть превращена в лягушку единовременно. Действует только на гуманоидов и зверей."},
[66055] = {name = "Малая волна исцеления", description = "[q]Восстанавливает 84015 to 95985 ед. здоровья союзнику."},
[66056] = {name = "Очищение духа", description = "[q]Очищает дух дружественной цели, избавляя ее от 1 яда, 1 болезни и 1 проклятия."},
[66058] = {name = "Темная воронка", description = "[q]Эйдис Погибель Тьмы начинает призыв мощного вихря Тьмы. Способность поддерживается в течение 5 sec. и наносит 15600 to 16400 ед. урона раз в 1 сек. в течение 5 sec."},
[66060] = {name = "Спринт", description = "[q]Повышает скорость передвижения на 120% на 3 sec."},
[66063] = {name = "Щит земли", description = "[q]Защищает цель земляным щитом. При атаке ближнего боя защищенная цель исцеляется. Эффект может срабатывать только раз в несколько секунд. Кол-во зарядов – 6. Время действия – 2 min."},
[66065] = {name = "Омоложение", description = "[q]Восстанавливает союзнику 416250 to 483750 ед. здоровья в течение 15 sec."},
[66066] = {name = "Покровительство природы", description = "[q]Восстанавливает 138750 to 161250 ед. здоровья союзнику."},
[66067] = {name = "Восстановление", description = "[q]Восстанавливает союзнику 85050 to 94950 ед. здоровья в момент произнесения заклинания и еще 194250 to 225750 ед. в течение 21 sec."},
[66068] = {name = "Шипы", description = "[q]На дружественной цели прорастают шипы, которые наносят 463 to 537 ед. урона от сил природы противникам за каждую атаку ближнего боя. Время действия – 10 min."},
[66069] = {name = "Шип близнецов", description = "[q]Шип близнецов наносит 125% урона от оружия и увеличивает получаемый целью урон на 20% на 15 sec. или до получения 5 ударов:ударов."},
[66070] = {name = "Гнев деревьев", description = "[q]Оплетает цель корнями, которые удерживают ее на месте и наносят 1500 ед. урона от сил природы в течение 10 sec. Сторонний урон может прервать этот эффект. Используется только вне помещений."},
[66071] = {name = "Хватка природы", description = "[q]При активации дает 100%-ю вероятность того, что ударивший заклинателя противник будет обездвижен \"Гневом деревьев\". Кол-во зарядов – 1. Время действия – 45 sec."},
[66075] = {name = "Шип близнецов", description = "[q]Шип близнецов наносит 125% урона от оружия и увеличивает получаемый целью урон на 20% на 15 sec. или до получения 5 ударов:ударов."},
[66076] = {name = "Бросок", description = "[q]Бросок оружия в противника, наносящий 0% обычного урона."},
[66081] = {name = "Залп", description = "[q]Обрушивает на противника и его ближайших союзников град снарядов и поражает до 3 целей, нанося им 100 ед. дополнительного урона."},
[66083] = {name = "Стрелы молний", description = "[q]Призывает молнии, зачаровывающие стрелы заклинателя. При атаках дальнего боя дополнительно наносится 4713 to 5287 ед. урона от сил природы."},
[66086] = {name = "Спокойствие", description = "[q]Восстановление у всех находящихся поблизости участников группы 231250 to 268750 ед. здоровья раз в 2 сек. в течение 10 sec. Требуется поддержание заклинания."},
[66092] = {name = "Решимость", description = "[q]Скарабей восстанавливает здоровье и становится невосприимчив к оглушению и эффектам, затрудняющим передвижение. Скорость передвижения увеличивается на 100%."},
[66093] = {name = "Жизнецвет", description = "[q]Восстанавливает 11310 to 12690 ед. здоровья каждую секунду и 55500 to 64500 ед. здоровья – когда эффект рассеивается или его действие оканчивается."},
[66098] = {name = "Исповедь", description = "[q]Выпускает в цель поток небесного света, мгновенно излечивающий союзника на 42255 to 47745 ед. и далее раз в 1 сек. в течение 2 sec."},
[66099] = {name = "Слово силы: Щит", description = "[q]Вытягивает частичку души участника группы и создает из нее щит, способный поглотить 67063 to 77937 ед. урона. Щит держится 30 sec. Пока персонаж защищен, произнесение им заклинаний не может быть прервано получением урона. Повторно наложить щит можно только через 15 sec."},
[66100] = {name = "Сожжение маны", description = "[q]Сжигает 4374 to 4626 ед. маны у противника. За каждую единицу сожженной маны противник получает 3 ед. урона от темной магии."},
[66104] = {name = "Быстрое исцеление", description = "[q]Восстанавливает 83250 to 96750 ед. здоровья союзнику."},
[66112] = {name = "Свет небес", description = "[q]Восстанавливает 127710 to 142290 ед. здоровья союзнику."},
[66113] = {name = "Вспышка Света", description = "[q]Восстанавливает союзнику 84825 to 95175 ед. здоровья."},
[66114] = {name = "Шок небес", description = "[q]Переполняет цель энергией светлой магии, нанося 72000 to 78000 ед. урона противнику или восстанавливая 72000 to 78000 ед. здоровья союзнику."},
[66115] = {name = "Длань свободы", description = "[q]Делает союзника невосприимчивым к затрудняющим передвижение эффектам на 10 sec. На персонажа единовременно может действовать только одно заклинание категории \"Длань\" от каждого паладина."},
[66116] = {name = "Очищение", description = "[q]Очищает дружественную цель, снимая 1 эффект яда, 1 эффект болезни и 1 магический эффект."},
[66117] = {name = "Выстрел", description = "[q]Выстрел, наносящий противнику физический урон."},
[66118] = {name = "Жуки-трупоеды", description = "[q]Выпускает рой насекомых, атакующих противников заклинателя и похищающих 30% текущего здоровья каждой цели раз в 1 сек. Минимальное количество похищаемого здоровья – 250 единиц."},
[66129] = {name = "Бешенство пауков", description = "[q]Повышает скорость передвижения, атаки и произнесения заклинаний за каждого нерубского землеглота в радиусе 12 м на 100%."},
[66134] = {name = "Теневой удар", description = "[q]Заклинатель проходит сквозь тени, появляясь за спиной враждебной цели, и наносит ей 40000 ед. урона от темной магии."},
[66177] = {name = "Обновление", description = "[q]Восстанавливает цели 346875 to 403125 ед. здоровья в течение 15 sec."},
[66178] = {name = "Шаг сквозь тень", description = "[q]Пройти сквозь тень, чтобы появиться за спиной у противника."},
[66180] = {name = "Мяч из толстой кожи", description = "[q]Бросьте мяч в одного из игроков. Если у него есть свободное место в сумке, он его поймает!"},
[66187] = {name = "Неизменная руна", description = nil},
[66193] = {name = "Вечная мерзлота", description = "[q]Замораживает землю в пределах небольшой области, мешая большинству существ закапываться и замедляя их скорость передвижения по этому участку на 80%."},
[66195] = {name = "Напалм", description = "[q]Выпускает шар напалма, наносящий урон от огня всем противникам в радиусе 5 м от места взрыва. Кроме того, пораженные цели будут облиты напалмом, который будет наносить урон от огня в течение 5 sec."},
[66197] = {name = "Пламя Легиона", description = "[q]Сжигает цель. В месте нахождения цели раз в 1 сек. на 6 sec. появляется пламя Легиона."},
[66203] = {name = "Струя пара", description = "[q]Наносит всем противникам в радиусе 15 м урон от огня и отбрасывает их назад. После активации этой способности пушка не сможет нагнетать давление пара в течение последующих 4 sec."},
[66207] = {name = "Подрезать крылья", description = "[q]Калечит противника, снижая скорость его передвижения на 50% на 10 sec."},
[66218] = {name = "Запуск", description = "[q]Отправляет водителя в указанное место."},
[66228] = {name = "Сила Пустоты", description = "[q]Сила Пустоты повышает наносимый магический урон на 20% на 30 sec."},
[66237] = {name = "Испепеление плоти", description = "[q]Ваша плоть обугливается у вас на глазах! Следующие 85000 ед. получаемого вами исцеления будет поглощено, а наносимый урон будет снижен на 50% в течение 15 sec. Если \"Испепеление плоти\" не будет снято до окончания времени действия, оно вызовет эффект \"Пылающая геенна\"."},
[66251] = {name = "Запуск", description = "[q]Отправляет водителя в указанное место."},
[66254] = {name = "Призыв огненного вихря", description = "[q]Призывает 1 элементаля огня, помогающего заклинателю до тех пор, пока он не откажется от сопровождения."},
[66258] = {name = "Инфернальное извержение", description = "[q]Лорд Джараксус призывает адский вулкан, порождающий инферналов."},
[66269] = {name = "Врата Пустоты", description = "[q]Лорд Джараксус призывает врата Пустоты."},
[66282] = {name = "Скрип ловуфки для яфферов", description = "[q]Ловуффка издает душераздирающие звуки, как если бы прямо перед вами кричали миллионы ящеров."},
[66283] = {name = "Крутящийся шип боли", description = "[q]Прыгает в сторону противника, хватает его и наносит значительный физический урон."},
[66290] = {name = "Сон", description = "[q]Погружает противника в сон, длящийся 6 sec. Противник проснется при получении урона. Единовременно можно усыпить только одного противника."},
[66296] = {name = "Покров тени", description = "[q]Окутывает машину тенями, делая ее незаметной и повышая скорость передвижения на 20 sec."},
[66300] = {name = "Погнали!", description = "[q]Жми на газ! Повышает скорость на 50%."},
[66313] = {name = "Огненная бомба", description = "[q]Наносит 4813 to 6187 ед. урона от огня всем противникам в радиусе 8 м от цели."},
[66320] = {name = "Огненная бомба", description = "[q]Раз в 1 сек. наносит противникам 7800 to 8200 ед. урона от огня."},
[66330] = {name = "Сотрясающий топот", description = "[q]Сотрясающий топот наносит 11700 to 12300 ед. физического урона всем противникам в радиусе 15 м и прерывает произнесение заклинаний на 8 сек."},
[66331] = {name = "Прокалывание", description = "[q]Наносит противнику 150% урона от оружия и заставляет его истекать кровью, нанося дополнительно 3938 to 5062 ед. урона раз в 2 сек. в течение 40 sec."},
[66334] = {name = "Поцелуй Госпожи", description = "[q]При прочтении следующего заклинания, применяющегося не мгновенно, все заклинания этой школы магии прерываются на 8 sec. и наносится 13650 to 14350 ед. урона от темной магии."},
[66378] = {name = "Демонический разрез", description = "[q]Вихревая атака, которая наносит 75% урона от оружия, игнорируя броню."},
[66407] = {name = "Трещина в черепе", description = "[q]Оглушает противника на 2 sec."},
[66408] = {name = "Побои", description = "[q]Наносит противнику 75% обычного урона в ближнем бою и прерывает произнесение заклинаний на 5 sec."},
[66456] = {name = "Бросок глефы", description = "[q]Выстреливает в цель большую глефу, наносящую физический урон и 4242 to 4758 ед. осадного урона в радиусе 5 м."},
[66489] = {name = "Бросок копья", description = "[q]Наносит противнику урон от магии льда."},
[66494] = {name = "Дорожка скверны", description = "[q]Инфернал пламени Скверны атакует цель, нанося ей 11700 to 12300 ед. урона от огня и создавая в области действия геену скверны."},
[66495] = {name = "Геенна скверны", description = "[q]В течение 6 sec. периодически наносит урон от огня союзникам, находящимся в радиусе 15 м."},
[66514] = {name = "Ледяное дыхание", description = "[q]Наносит 0 ед. урона от магии льда противникам в конусе поражения на расстоянии 70 м перед заклинателем. Цели замораживаются на 2 sec."},
[66515] = {name = "Отражающий щит", description = "[q]Защищает заклинателя щитом, поглощающим урон и отражающим 25% поглощенного урона на атакующего."},
[66518] = {name = "Выстрел из орудия", description = "[q]Наносит урон от огня и 943 to 1057 ед. осадного урона всем противникам в радиусе 10 м от места взрыва."},
[66528] = {name = "Молния скверны", description = "[q]Бьет противника молнией Легиона, которая перескакивает на другого находящегося поблизости противника. Заклинание поражает до 3 целей."},
[66529] = {name = "Выстрел из орудия", description = "[q]Выпускает огненный шар, наносящий урон от огня и 943 to 1057 ед. осадного урона всем противникам в радиусе 10 м от места взрыва."},
[66532] = {name = "Огненный шар Скверны", description = "[q]Наносит 34125 to 35875 ед. урона от огня и дополнительно 10725 to 11275 ед. урона от огня раз в 1 сек. в течение 5 sec."},
[66536] = {name = "Божественная кара", description = "[q]Удар по противнику, нанесение 5625 to 6875 ед. урона от светлой магии."},
[66537] = {name = "Обновление", description = "[q]Восстанавливает 22000 ед. здоровья дружественной цели раз в 3 сек."},
[66538] = {name = "Священный огонь", description = "[q]Противника охватывает пламя, обжигая и нанося дополнительный урон от огня раз в 2 сек. в течение 8 sec."},
[66542] = {name = "Запуск зажигательной ракеты", description = "[q]Выстреливает по цели ракетой, начиненной напалмом. Горящий напалм разливается в радиусе 5 м от места попадания ракеты и наносит урон всем противникам в этой области в течение 15 sec."},
[66544] = {name = "Yellow Aura", description = nil},
[66546] = {name = "Кольцо света", description = "[q]Создает вспышку божественного света, которая наносит 7000 to 9000 ед. урона от светлой магии всем находящимся поблизости противникам и отбрасывает их назад."},
[66547] = {name = "Признание", description = "[q]Заставляет цель погрузиться в самые печальные воспоминания."},
[66552] = {name = "Пробуждающий кошмар", description = "[q]Повергает находящихся рядом противников в страх, наносящий периодический урон от темной магии и заставляющий их бежать в ужасе в течение 4 sec."},
[66601] = {name = "Blue Aura", description = nil},
[66602] = {name = "Red Aura", description = nil},
