[66619] = {name = "Тени прошлого", description = "[q]Тени прошлого окутывают цель, нанося ей урон от темной магии и снижая скорость атаки и произнесения заклинаний на 90% в течение 5 sec."},
[66620] = {name = "Старые раны", description = "[q]Наносит противнику 1875 ед. физического урона раз в 1 сек. в течение 12 sec."},
[66636] = {name = "Вскипающий гнев", description = "[q]Растущая ярость увеличивает наносимый урон на 15%."},
[66665] = {name = "Сжигающее дыхание", description = "[q]Сжигающее дыхание наносит всем целям 5363 to 5637 ед. урона от огня раз в 1 сек. в течение 3 sec."},
[66683] = {name = "Могучее сокрушение", description = "[q]Ледяной Рев взмывает в воздух и обрушивается на землю, нанося 16188 to 18812 ед. физического урона всем противникам, отбрасывая их назад и оглушая."},
[66684] = {name = "Пылающая головня", description = "[q]Наносит 4388 to 4612 ед. урона от огня всем противникам в радиусе 4 м от горящей головни."},
[66689] = {name = "Арктическое дыхание", description = "[q]Мерзлое дыхание замораживает цели в конусе поражения перед заклинателем, нанося 30000 ед. урона магией льда в течение 5 sec."},
[66721] = {name = "Пылающая ярость", description = "[q]Наносимый урон увеличивается на 5%."},
[66725] = {name = "Кулаки-метеоры", description = "[q]Атаки наносят урон, равномерно распределяемый между основной целью и ближайшей к ней целью в радиусе 10 м."},
[66734] = {name = "Тяжелый шаг", description = "[q]Ледяной Рев растаптывает противников в радиусе 12 м, нанося им 50000 ед. физического урона."},
[66757] = {name = "Замерзшая земля", description = "[q]Земля под оком льда такая холодная, что ваши ноги практически примерзают к ней."},
[66758] = {name = "Глубокое потрясение", description = "[q]Оглушение от мощного удара, неспособность действовать. Получаемый урон увеличен на 100%. Время действия – 15 sec."},
[66759] = {name = "Кипящая ярость", description = "[q]Увеличивает физический урон и скорость атаки на 100%."},
[66770] = {name = "Свирепое бодание", description = "[q]Ледяной Рев бодает противника, нанося 83250 to 96750 ед. физического урона и оглушая его на 2.50 sec."},
[66776] = {name = "Гнев", description = "[q]Генерал покинул свои покои и пришел в ярость. До тех пор, пока он не вернется обратно в свои покои, наносимый им урон будет увеличен на 300%, получаемый урон снижен на 95%, а скорость передвижения увеличена на 100%."},
[66794] = {name = "Сбивание", description = "[q]Заклинатель вращается на месте, нанося 10175 to 11825 ед. физического урона противникам в радиусе 15 м и отбрасывая их назад."},
[66795] = {name = "Сбор пиломатериалов", description = "[q]Сбор древесины Азшары из ближайшего штабеля пиломатериалов."},
[66796] = {name = "Плевок огнем", description = "[q]Нанесение 10175 to 11825 ед. урона от огня противнику."},
[66798] = {name = "Отсрочка смерти", description = "[q]Оглушает и отбрасывает назад цель, наносящую урон от темной магии."},
[66808] = {name = "Кулаки-метеоры", description = "[q]Атаки наносят урон, равномерно распределяемый между основной целью и ближайшей к ней целью в радиусе 10 м."},
[66811] = {name = "Циркулярная пила", description = "[q]Спиливает ближайшее дерево или наносит урон противникам, находящимися перед вами на расстоянии до 5 м."},
[66813] = {name = "Выброс лавы", description = "[q]Хранитель пламени бросает в цель раскаленную лаву, нанося 19425 to 22575 ед. урона от огня."},
[66818] = {name = "Кислотная рвота", description = nil},
[66821] = {name = "Жгучая рвота", description = "[q]Наносит всем противникам, находящимся спереди от заклинателя, 4625 to 5375 ед. урона от огня раз в 0.3 сек. в течение 2.50 sec."},
[66822] = {name = "Призыв снайпера", description = nil},
[66823] = {name = "Паралитический токсин", description = "[q]Отравляет цель паралитическим токсином, наносящим урон от сил природы раз в 2 сек. и постепенно снижающим скорость передвижения жертвы, пока та не будет парализована полностью. Горящая желчь нейтрализует паралитический токсин."},
[66824] = {name = "Парализующий укус", description = "[q]Наносит противнику 18500 to 21500 ед. урона от сил природы и отравляет его паралитическим токсином."},
[66862] = {name = "Сияние", description = "[q]Эдрик окутывается аурой ослепительного света. Все находящиеся поблизости противники, смотревшие на него, получают 5938 to 6562 ед. урона от светлой магии и слепо бродят по полю боя в течение 4 sec."},
[66863] = {name = "Молот правосудия", description = "[q]Оглушает противника, делая его уязвимым к \"Молоту праведника\" и не давая ему передвигаться и атаковать в течение 6 sec."},
[66865] = {name = "Отмщение", description = "[q]Каждая атака ближнего боя Эдрика может наделить его невероятной силой, гарантирующей нанесение критического удара при следующей атаке ближнего боя."},
[66867] = {name = "Молот праведника", description = "[q]Бросает молот, который наносит противнику 16000 ед. урона от светлой магии. Если цель не находится под действием \"Молота правосудия\", то этот молот можно поймать и метнуть обратно в заклинателя."},
[66868] = {name = "Метнуть лезвие", description = "[q]Бросить пару лезвий пилы во вражеских ночных эльфов на расстояние до 35 м."},
[66869] = {name = "Горящая желчь", description = "[q]Обливает противников горящей желчью, наносящей им и находящимся неподалеку их союзникам периодический урон от огня. Горящая желчь йормунгара обладает свойством нейтрализовывать паралитические токсины."},
[66879] = {name = "Обжигающий укус", description = "[q]Наносит противнику 18500 to 21500 ед. урона от огня и обливает его горящей желчью."},
[66880] = {name = "Кислотный плевок", description = "[q]Наносит противнику 10175 to 11825 ед. урона от сил природы."},
[66883] = {name = "Лужа жижи", description = "[q]Отравляющее облако появляется над местностью и разрастается в течение 30 sec."},
[66893] = {name = "Гранатомет", description = "[q]Забрасывает гранатами словно лепестками роз."},
[66901] = {name = "Парализующие брызги", description = "[q]Обрызгивает кислотой противника и находящиеся поблизости цели, нанося им 12950 to 15050 ед. урона от сил природы и ослабляя их паралитическим токсином."},
[66902] = {name = "Горящие брызги", description = "[q]Обрызгивает противника и находящиеся поблизости цели, нанося им 12950 to 15050 ед. урона от огня и заливая их горящей желчью."},
[66937] = {name = "Перезарядка", description = "[q]Использование аварийного запаса энергии, позволяющего пополнить здоровье крошшера на 70% от максимального запаса."},
[66954] = {name = "Плевок кислотой", description = "[q]Наносит противнику урон от сил природы, ослабляя его броню на 50% на 6 sec."},
[66966] = {name = "Мощный удар", description = "[q]Наносит мощный удар цели, причиняя 68 to 82 ед. урона и откидывая назад."},
[66967] = {name = "Гнев деревьев", description = "[q]Противника опутывают корни, нанося урон от сил природы раз в 3 сек. и не давая цели двигаться на время до 5 sec."},
[66968] = {name = "Валун", description = "[q]Валун, наносящий физический урон противнику."},
[67002] = {name = "Приступить к вызову Кроули", description = nil},
[67005] = {name = "Призыв тени", description = "[q]Призывает смертную тень в подчинение вестнику смерти Каросу."},
