[67034] = {name = "Бросок глефы", description = "[q]Выстреливает в цель большую глефу, наносящую физический урон и 4242 to 4758 ед. осадного урона в радиусе 5 м."},
[67037] = {name = "Вихрь", description = "[q]Кружась в вихре, заклинатель атакует ближайших противников, нанося каждому обычный урон и дополнительно 50 ед. урона. Время действия – until cancelled."},
[67038] = {name = "Мощный удар", description = "[q]Наносит 4375 to 5625 ед. физического урона всем противникам, находящимся в секторе радиусом 30 м перед заклинателем."},
[67194] = {name = "Фонтан света", description = "[q]Создает фонтан света, который исцеляет находящихся поблизости союзников в течение 45 sec. или пока не будет уничтожен."},
[67195] = {name = "Стальной ливень", description = "[q]Обрушивает на выбранную область град маленьких лезвий. Каждое лезвие наносит физический урон в радиусе 5 м. Время действия – 10 sec."},
[67229] = {name = "Контроль над разумом", description = "[q]Подчинение разума гуманоида на 8 sec."},
[67233] = {name = "Шквал ударов", description = "[q]Повышает скорость атаки заклинателя на 100%, но снижает наносимый с каждым ударом урон на 30%. Время действия – 8 sec."},
[67235] = {name = "Зуботычина", description = "[q]Нанесение противнику 30 ед. урона и лишение его на 4 sec. возможности произносить заклинания."},
[67237] = {name = "Дисбалансирующий удар", description = "[q]Наносит цели 120% урона от оружия и лишает ее равновесия, что на 6 sec. снижает ее навык защиты на 100."},
[67247] = {name = "Ослепительный свет", description = "[q]Окутывает союзника аурой ослепительного света, восстанавливающей ему 5655 to 6345 ед. здоровья и наносящей 5655 to 6345 ед. урона от светлой магии всем противникам в радиусе 8 м от цели."},
[67251] = {name = "Божественный щит", description = "[q]Заклинатель защищен от всех атак и заклинаний в течение 9 sec."},
[67255] = {name = "Завершительная медитация", description = "[q]Эффект медитации снижает скорость передвижения и атаки противников в радиусе 15 м. Время действия – 8 sec."},
[67279] = {name = "Пушка повстанцев", description = "[q]Выстреливает в противника огромным пушечным ядром."},
[67280] = {name = "Бросить кинжал", description = "[q]Бросает кинжал в случайно выбранного противника, нанося мгновенный урон и дополнительный урон раз в 3 сек. Время действия – 12 sec."},
[67294] = {name = "Shoot Me Cue", description = nil},
[67301] = {name = "Залп огня", description = "[q]Запуск пылающего снаряда в противника, наносящего урон 20 ед. и переводящего весь остальной урон в разряд огня."},
[67322] = {name = "Погружение", description = "[q]Попытка закопаться в землю. Не может успешно применяться на вечной мерзлоте."},
[67345] = {name = "Вращение", description = "[q]Заклинатель вращается вокруг целей, нанося 12950 to 15050 ед. физического урона находящимся рядом противникам и отбрасывая их назад."},
[67367] = {name = "Возвращенный к жизни", description = "[q]Недавно возвращен в жизни… выглядит не лучшим образом."},
[67393] = {name = "Высадить пассажира", description = "[q]Высадить пассажира из коляски вашего мотоцикла."},
[67411] = {name = "Вытягивание духа", description = "[q]Вытягивает дух из павшей ученицы Элуны."},
[67440] = {name = "Бросок валуна", description = "[q]Выстрел огромным валуном на большое расстояние. Валун наносит урон от огня и 1414 to 1586 ед. осадного урона всем противникам в радиусе 10 м."},
[67441] = {name = "Таран", description = "[q]Наносит урон всем противникам перед разрушителем в радиусе 7 м и отталкивает их назад. Также наносит 1885 to 2115 ед. осадного урона строениям."},
[67452] = {name = "Запуск ракеты", description = "[q]Выстреливает сверхмощной ракетой на большое расстояние. При взрыве ракета наносит урон всем противникам в радиусе 8 м."},
[67461] = {name = "Выстрел из пушки", description = "[q]Выстрел из пушки, наносящий урон от огня и 4242 to 4758 ед. осадного урона близлежащим строениям."},
[67503] = {name = "Отвага повстанцев", description = "[q]Быстрее, лучше, сильнее!"},
[67514] = {name = "Призыв охотника Скверны", description = "[q]Вызов Охотника Скверны, который будет помогать заклинателю до тех пор, пока не будет освобожден."},
[67523] = {name = "Огненная вспышка", description = "[q]Испепеляет противника... ДОТЛА!"},
[67528] = {name = "Волна исцеления", description = "[q]Призывает силы природы для исцеления союзника."},
[67529] = {name = "Цепная молния", description = "[q]Поражение противника молнией, которая перескакивает на другого находящегося поблизости противника. Заклинание поражает до 3 целей, нанося каждой следующей цели возрастающий урон от сил природы."},
[67530] = {name = "Щит земли", description = "[q]Защита цели земляным щитом. При атаке ближнего боя защищенная цель исцеляется. Эффект может срабатывать только раз в несколько секунд. 10 зарядов. Время действия – 2 min."},
[67534] = {name = "Проклятие лечения", description = "[q]Налагает на цель проклятие, исцеляющее всех ее противников при получении целью прямого исцеления."},
